INSERT INTO `creature_text` VALUES ('20904', '5', '0', 'Anarchy! Bedlam! Oh, you are so wise! Yes, I see it now, of course!', '14', '0', '100', '0', '0', '11227', '19110', '0', 'Warden Mellichar');
