INSERT INTO `creature_text` VALUES ('28601', '0', '0', 'Fool!  You led us to the only being that could stand up to our armies!  You will never bring the Etymidian back to Northrend!', '12', '0', '100', '25', '0', '0', '29968', '0', 'High Cultist Herenn');
INSERT INTO `creature_text` VALUES ('28859', '14', '0', 'I am unstoppable!', '14', '0', '100', '0', '2000', '14533', '31783', '0', 'Malygos - Buffed by a spark');
INSERT INTO `creature_text` VALUES ('28859', '13', '0', 'The powers at work here exceed anything you could possibly imagine!', '14', '0', '100', '0', '5000', '14532', '31782', '0', 'Malygos - Surge of Power');
INSERT INTO `creature_text` VALUES ('28859', '12', '0', '%s takes a deep breath...', '41', '0', '100', '0', '10000', '0', '33657', '0', 'Malygos - Surge of Power warning (Phase 2)');
INSERT INTO `creature_text` VALUES ('28859', '10', '0', 'Now your benefactors make their appearance, but they are too late! The powers contained here are sufficient to destroy the world ten times over. What do you think they will do to you?', '14', '0', '100', '0', '13000', '14530', '31779', '0', 'Intro Phase 3');
INSERT INTO `creature_text` VALUES ('28859', '11', '0', 'Submit!', '14', '0', '100', '0', '1000', '14531', '31780', '0', 'Malygos - Start phase 3');
INSERT INTO `creature_text` VALUES ('28859', '9', '0', 'ENOUGH! If you intend to reclaim Azeroth\'s magic, then you shall have it!', '14', '0', '100', '0', '7000', '14529', '31778', '0', 'Malygos - End Phase 2');
INSERT INTO `creature_text` VALUES ('28859', '8', '2', 'Your spirit will linger here forever!', '14', '0', '100', '0', '3800', '14528', '31777', '0', 'Malygos - Killed Player (Phase 2)');
INSERT INTO `creature_text` VALUES ('28859', '8', '1', 'I am the Spell-Weaver! My power is infinite!', '14', '0', '100', '0', '5200', '14527', '31776', '0', 'Malygos - Killed Player (Phase 2)');
INSERT INTO `creature_text` VALUES ('28859', '8', '0', 'Your energy will be put to good use.', '14', '0', '100', '0', '2000', '14526', '31775', '0', 'Malygos - Killed Player (Phase 2)');
INSERT INTO `creature_text` VALUES ('28859', '7', '0', 'Watch helplessly as your hopes are swept away!', '14', '0', '100', '0', '4000', '14525', '31774', '0', 'Malygos - Magic Blast');
INSERT INTO `creature_text` VALUES ('28859', '5', '0', 'Few have experienced the pain I will now inflict upon you!', '14', '0', '100', '0', '5500', '14523', '31765', '0', 'Malygos - Start phase 2');
INSERT INTO `creature_text` VALUES ('28859', '6', '0', 'I will teach you ignorant children just how little you know of magic.', '14', '0', '100', '0', '7000', '14524', '31773', '0', 'Malygos - Anti-Magic Shell');
INSERT INTO `creature_text` VALUES ('28859', '4', '0', 'I had hoped to end your lives quickly, but you have proven more... resilient than I anticipated. Nonetheless, your efforts are in vain. It is you reckless, careless mortals who are to blame for this war. I do what I must, and if it means your extinction.... then SO BE IT!!', '14', '0', '100', '0', '22900', '14522', '31772', '0', 'Malygos - End Phase One');
INSERT INTO `creature_text` VALUES ('28859', '3', '2', 'How very naive.', '14', '0', '100', '0', '4800', '14521', '31769', '0', 'Malygos - Killed Player (Phase 1)');
INSERT INTO `creature_text` VALUES ('28859', '3', '1', 'More artifacts to confiscate.', '14', '0', '100', '0', '2800', '14520', '31768', '0', 'Malygos - Killed Player (Phase 1)');
INSERT INTO `creature_text` VALUES ('28859', '3', '0', 'Your stupidity has finally caught up with you!', '14', '0', '100', '0', '3250', '14519', '31767', '0', 'Malygos - Killed Player (Phase 1)');
INSERT INTO `creature_text` VALUES ('28859', '2', '0', 'You will not succeed while I draw breath!', '14', '0', '100', '0', '3000', '14518', '31781', '0', 'Malygos - Begin to cast Deep Breath');
INSERT INTO `creature_text` VALUES ('28914', '0', '0', 'Make haste, $N. Use the portal!', '12', '0', '100', '25', '0', '0', '29443', '0', 'Orbaz Bloodlane');
INSERT INTO `creature_text` VALUES ('28923', '0', '0', 'I have witnessed the rise and fall of empires... the birth and extinction of entire species... Over countless millennia the foolishness of mortals has remained the only constant. Your presence here confirms this.', '14', '0', '100', '0', '0', '14160', '30536', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('28923', '1', '0', 'My master has shown me the future, and you have no place in it. Azeroth will be reborn in darkness. Yogg-Saron shall be released! The Pantheon shall fall!', '14', '0', '100', '0', '0', '14161', '30537', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('28923', '2', '0', 'What hope is there for you? None!', '14', '0', '100', '0', '0', '14162', '30535', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('28923', '3', '0', 'You cannot hide from fate!', '14', '0', '100', '0', '0', '14163', '30538', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('28923', '3', '1', 'Come closer. I will make it quick.', '14', '0', '100', '0', '0', '14164', '30539', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('28923', '3', '2', 'Your flesh cannot hold out for long.', '14', '0', '100', '0', '0', '14165', '30540', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('28923', '4', '0', 'Only mortal...', '14', '0', '100', '0', '0', '14166', '30541', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('28923', '4', '1', 'I... am... FOREVER!', '14', '0', '100', '0', '0', '14167', '30542', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('28923', '4', '2', 'What little time you had, you wasted!', '14', '0', '100', '0', '0', '14168', '30543', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('28923', '5', '0', 'You stare blindly into the abyss!', '14', '0', '100', '0', '0', '14169', '30545', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('28923', '6', '0', 'Your ignorance is profound. Can you not see where this path leads?', '14', '0', '100', '0', '0', '14170', '30546', '0', 'Loken');
