INSERT INTO `creature_text` VALUES ('30146', '0', '0', 'Curse you!  You will not treat me like a beast!', '12', '0', '100', '1', '0', '0', '30793', '0', 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES ('30146', '0', '1', 'Enough!  I will teach you some manners, wench!', '12', '0', '100', '1', '0', '0', '30794', '0', 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES ('30146', '0', '2', 'I\'d rather die fighting than live like a slave!', '12', '0', '100', '1', '0', '0', '30795', '0', 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES ('30146', '1', '0', 'Back... to work...', '12', '0', '100', '1', '0', '0', '30796', '0', 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES ('30146', '1', '1', 'We will have revenge... some day.', '12', '0', '100', '1', '0', '0', '30798', '0', 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES ('30146', '1', '2', 'You treat us worse than animals!', '12', '0', '100', '1', '0', '0', '30797', '0', 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES ('30190', '0', '0', 'Metallic specimen of foreign origin detected.', '12', '0', '100', '0', '5', '0', '30847', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '1', '0', 'Beginning analysis ...', '12', '0', '100', '0', '0', '0', '30848', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '2', '0', 'Specimen identified as an alloy of saronite and iron.', '12', '0', '100', '0', '0', '0', '30849', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '3', '0', 'The ore used in this sample originated deep within the crust of Azeroth and was recently unearthed.', '12', '0', '100', '275', '0', '0', '30850', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '4', '0', 'The saronite in this sample is nearly pure. Ores like this do not occur naturally on the surface of Azeroth.', '12', '0', '100', '0', '0', '0', '30851', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '5', '0', 'This ore can only have been created by a powerful, malevolent force. Attempting to access databanks for more information ...', '12', '0', '100', '1', '0', '0', '30854', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '6', '0', 'Access denied?!', '12', '0', '100', '6', '0', '0', '30859', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '7', '0', 'Further information can only be accessed from the archives within Ulduar, by order of Keeper Loken.', '12', '0', '100', '1', '0', '0', '30860', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '8', '0', 'But one thing is for certain: this plate\'s size, thickness, and bolt holes point to a use as armor for a colossal structure.', '12', '0', '100', '1', '0', '0', '30867', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30190', '9', '0', 'Directive completed. Returning to standby mode.', '12', '0', '100', '1', '0', '0', '30861', '0', 'Attendant Tock');
INSERT INTO `creature_text` VALUES ('30224', '0', '0', 'What\'s on yer mind, lad?', '12', '0', '100', '6', '0', '0', '31065', '0', 'Crusader Lord Dalfors');
INSERT INTO `creature_text` VALUES ('30225', '0', '0', 'Eversong Woods, Dalfors. I\'m thinking about how beautiful it was before Arthas cut a swathe of death through it...', '12', '0', '100', '1', '0', '0', '31070', '0', 'Crusader Sunborn');
INSERT INTO `creature_text` VALUES ('30271', '0', '0', 'Guards!', '14', '6', '100', '0', '0', '0', '4583', '0', 'Galendror Whitewing');
INSERT INTO `creature_text` VALUES ('30274', '0', '0', 'Thank you and farewell, friend. I must return to the Argent Vanguard.', '12', '0', '100', '0', '0', '0', '30980', '0', 'Freed Crusader Speach');
INSERT INTO `creature_text` VALUES ('30377', '0', '0', 'You are dealing with a being that holds within it the consciousness of the most cunning, intelligent, and ruthless individuals to ever live.', '12', '0', '100', '1', '0', '0', '31005', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '1', '0', 'The Lich King is unlike any foe that you have ever faced, Highlord. Though you bested him upon the holy ground of Light\'s Hope Chapel, you tread now upon his domain.\n', '12', '0', '100', '1', '0', '0', '31006', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '2', '0', 'You cannot win. Not like this...\n', '12', '0', '100', '274', '0', '0', '31007', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '3', '0', 'Nothing. There is nothing that you can do while the Light binds you. It controls you wholly, shackling you to the ground with its virtues.', '12', '0', '100', '274', '0', '0', '31009', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '4', '0', '%s shakes his head.', '16', '0', '100', '0', '0', '0', '31011', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '5', '0', 'Look upon the field, Highlord. The Lich King has halted your advance completely and won the upper hand!', '12', '0', '100', '25', '0', '0', '31012', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '6', '0', 'The breach you created was sealed with Nerubian webbing almost as quickly as it was opened.', '12', '0', '100', '1', '0', '0', '31013', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '7', '0', 'Your soldiers are being used as living shields to stave off artillery fire in the Valley of Echoes, allowing the forces of the Lich King to assault your base without impediment.', '12', '0', '100', '1', '0', '0', '31014', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '8', '0', 'The Lich King knows your boundaries, Highlord. He knows that you will not fire on your own men. Do you not understand? He has no boundaries. No rules to abide. \n', '12', '0', '100', '1', '0', '0', '31015', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30377', '9', '0', 'Then you have lost, Highlord.\n', '12', '0', '100', '1', '0', '0', '31017', '0', 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES ('30407', '0', '0', 'Let\'s get out of here! Quickly!', '12', '0', '100', '0', '0', '0', '31115', '0', 'Captured Crusader Start');
INSERT INTO `creature_text` VALUES ('30407', '1', '0', 'Faster, hero! We must get back to the Argent Vanguard and warn them of the impending attack!', '12', '0', '100', '0', '0', '0', '31122', '0', 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES ('30407', '1', '1', 'There are hundreds of them! Maybe thousands! They will attack at any moment! I\'m sure of it!', '12', '0', '100', '0', '0', '0', '31123', '0', 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES ('30407', '1', '2', 'Had you not come along I would have died for sure!', '12', '0', '100', '0', '0', '0', '31124', '0', 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES ('30407', '1', '3', 'They\'re planning a huge attack! We need to warn Highlord Fordring!', '12', '0', '100', '2', '0', '0', '31121', '0', 'Captured Crusader Middle');
INSERT INTO `creature_text` VALUES ('30407', '2', '0', 'Thank you, hero.', '12', '0', '100', '2', '0', '0', '31130', '0', 'Captured Crusader End');
INSERT INTO `creature_text` VALUES ('30407', '2', '1', 'Thank you, friend, but we\'re not out of trouble yet!', '12', '0', '100', '2', '0', '0', '31114', '0', 'Captured Crusader End');
INSERT INTO `creature_text` VALUES ('31237', '0', '0', 'You shouldn\'t have done that!  He\'s not going to like it.', '15', '0', '100', '0', '0', '0', '32338', '0', 'Matthias Lehner - On quest accept');
INSERT INTO `creature_text` VALUES ('31397', '0', '0', 'AHAHAHAHA... you\'ll join us soon enough!', '14', '0', '100', '0', '0', '0', '32473', '0', 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES ('31397', '0', '1', 'I don\'t want to leave! I want to stay here!', '14', '0', '100', '0', '0', '0', '32471', '0', 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES ('31397', '0', '2', 'NO! You\'re wrong! The voices in my head are beautiful!', '14', '0', '100', '0', '0', '0', '32470', '0', 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES ('31397', '0', '3', 'My life for you!', '14', '0', '100', '0', '0', '0', '32467', '0', 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES ('31397', '0', '4', 'I\'m coming, master!', '14', '0', '100', '0', '0', '0', '32466', '0', 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES ('31426', '0', '0', 'Guards!', '14', '6', '100', '0', '0', '0', '4583', '0', 'Doras');
INSERT INTO `creature_text` VALUES ('28859', '0', '4', 'I give you one chance. Pledge fealty to me, and perhaps I will not slaughter you for your insolence.', '14', '0', '100', '0', '7000', '14516', '31763', '0', 'Malygos - Intro');
INSERT INTO `creature_text` VALUES ('28859', '0', '2', 'What could you hope to accomplish? To storm brazenly into my domain... to employ magic... against ME?', '14', '0', '100', '0', '13000', '14514', '31761', '0', 'Malygos - Intro');
INSERT INTO `creature_text` VALUES ('28859', '0', '3', 'I am without limits here. The rules of your cherished reality do not apply. In this realm, I am in control!', '14', '0', '100', '0', '10000', '14515', '31762', '0', 'Malygos - Intro');
INSERT INTO `creature_text` VALUES ('28859', '0', '0', 'Lesser beings, intruding here! A shame that your excess courage does not compensate for your stupidity!', '14', '0', '100', '0', '10000', '14512', '31759', '0', 'Malygos - Intro');
INSERT INTO `creature_text` VALUES ('28859', '0', '1', 'None but the blue dragonflight are welcome here. Perhaps this is the work of Alexstrasza? Well, then, she has sent you to your deaths!', '14', '0', '100', '0', '11000', '14513', '31760', '0', 'Malygos - Intro');
INSERT INTO `creature_text` VALUES ('32736', '0', '0', 'The view up here is amazing!', '12', '0', '0', '5', '0', '0', '33031', '0', 'Scribe Whitman');
INSERT INTO `creature_text` VALUES ('32736', '0', '1', 'Too bad I left my light feathers at home... Slow Fall would work perfect here!', '12', '0', '0', '1', '0', '0', '33032', '0', 'Scribe Whitman');
