INSERT INTO `creature_text` VALUES ('33008', '0', '0', 'It\'s an angry male!', '42', '0', '100', '0', '0', '0', '33191', '0', 'Male Icepaw Bear');
INSERT INTO `creature_text` VALUES ('33303', '0', '0', 'Thank you, mortal, for freeing me from this curse. I beg you, take this blade.', '12', '0', '100', '1', '0', '0', '33436', '0', 'Maiden of Winter\'s Breath Lake');
INSERT INTO `creature_text` VALUES ('33303', '1', '0', 'It has brought me naught but ill. Mayhap you can find someone who will contain its power.', '12', '0', '100', '1', '0', '0', '33437', '0', 'Maiden of Winter\'s Breath Lake');
INSERT INTO `creature_text` VALUES ('33788', '0', '0', 'Help shield us from these cutting winds, little sapling.', '12', '0', '100', '2', '0', '0', '33814', '0', 'Fhyron Shadesong');
INSERT INTO `creature_text` VALUES ('33788', '0', '1', 'There you are.', '12', '0', '100', '273', '0', '0', '33813', '0', 'Fhyron Shadesong');
INSERT INTO `creature_text` VALUES ('33788', '0', '2', 'Grow, little one.', '12', '0', '100', '273', '0', '0', '33812', '0', 'Fhyron Shadesong');
