INSERT INTO `creature_text` VALUES ('34716', '0', '0', 'Thank you, $C.', '12', '0', '100', '5', '0', '0', '35180', '0', 'Captive Aspirant');
INSERT INTO `creature_text` VALUES ('34716', '0', '1', 'They\'re killing the prisoners! Hurry, help the others.', '12', '0', '100', '5', '0', '0', '35181', '0', 'Captive Aspirant');
INSERT INTO `creature_text` VALUES ('34716', '0', '2', 'I knew I shouldn\'t have come to the tournament!', '12', '0', '100', '5', '0', '0', '35184', '0', 'Captive Aspirant');
INSERT INTO `creature_text` VALUES ('34716', '0', '3', 'I thought I was going to die in there!', '12', '0', '100', '5', '0', '0', '35183', '0', 'Captive Aspirant');
INSERT INTO `creature_text` VALUES ('34723', '0', '0', 'Dat cloud look like da Warchief.', '12', '1', '100', '25', '0', '0', '35027', '0', 'Watcher Tolwe - Random');
INSERT INTO `creature_text` VALUES ('34723', '0', '1', 'Dere be a bug.', '12', '1', '100', '25', '0', '0', '35029', '0', 'Watcher Tolwe - Random');
INSERT INTO `creature_text` VALUES ('34956', '0', '0', 'They were seconds from strapping me to one of those altars and sucking my soul out. Let\'s get out of here!', '12', '0', '100', '5', '0', '0', '35790', '0', 'Kul the Reckless');
INSERT INTO `creature_text` VALUES ('35642', '0', '0', 'If you\'ll excuse me, I\'m afraid I have other business I must attend to.  Please call on me again in the future.', '12', '0', '100', '3', '0', '0', '35977', '0', 'Jeeves');
INSERT INTO `creature_text` VALUES ('36597', '0', '0', 'So the Light\'s vaunted justice has finally arrived? Shall I lay down Frostmourne and throw myself at your mercy, Fordring?', '14', '0', '0', '0', '0', '17349', '38070', '0', 'The Lich King - SAY_LK_INTRO_1');
INSERT INTO `creature_text` VALUES ('36597', '1', '0', 'You\'ll learn of that first hand. When my work is complete, you will beg for mercy -- and I will deny you. Your anguished cries will be testament to my unbridled power...', '14', '0', '0', '22', '0', '17350', '38071', '0', 'The Lich King - SAY_LK_INTRO_2');
INSERT INTO `creature_text` VALUES ('36597', '2', '0', 'I\'ll keep you alive to witness the end, Fordring. I would not want the Light\'s greatest champion to miss seeing this wretched world remade in my image.', '14', '0', '0', '0', '0', '17351', '38072', '0', 'The Lich King - SAY_LK_INTRO_3');
INSERT INTO `creature_text` VALUES ('36597', '3', '0', 'Come then, champions. Feed me your rage!', '14', '0', '0', '0', '0', '0', '38073', '0', 'The Lich King - SAY_LK_AGGRO');
INSERT INTO `creature_text` VALUES ('36597', '4', '0', 'I will freeze you from within until all that remains is an icy husk.', '14', '0', '0', '0', '0', '17369', '38046', '0', 'The Lich King - SAY_LK_REMORSELESS_WINTER');
INSERT INTO `creature_text` VALUES ('36597', '5', '0', 'Watch as the world around you collapses!', '14', '0', '0', '0', '0', '17370', '38047', '0', 'The Lich King - SAY_LK_QUAKE');
INSERT INTO `creature_text` VALUES ('36597', '6', '0', 'Val\'kyr, your master calls!', '14', '0', '0', '0', '0', '17373', '38052', '0', 'The Lich King - SAY_LK_SUMMON_VALKYR');
INSERT INTO `creature_text` VALUES ('36597', '7', '0', 'Frostmourne hungers...', '14', '0', '0', '0', '0', '17366', '38042', '0', 'The Lich King - SAY_LK_HARVEST_SOUL');
INSERT INTO `creature_text` VALUES ('36597', '8', '0', 'Frostmourne, OBEY ME!', '14', '0', '0', '0', '0', '17367', '38043', '0', 'The Lich King - SAY_LK_FROSTMOURNE_ESCAPE');
INSERT INTO `creature_text` VALUES ('36597', '9', '0', 'Frostmourne feeds on the soul of your fallen ally!', '14', '0', '0', '0', '0', '17368', '38044', '0', 'The Lich King - SAY_LK_FROSTMOURNE_KILL');
INSERT INTO `creature_text` VALUES ('36597', '10', '0', 'Hope wanes...', '14', '0', '0', '0', '0', '17363', '38038', '0', 'The Lich King - SAY_LK_KILL');
INSERT INTO `creature_text` VALUES ('36597', '10', '1', 'The end has come!', '14', '0', '0', '0', '0', '17364', '38040', '0', 'The Lich King - SAY_LK_KILL');
INSERT INTO `creature_text` VALUES ('36597', '11', '0', 'Face now your tragic end.', '14', '0', '0', '0', '0', '17365', '38041', '0', 'The Lich King - SAY_LK_BERSERK');
INSERT INTO `creature_text` VALUES ('36597', '12', '0', '%s begins to cast Defile!', '41', '0', '0', '0', '0', '0', '39688', '0', 'The Lich King - EMOTE_DEFILE_WARNING');
INSERT INTO `creature_text` VALUES ('36597', '13', '0', ' |TInterface\\Icons\\ability_creature_disease_02.blp:16|t You have been infected by |cFFBC05FFNecrotic Plague|r! |TInterface\\Icons\\ability_creature_disease_02.blp:16|t', '42', '0', '0', '0', '0', '0', '39695', '0', 'The Lich King - EMOTE_NECROTIC_PLAGUE_WARNING');
INSERT INTO `creature_text` VALUES ('36597', '14', '0', 'No questions remain unanswered. No doubts linger. You ARE Azeroth\'s greatest champions. You overcame every challenge I laid before you. My mightiest servants have fallen before your relentless onslaught... your unbridled fury... ', '14', '0', '0', '0', '0', '17353', '38074', '0', 'The Lich King - SAY_LK_OUTRO_1');
INSERT INTO `creature_text` VALUES ('36597', '15', '0', 'Is it truly righteousness that drives you? I wonder...', '14', '0', '0', '0', '0', '17354', '38075', '0', 'The Lich King - SAY_LK_OUTRO_2');
INSERT INTO `creature_text` VALUES ('36597', '16', '0', 'You trained them well, Fordring. You delivered the greatest fighting force this world has ever known... right into my hands - exactly as I intended! You shall be rewarded for your unwitting sacrifice.', '14', '0', '0', '0', '0', '17355', '38076', '0', 'The Lich King - SAY_LK_OUTRO_3');
INSERT INTO `creature_text` VALUES ('36597', '17', '0', 'Watch now as I raise them from the dead to become masters of the Scourge. They will shroud this world in chaos and destruction. Azeroth\'s fall will come at their hands -- and you will be the first to die.', '14', '0', '0', '0', '0', '17356', '38077', '0', 'The Lich King - SAY_LK_OUTRO_4');
