INSERT INTO `creature_text` VALUES ('36669', '0', '0', 'It\'s good to finally see you, courier. Krasus has entrusted us with one of the few remaining copies of Ancient Dragonforged Blades.', '12', '0', '100', '1', '0', '0', '37620', '0', 'Arcanist Tybalin');
INSERT INTO `creature_text` VALUES ('36669', '1', '0', 'Here is the book. You must ensure it gets to Caladis Brightspear in Icecrown before the Sunreavers realize what is happening.', '12', '0', '100', '1', '0', '0', '37621', '0', 'Arcanist Tybalin');
INSERT INTO `creature_text` VALUES ('36669', '2', '0', 'Travel quickly! The Sunreavers will not waste any time once they realize that we have the information they seek.', '12', '0', '100', '1', '0', '0', '37622', '0', 'Arcanist Tybalin');
INSERT INTO `creature_text` VALUES ('36670', '0', '0', 'You\'re late, courier. No, I don\'t want any excuses; this errand is far too important.', '12', '0', '100', '1', '0', '0', '37580', '0', 'Magister Hathorel');
INSERT INTO `creature_text` VALUES ('36670', '1', '0', 'Here\'s the tome our representative brought from Wyrmrest. Get this to Myralion Sunblaze immediately.', '12', '0', '100', '1', '0', '0', '37581', '0', 'Magister Hathorel');
INSERT INTO `creature_text` VALUES ('36670', '2', '0', 'Watch your back, courier. I needn\'t remind you of the value of the book, nor the fact that the Silver Covenant will stop at nothing to take it from us.', '12', '0', '100', '1', '0', '0', '37582', '0', 'Magister Hathorel');
INSERT INTO `creature_text` VALUES ('36678', '0', '0', 'Just an ordinary gas cloud, but watch out because that\'s no ordinary gas cloud!', '14', '0', '0', '432', '0', '17119', '37843', '0', 'Professor Putricide - SAY_GASEOUS_BLIGHT');
INSERT INTO `creature_text` VALUES ('36678', '1', '0', 'Oh Festergut, you were always my favorite -- next to Rotface.  The good news is that you left behind so much gas. I can practically taste it!', '14', '0', '0', '0', '0', '17124', '37848', '0', 'Professor Putricide - SAY_FESTERGUT_DEATH');
INSERT INTO `creature_text` VALUES ('36678', '2', '0', 'Great news, everyone! The slime is flowing again!', '14', '0', '0', '1', '0', '17126', '38120', '0', 'Professor Putricide - SAY_ROTFACE_OOZE_FLOOD1');
INSERT INTO `creature_text` VALUES ('36678', '2', '1', 'Good news, everyone! I\'ve fixed the poison slime pipes!', '14', '0', '0', '1', '0', '17123', '37049', '0', 'Professor Putricide - SAY_ROTFACE_OOZE_FLOOD2');
INSERT INTO `creature_text` VALUES ('36678', '3', '0', 'Terrible news, everyone. Rotface is dead, but great news everyone! He left behind plenty of ooze for me to use! What? I\'m a poet and I didn\'t know it... Astounding!', '14', '0', '0', '0', '0', '17146', '37847', '0', 'Professor Putricide - SAY_ROTFACE_DEATH');
INSERT INTO `creature_text` VALUES ('36678', '4', '0', 'Good news, everyone! I think I\'ve perfected a plague that will destroy all life on Azeroth!', '14', '0', '0', '0', '0', '17114', '37838', '0', 'Professor Putricide - SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36678', '5', '0', '%s begins to cast Unstable Experiment!', '41', '0', '0', '0', '0', '0', '38500', '0', 'Professor Putricide - EMOTE_UNSTABLE_EXPERIMENT');
