INSERT INTO `creature_text` VALUES ('36678', '11', '0', 'Hrm, interesting...', '14', '0', '0', '0', '0', '17115', '37839', '0', 'Professor Putricide - SAY_KILL_1');
INSERT INTO `creature_text` VALUES ('36678', '11', '1', 'That was unexpected!', '14', '0', '0', '0', '0', '17116', '38545', '0', 'Professor Putricide - SAY_KILL_2');
