INSERT INTO `creature_text` VALUES ('39217', '1', '0', 'First, you must escape Frostmourne\'s hold, or be damned as I am; trapped within this cursed blade for all eternity. ', '14', '0', '0', '0', '0', '17395', '38115', '0', 'Terenas Menethil - SAY_TERENAS_INTRO_2');
INSERT INTO `creature_text` VALUES ('39368', '13', '0', 'Fantastic!', '12', '0', '100', '273', '0', '0', '39432', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '14', '0', ' However, the most important part of battle is how you celebrate a good hard earned victory!', '12', '0', '100', '396', '0', '0', '39384', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '12', '0', 'Everyone, cheer!', '12', '0', '100', '0', '0', '0', '39387', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '11', '0', 'Reward your fellow soldiers in battle with a resounding cheer.  Now, cheer at me when I tell you to!', '12', '0', '100', '1', '0', '0', '39428', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '9', '0', 'Wow, nice and scary! ', '12', '0', '100', '0', '0', '0', '39431', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '10', '0', 'Remember though, a key factor in winning any battle is positive reinforcement.', '12', '0', '100', '5', '0', '0', '39386', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '8', '0', 'Let me hear that battle roar!', '12', '0', '100', '5', '0', '0', '39395', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '7', '0', 'When I give the signal, let loose your greatest roar!', '12', '0', '100', '1', '0', '0', '39411', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '5', '0', 'Nice job!', '12', '0', '100', '273', '0', '0', '39430', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '6', '0', 'When you go into combat, it is important that you know how to intimidate the enemy with a deafening battle roar!', '12', '0', '100', '396', '0', '0', '39385', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '4', '0', 'Okay, recruits, salute!', '12', '0', '100', '66', '0', '0', '39394', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '3', '0', 'Show me that discipline with a proper salute when I say so!', '12', '0', '100', '5', '0', '0', '39413', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '2', '0', 'The first thing you need to learn is proper discipline.', '12', '0', '100', '1', '0', '0', '39383', '0', 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text` VALUES ('39368', '1', '0', 'I am going to teach you all what it takes to be a proper soldier! ', '12', '0', '100', '5', '0', '0', '39404', '0', 'Drill Sergeant Steamcrank');
