INSERT INTO `creature_text` VALUES ('9096', '0', '0', '%s goes into a frenzy!', '16', '0', '100', '0', '0', '0', '38630', '0', 'Rage Talon Dragonspawn');
INSERT INTO `creature_text` VALUES ('4983', '0', '0', 'I noticed some fire on that island over there. A human, too. Let\'s go check it out, $N.', '12', '1', '0', '0', '0', '0', '1669', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4983', '1', '0', 'That\'s Reethe alright. Let\'s go see what he has to say, yeah?', '12', '1', '0', '0', '0', '0', '1670', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4980', '0', '0', 'W-what do you want? Just leave me alone...', '12', '0', '0', '0', '0', '0', '1674', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('4980', '1', '0', 'I swear, I didn\'t steal anything from you! Here, take some of my supplies, just go away!', '12', '0', '0', '0', '0', '0', '1675', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('4983', '2', '0', 'Just tell us what you know about the Shady Rest Inn, and I won\'t bash your skull in.', '12', '0', '0', '0', '0', '0', '1707', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4980', '2', '0', 'I... Well, I may of taken a little thing or two from the inn... but what would an ogre care about that?', '12', '0', '0', '0', '0', '0', '1708', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('4983', '3', '0', 'Look here, if you don\'t tell me about the fire--', '12', '0', '0', '0', '0', '0', '1709', '0', 'Ogron');
INSERT INTO `creature_text` VALUES ('4980', '3', '0', 'Not one step closer, ogre!', '12', '0', '0', '0', '0', '0', '1710', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('4980', '4', '0', 'And I don\'t know anything about this fire of yours...', '12', '0', '0', '0', '0', '0', '1711', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('4980', '5', '0', 'What was that? Did you hear something?', '12', '0', '0', '0', '0', '0', '1731', '0', 'Paval');
INSERT INTO `creature_text` VALUES ('5046', '0', '0', 'Paval Reethe! Found you at last. And consorting with ogres now? No fear, even deserters and traitors are afforded some mercy.', '12', '0', '0', '0', '0', '0', '1714', '0', 'Caldwell');
INSERT INTO `creature_text` VALUES ('5046', '1', '0', 'Private, show Lieutenant Reethe some mercy.', '12', '0', '0', '0', '0', '0', '1720', '0', 'Caldwell');
INSERT INTO `creature_text` VALUES ('5045', '0', '0', 'Gladly, sir.', '12', '0', '0', '0', '0', '0', '1721', '0', 'Hallan');
INSERT INTO `creature_text` VALUES ('4980', '6', '0', '%s staggers backwards as the arrow lodges itself deeply in his chest.', '16', '0', '0', '0', '0', '0', '1732', '0', 'Paval');
