INSERT INTO `creature_text` VALUES ('23602', '0', '1', 'When you\'re off duty, let\'s go to the inn. I\'ll buy a round and we can reminisce about the good old days.', '12', '7', '100', '1', '0', '0', '22051', '0', 'Deserter Agitator');
INSERT INTO `creature_text` VALUES ('23602', '0', '2', 'Wouldn\'t you like to bring those days back? I have some friends who know just the way to do that...', '12', '7', '100', '1', '0', '0', '22052', '0', 'Deserter Agitator');
INSERT INTO `creature_text` VALUES ('23602', '1', '0', 'These people will know Admiral Proudmoore for the true hero he was!', '12', '7', '100', '5', '0', '0', '22048', '0', 'Deserter Agitator');
INSERT INTO `creature_text` VALUES ('23602', '1', '1', 'You can\'t stop us from exposing the truth about Jaina\'s cowardice!', '12', '7', '100', '5', '0', '0', '22047', '0', 'Deserter Agitator');
INSERT INTO `creature_text` VALUES ('23602', '1', '2', 'If you think you can stop us, you\'re mistaken, \'friend.\'', '12', '7', '100', '5', '0', '0', '22046', '0', 'Deserter Agitator');
INSERT INTO `creature_text` VALUES ('23602', '1', '3', 'It\'s people like you who weaken the Alliance and invite the Horde to take away all we\'ve fought for!', '12', '7', '100', '5', '0', '0', '22049', '0', 'Deserter Agitator');
INSERT INTO `creature_text` VALUES ('17808', '0', '0', 'The clock... is still... ticking.', '14', '0', '100', '0', '0', '10982', '18229', '0', 'Anetheron - SAY_ONDEATH');
INSERT INTO `creature_text` VALUES ('17808', '1', '0', 'Your hopes are lost!', '14', '0', '100', '0', '0', '10981', '18237', '0', 'Anetheron - SAY_ONSLAY1');
INSERT INTO `creature_text` VALUES ('17808', '1', '1', 'Scream for me.', '14', '0', '100', '0', '0', '11038', '18238', '0', 'Anetheron - SAY_ONSLAY2');
INSERT INTO `creature_text` VALUES ('17808', '1', '2', 'Pity, no time for a slow death!', '14', '0', '100', '0', '0', '11039', '18239', '0', 'Anetheron - SAY_ONSLAY3');
INSERT INTO `creature_text` VALUES ('17808', '2', '0', 'The swarm is eager to feed.', '14', '0', '100', '0', '0', '10979', '18233', '0', 'Anetheron - SAY_SWARM1');
INSERT INTO `creature_text` VALUES ('17808', '2', '1', 'Pestilence upon you!', '14', '0', '100', '0', '0', '11037', '18234', '0', 'Anetheron - SAY_SWARM2');
