INSERT INTO `creature_text` VALUES ('27658', '0', '0', 'Thank you for freeing us, mortals.  Beware, the blue flight is alerted to your presence.  Even now, Malygos sends Varos Cloudstrider and his ring guardians to defend the Oculus.  You will need our help to stand a chance.', '12', '0', '100', '1', '3500', '0', '26937', '0', 'Belgaristrasz - On freed');
