INSERT INTO `creature_text` VALUES ('27692', '2', '0', 'Use Leeching Poison to damage enemies and keep me healed.  Touch the Nightmare is very powerful, but it hurts me, so only use it when I have a lot of health!', '15', '0', '100', '1', '0', '0', '28255', '0', 'Emerald - On explaining abilities');
