INSERT INTO `creature_text` VALUES ('7998', '6', '0', 'Trogg incursion! Defend me while I blast the hole closed!', '12', '0', '100', '0', '0', '0', '4132', '0', 'SAY_BLASTMASTER_6');
INSERT INTO `creature_text` VALUES ('7998', '7', '0', 'I don\'t think one charge is going to cut it. Keep fending them off!', '12', '0', '100', '0', '0', '0', '4133', '0', 'SAY_BLASTMASTER_7');
