INSERT INTO `creature_text` VALUES ('27923', '0', '0', 'Hang on to your hat, $N! To Scalawag we go!', '12', '0', '100', '0', '0', '0', '27272', '0', 'Lou the Cabin Boy');
INSERT INTO `creature_text` VALUES ('27923', '1', '0', 'YAAARRRRR! Here we be, matey! Scalawag Point!', '12', '0', '100', '0', '0', '0', '27273', '0', 'Lou the Cabin Boy');
INSERT INTO `creature_text` VALUES ('27933', '0', '0', 'Enjoy the ride! It\'s a one way trip!', '12', '0', '100', '3', '0', '0', '27337', '0', 'Alanya');
