INSERT INTO `creature_text` VALUES ('39712', '0', '0', 'They may take our lives, but they\'ll never take...', '12', '0', '100', '0', '0', '0', '39708', '0', 'High Tinker Mekkatorque to Milli Featherwhistle 1');
INSERT INTO `creature_text` VALUES ('39712', '1', '0', '...our INNOVATION!', '12', '0', '100', '0', '0', '0', '39722', '0', 'High Tinker Mekkatorque to Milli Featherwhistle 2');
INSERT INTO `creature_text` VALUES ('39712', '2', '0', 'What I want out of each and every one of you is a hard-target search of every refuelling station, residence, warehouse, farmhouse, henhouse, outhouse and doghouse in this area. ', '12', '0', '100', '0', '0', '0', '39709', '0', 'High Tinker Mekkatorque to Ozzie Togglevolt 1');
INSERT INTO `creature_text` VALUES ('39712', '3', '0', 'Your fugitive\'s name is Mekgineer Thermaplugg.', '12', '0', '100', '0', '0', '0', '39723', '0', 'High Tinker Mekkatorque to Ozzie Togglevolt 2');
INSERT INTO `creature_text` VALUES ('39712', '4', '0', 'Go get him.', '12', '0', '100', '0', '0', '0', '39724', '0', 'High Tinker Mekkatorque to Ozzie Togglevolt 3');
INSERT INTO `creature_text` VALUES ('39712', '5', '0', 'We will not go quietly into the night! We will not vanish without a fight!', '12', '0', '100', '0', '0', '0', '39710', '0', 'High Tinker Mekkatorque to Tog Rustsprocket 1');
INSERT INTO `creature_text` VALUES ('39712', '6', '0', ' We\'re going to live on! We\'re going to survive! Today we celebrate...', '12', '0', '100', '0', '0', '0', '39725', '0', 'High Tinker Mekkatorque to Tog Rustsprocket 2');
INSERT INTO `creature_text` VALUES ('39712', '7', '0', '...our Autonomy Day!', '12', '0', '100', '0', '0', '0', '39726', '0', 'High Tinker Mekkatorque to Tog Rustsprocket 3');
INSERT INTO `creature_text` VALUES ('1268', '0', '0', 'Hmm, I suppose it could work.  But it could really use a little more umph!', '12', '0', '100', '0', '0', '0', '39711', '0', 'Ozzie Togglevolt reply');
INSERT INTO `creature_text` VALUES ('7955', '0', '0', 'What?  I don\'t even know what you\'re talking about!  That\'s terrible!', '12', '0', '100', '0', '0', '0', '39712', '0', 'Milli Featherwhistle reply');
INSERT INTO `creature_text` VALUES ('6119', '0', '0', 'Horrible! Well, all right, maybe it just needs a little cleaning up?', '12', '0', '100', '0', '0', '0', '39713', '0', 'Tog Rustsprocket reply');
INSERT INTO `creature_text` VALUES ('2986', '0', '0', 'Until we meet again, brave one.', '12', '0', '100', '113', '0', '0', '1589', '0', 'Dorn Plainstalker');
INSERT INTO `creature_text` VALUES ('23671', '0', '0', 'You think I do not know what you are trying to do? You think I haven\'t been watching you?', '12', '0', '100', '1', '2000', '0', '22787', '0', 'Halfdan');
INSERT INTO `creature_text` VALUES ('23671', '1', '0', 'Hah! You thought to interfere with my ascension? Nothing can stop me now!', '12', '0', '100', '1', '2000', '0', '22786', '0', 'Halfdan');
INSERT INTO `creature_text` VALUES ('23671', '2', '0', 'You will not stop my ascension, tiny $R. Time to die!', '14', '0', '100', '1', '2000', '0', '22788', '0', 'Halfdan');
INSERT INTO `creature_text` VALUES ('23671', '3', '0', 'No! You will not defeat me!', '14', '0', '100', '1', '1000', '0', '22789', '0', 'Halfdan');
