INSERT INTO `creature_text` VALUES ('12018', '6', '0', 'These mortal infidels, my lord! They have invaded your sanctum and seek to steal your secrets!', '14', '0', '100', '0', '0', '8041', '7661', '0', 'majordomo SAY_ARRIVAL2_MAJ');
INSERT INTO `creature_text` VALUES ('11982', '0', '0', '%s goes into a killing frenzy!', '16', '0', '100', '0', '0', '0', '24143', '0', 'magmadar EMOTE_FRENZY');
INSERT INTO `creature_text` VALUES ('6784', '0', '0', 'Ugh.', '12', '0', '100', '0', '0', '0', '5404', '0', 'calvin montague SAY_RIN_END_6');
INSERT INTO `creature_text` VALUES ('349', '0', '0', 'My wounds are grave.  Forgive my slow pace but my injuries won\'t allow me to walk any faster.', '12', '7', '100', '0', '0', '0', '25', '0', 'corporal keeshan SAY_CORPORAL_1');
INSERT INTO `creature_text` VALUES ('349', '1', '0', 'Ah, fresh air at last! I need a moment to rest.', '12', '7', '100', '0', '0', '0', '26', '0', 'corporal keeshan SAY_CORPORAL_2');
INSERT INTO `creature_text` VALUES ('349', '2', '0', 'The Blackrock infestation is thick in these parts.  I will do my best to keep the pace.  Let\'s go!', '12', '7', '100', '0', '0', '0', '27', '0', 'corporal keeshan SAY_CORPORAL_3');
INSERT INTO `creature_text` VALUES ('349', '3', '0', 'Marshal Marris, sir.  Corporal Keeshan of the 12th Sabre Regiment returned from battle and reporting for duty!', '12', '7', '100', '0', '0', '0', '29', '0', 'corporal keeshan SAY_CORPORAL_4');
INSERT INTO `creature_text` VALUES ('349', '4', '0', 'Brave adventurer, thank you for rescuing me! I am sure Marshal Marris will reward your kind deed.', '12', '7', '100', '0', '0', '0', '30', '0', 'corporal keeshan SAY_CORPORAL_5');
INSERT INTO `creature_text` VALUES ('17238', '3', '0', 'It must be the purity of the Mark of the Lightbringer that is drawing forth the Scourge to us.  We must proceed with caution lest we be overwhelmed!', '12', '7', '100', '1', '0', '0', '13617', '0', 'Q Tomb of the Lighbringer');
INSERT INTO `creature_text` VALUES ('17238', '2', '0', 'Beware!  We are attacked!', '12', '7', '100', '5', '0', '0', '13616', '0', 'Q Tomb of the Lighbringer');
INSERT INTO `creature_text` VALUES ('17238', '0', '0', 'Shall we begin, my friend?', '12', '7', '100', '6', '0', '0', '13613', '0', 'Q Tomb of the Lighbringer');
INSERT INTO `creature_text` VALUES ('17238', '1', '0', 'This area is known to be full of foul Scourge.  You may want to take a moment to prepare any defenses at your disposal.', '12', '7', '100', '1', '0', '0', '13614', '0', 'Q Tomb of the Lighbringer');
INSERT INTO `creature_text` VALUES ('1854', '0', '0', 'Be welcome, friends!', '12', '7', '100', '0', '0', '0', '13621', '0', 'anchorite truuen SAY_WP_3');
INSERT INTO `creature_text` VALUES ('17233', '0', '0', 'Thank you for coming here in remembrance of me.  Your efforts in recovering that symbol, while unnecessary, are certainly touching to an old man\'s heart.', '12', '0', '100', '0', '0', '0', '13622', '0', 'anchorite truuen SAY_WP_4');
INSERT INTO `creature_text` VALUES ('17233', '1', '0', 'Please, rise my friend.  Keep the Blessing as a symbol of the strength of the Light and how heroes long gone might once again rise in each of us to inspire.', '12', '0', '100', '0', '0', '0', '13623', '0', 'anchorite truuen SAY_WP_5');
INSERT INTO `creature_text` VALUES ('17233', '2', '0', 'Thank you my friend for making this possible.  This is a day that I shall never forget!  I think I will stay a while.  Please return to High Priestess MacDonnell at the camp.  I know that she\'ll be keenly interested to know of what has transpired here.', '12', '7', '100', '0', '0', '0', '13624', '0', 'anchorite truuen SAY_WP_6');
INSERT INTO `creature_text` VALUES ('14834', '0', '0', 'PRIDE HERALDS THE END OF YOUR WORLD.  COME, MORTALS!  FACE THE WRATH OF THE SOULFLAYER!', '14', '0', '100', '0', '0', '8414', '10447', '0', 'hakkar SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('14834', '1', '0', 'Fleeing will do you no good, mortals!', '14', '0', '100', '0', '0', '0', '10635', '0', 'hakkar SAY_FLEEING');
INSERT INTO `creature_text` VALUES ('14834', '2', '0', 'You dare set foot upon Hakkari holy ground? Minions of Hakkar, destroy the infidels!\n', '14', '0', '100', '0', '0', '0', '10594', '0', 'hakkar SAY_MINION_DESTROY');
INSERT INTO `creature_text` VALUES ('14834', '3', '0', 'Minions of Hakkar, hear your God. The sanctity of this temple has been compromised. Invaders encroach upon holy ground! The Altar of Blood must be protected. Kill them all!', '14', '0', '100', '0', '0', '0', '10546', '0', 'hakkar SAY_PROTECT_ALTAR');
INSERT INTO `creature_text` VALUES ('14509', '0', '0', 'Shirvallah, fill me with your RAGE!', '14', '0', '100', '0', '0', '8419', '10455', '0', 'thekal SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('14509', '1', '0', 'Hakkar binds me no more!  Peace at last!', '14', '0', '100', '0', '0', '8424', '10451', '0', 'thekal SAY_DEATH');
INSERT INTO `creature_text` VALUES ('14517', '0', '0', 'Lord Hir\'eek, grant me wings of vengeance!', '14', '0', '100', '0', '0', '8417', '10027', '0', 'jeklik SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('14517', '1', '0', 'I command you to rain fire down upon these invaders!', '14', '0', '100', '0', '0', '0', '10369', '0', 'jeklik SAY_RAIN_FIRE');
INSERT INTO `creature_text` VALUES ('14517', '2', '0', 'Finally...death!  Curse you, Hakkar!  Curse you!', '14', '0', '100', '0', '0', '8422', '10452', '0', 'jeklik SAY_DEATH');
INSERT INTO `creature_text` VALUES ('14515', '0', '0', 'Bethekk, your priestess calls upon your might!', '14', '0', '100', '0', '0', '8416', '10461', '0', 'arlokk SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('14515', '1', '0', 'Feast on $N, my pretties!', '14', '0', '100', '0', '0', '0', '10472', '0', 'arlokk SAY_FEAST_PANTHER');
INSERT INTO `creature_text` VALUES ('14515', '2', '0', 'At last, I am free of the Soulflayer!', '14', '0', '100', '0', '0', '8412', '10450', '0', 'arlokk SAY_DEATH');
INSERT INTO `creature_text` VALUES ('11382', '0', '0', 'I\'ll feed your souls to Hakkar himself!', '14', '0', '100', '0', '0', '8413', '0', '0', 'mandokir SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('11382', '1', '0', 'Ding!', '14', '0', '100', '0', '0', '0', '10505', '0', 'mandokir SAY_DING_KILL');
INSERT INTO `creature_text` VALUES ('11382', '2', '0', 'I\'m keeping my eye on you, $N!', '14', '0', '100', '0', '0', '0', '0', '0', 'mandokir SAY_WATCH');
INSERT INTO `creature_text` VALUES ('11382', '3', '0', 'Don\'t make me angry. You won\'t like it when I\'m angry.', '14', '0', '100', '0', '0', '0', '0', '0', 'mandokir SAY_WATCH_WHISPER');
INSERT INTO `creature_text` VALUES ('11380', '0', '0', 'Hey! Grats, mon!', '14', '0', '100', '0', '0', '0', '10601', '0', 'mandokir SAY_GRATS_JINDO');
INSERT INTO `creature_text` VALUES ('11380', '1', '0', 'Welcome to the great show, friends.  Step right up to die!', '14', '0', '100', '0', '0', '8425', '10449', '0', 'jindo SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('14510', '0', '0', 'Draw me to your web mistress Shadra. Unleash your venom!', '14', '0', '100', '0', '0', '8418', '0', '0', 'marli SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('14510', '1', '0', 'Shadra, make of me your avatar!', '14', '0', '100', '0', '0', '0', '10443', '0', 'marli SAY_TRANSFORM');
INSERT INTO `creature_text` VALUES ('14510', '2', '0', 'Aid me my brood!', '14', '0', '100', '0', '0', '0', '10448', '0', 'marli SAY_SPIDER_SPAWN');
INSERT INTO `creature_text` VALUES ('14510', '3', '0', 'Bless you, mortals, for this release...Hakkar controls me no longer!', '14', '0', '100', '0', '0', '8423', '10459', '0', 'marli SAY_DEATH');
INSERT INTO `creature_text` VALUES ('11136', '0', '0', 'Praise be to Egan!', '12', '0', '100', '0', '0', '0', '6453', '0', 'freed soul SAY_ZAPPED0');
INSERT INTO `creature_text` VALUES ('11136', '0', '1', 'Rivendare must be destroyed!', '12', '0', '100', '0', '0', '0', '6455', '0', 'freed soul SAY_ZAPPED1');
INSERT INTO `creature_text` VALUES ('1398', '0', '4', 'Die, $R!  These lands belong to the Stonesplinter Tribe!', '12', '0', '100', '0', '0', '0', '446', '0', 'Boss Galgosh - On Aggro');
INSERT INTO `creature_text` VALUES ('1978', '0', '0', 'Let\'s get to the others, and keep an eye open for those wolves outside...', '12', '0', '100', '0', '0', '0', '481', '0', 'erland SAY_QUESTACCEPT');
INSERT INTO `creature_text` VALUES ('1978', '1', '0', 'Be careful, $N.  Those wolves like to hide among the trees.', '12', '0', '100', '0', '0', '0', '482', '0', 'erland SAY_START');
INSERT INTO `creature_text` VALUES ('1978', '2', '0', 'A $N attacks!', '12', '0', '100', '0', '0', '0', '543', '0', 'erland SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES ('1978', '2', '1', 'Beware!  I am under attack!', '12', '0', '100', '0', '0', '0', '544', '0', 'erland SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES ('1978', '2', '2', 'Beware!  A $N is upon us!', '12', '0', '100', '0', '0', '0', '541', '0', 'erland SAY_AGGRO_3');
INSERT INTO `creature_text` VALUES ('1978', '3', '0', 'We\'re almost there!', '12', '0', '100', '0', '0', '0', '483', '0', 'erland SAY_PROGRESS');
INSERT INTO `creature_text` VALUES ('1978', '4', '0', 'We made it!  Thanks, $N.  I couldn\'t have gotten here without you.', '12', '0', '100', '0', '0', '0', '484', '0', 'erland SAY_LAST');
INSERT INTO `creature_text` VALUES ('1950', '0', '0', 'It\'s good to see you again, Erland.  What is your report?', '12', '0', '100', '0', '0', '0', '534', '0', 'erland SAY_RANE');
INSERT INTO `creature_text` VALUES ('1978', '5', '0', 'Masses of wolves are to the east, and whoever lived at Malden\'s Orchard is gone.', '12', '0', '100', '0', '0', '0', '535', '0', 'erland SAY_RANE_ANSWER');
INSERT INTO `creature_text` VALUES ('1978', '6', '0', 'If I am excused, then I\'d like to check on Quinn...', '12', '0', '100', '0', '0', '0', '536', '0', 'erland SAY_MOVE_QUINN');
INSERT INTO `creature_text` VALUES ('1978', '7', '0', 'Hello, Quinn.  How are you faring?', '12', '0', '100', '0', '0', '0', '537', '0', 'erland SAY_QUINN');
INSERT INTO `creature_text` VALUES ('1951', '0', '0', 'I\'ve been better.  Ivar the Foul got the better of me...', '12', '33', '100', '0', '0', '0', '539', '0', 'erland SAY_QUINN_ANSWER');
INSERT INTO `creature_text` VALUES ('1978', '8', '0', 'Try to take better care of yourself, Quinn.  You were lucky this time.', '12', '0', '100', '0', '0', '0', '538', '0', 'erland SAY_BYE');
INSERT INTO `creature_text` VALUES ('24981', '0', '0', 'Deployment successful.  Trespassers will be neutralized.', '12', '0', '100', '0', '0', '0', '24016', '0', 'converted sentry SAY_CONVERTED_1');
INSERT INTO `creature_text` VALUES ('24981', '0', '1', 'Objective acquired.  Initiating security routines.', '12', '0', '100', '0', '0', '0', '24014', '0', 'converted sentry SAY_CONVERTED_2');
INSERT INTO `creature_text` VALUES ('6109', '0', '0', 'Come, little ones. Face me!', '14', '0', '100', '0', '0', '0', '9071', '0', 'azuregos SAY_TELEPORT');
INSERT INTO `creature_text` VALUES ('9623', '0', '0', 'A-Me good. Good, A-Me. Follow... follow A-Me. Home. A-Me go home.', '12', '0', '100', '0', '0', '0', '0', '0', 'ame SAY_READY');
INSERT INTO `creature_text` VALUES ('9623', '1', '0', '$N, no hurt A-Me. A-Me, good.', '12', '0', '100', '0', '0', '0', '5158', '0', 'ame SAY_AGGRO1');
INSERT INTO `creature_text` VALUES ('9623', '2', '0', 'Good... good, A-Me. A-Me good. Home. Find home.', '12', '0', '100', '0', '0', '0', '5063', '0', 'ame SAY_SEARCH');
INSERT INTO `creature_text` VALUES ('9623', '3', '0', 'Danger. Danger! $N try hurt A-Me.', '12', '0', '100', '0', '0', '0', '5159', '0', 'ame SAY_AGGRO2');
INSERT INTO `creature_text` VALUES ('9623', '4', '0', 'Bad, $N. $N, bad!', '12', '0', '100', '0', '0', '0', '5157', '0', 'ame SAY_AGGRO3');
INSERT INTO `creature_text` VALUES ('9623', '5', '0', 'A-Me home! A-Me, good! Good A-Me. Home. Home. Home.', '12', '0', '100', '0', '0', '0', '5156', '0', 'ame SAY_FINISH');
INSERT INTO `creature_text` VALUES ('9999', '0', '0', 'Well, I\'m not sure how far I\'ll make it in this state... I\'m feeling kind of faint...', '12', '0', '100', '0', '0', '0', '5391', '0', 'ringo SAY_RIN_START_1');
INSERT INTO `creature_text` VALUES ('9999', '0', '1', 'If I look woozy, slap me.  If I pass out, use the canteen.  Now let\'s go!', '12', '0', '100', '0', '0', '0', '5392', '0', 'ringo SAY_RIN_START_2');
INSERT INTO `creature_text` VALUES ('9999', '1', '0', 'The heat... I can\'t take it...', '12', '0', '100', '0', '0', '0', '5396', '0', 'ringo SAY_FAINT_1');
INSERT INTO `creature_text` VALUES ('9999', '1', '1', 'Maybe... you could carry me?', '12', '0', '100', '0', '0', '0', '5397', '0', 'ringo SAY_FAINT_2');
INSERT INTO `creature_text` VALUES ('9999', '1', '2', 'Uuuuuuggggghhhhh....', '12', '0', '100', '0', '0', '0', '5394', '0', 'ringo SAY_FAINT_3');
INSERT INTO `creature_text` VALUES ('9999', '1', '3', 'I\'m not feeling so well...', '12', '0', '100', '0', '0', '0', '5395', '0', 'ringo SAY_FAINT_4');
INSERT INTO `creature_text` VALUES ('9999', '2', '0', 'Where... where am I?', '12', '0', '100', '0', '0', '0', '26240', '0', 'ringo SAY_WAKE_1');
INSERT INTO `creature_text` VALUES ('9999', '2', '1', 'I am feeling a little better now, thank you.', '12', '0', '100', '0', '0', '0', '5399', '0', 'ringo SAY_WAKE_2');
INSERT INTO `creature_text` VALUES ('9999', '2', '2', 'Yes, I must go on.', '12', '0', '100', '0', '0', '0', '5398', '0', 'ringo SAY_WAKE_3');
INSERT INTO `creature_text` VALUES ('9999', '2', '3', 'How am I feeling? Quite soaked, thank you.', '12', '0', '100', '0', '0', '0', '5401', '0', 'ringo SAY_WAKE_4');
INSERT INTO `creature_text` VALUES ('9999', '3', '0', 'Spraggle! I didn\'t think I\'d make it back!', '12', '0', '100', '0', '0', '0', '5402', '0', 'ringo SAY_RIN_END_1');
INSERT INTO `creature_text` VALUES ('9997', '0', '0', 'Ringo! You\'re okay!', '12', '0', '100', '0', '0', '0', '5405', '0', 'ringo SAY_SPR_END_2');
INSERT INTO `creature_text` VALUES ('9999', '4', '0', 'Oh... I\'m feeling faint...', '12', '0', '100', '0', '0', '0', '5403', '0', 'ringo SAY_RIN_END_3');
INSERT INTO `creature_text` VALUES ('9999', '5', '0', '%s faints and falls onto the ground.', '16', '0', '100', '0', '0', '0', '5393', '0', 'ringo EMOTE_RIN_END_4');
INSERT INTO `creature_text` VALUES ('9999', '6', '0', '%s stands up after a short pause.', '16', '0', '100', '0', '0', '0', '0', '0', 'ringo EMOTE_RIN_END_5');
INSERT INTO `creature_text` VALUES ('9999', '7', '0', 'Ugh.', '12', '0', '100', '0', '0', '0', '5404', '0', 'ringo SAY_RIN_END_6');
INSERT INTO `creature_text` VALUES ('9997', '1', '0', 'Ringo? Wake up! Don\'t worry, I\'ll take care of you.', '12', '0', '100', '0', '0', '0', '5406', '0', 'ringo SAY_SPR_END_7');
INSERT INTO `creature_text` VALUES ('9999', '8', '0', '%s fades away after a long pause.', '16', '0', '100', '0', '0', '0', '0', '0', 'ringo EMOTE_RIN_END_8');
INSERT INTO `creature_text` VALUES ('3850', '3', '0', 'For once I agree with you... scum.', '12', '7', '100', '1', '0', '0', '1329', '0', 'prisoner ashcrombe SAY_BOSS_DIE_AS');
INSERT INTO `creature_text` VALUES ('7766', '1', '0', 'That\'s it! That\'s what you were waiting for! KILL THEM!', '14', '0', '100', '0', '0', '0', '4613', '0', 'lord gregor lescovar SAY_TYRION_2');
INSERT INTO `creature_text` VALUES ('26935', '0', '0', 'I\'m old enough to shoot beasts like you right between the eyes... at twice this distance.', '12', '0', '100', '0', '0', '0', '26110', '0', 'Sasha');
INSERT INTO `creature_text` VALUES ('26935', '1', '0', 'Don\'t you dare talk about my father, monster.  He was twice the man you\'ll ever be.', '12', '0', '100', '0', '0', '0', '26113', '0', 'Sasha');
INSERT INTO `creature_text` VALUES ('26935', '2', '0', 'I\'d rather be dead than be one of you!  You think you\'re still human?  You\'re... animals!', '12', '0', '100', '0', '0', '0', '26112', '0', 'Sasha');
INSERT INTO `creature_text` VALUES ('26935', '3', '0', 'Surprise, you scum!  You\'re going to tell me where my sister is or I\'ll put a bucketful of truesilver bullets through your wife\'s heart.', '12', '0', '100', '0', '0', '0', '26884', '0', 'Sasha');
INSERT INTO `creature_text` VALUES ('26935', '4', '0', 'Where is Anya?', '12', '0', '100', '0', '0', '0', '26889', '0', 'Sasha');
INSERT INTO `creature_text` VALUES ('26935', '5', '0', 'There is one last thing.  I need to know where Arugal is.', '12', '0', '100', '0', '0', '0', '26890', '0', 'Sasha');
INSERT INTO `creature_text` VALUES ('26971', '0', '0', 'How old are you, lass?', '12', '0', '100', '0', '0', '0', '26107', '0', 'Anatoly');
INSERT INTO `creature_text` VALUES ('26971', '1', '0', 'You won\'t get away with this, you know?  You\'re just a kid.', '12', '0', '100', '0', '0', '0', '26106', '0', 'Anatoly');
INSERT INTO `creature_text` VALUES ('26971', '2', '0', 'Your father was weak, Sasha... he didn\'t have the guts to do what had to be done.', '12', '0', '100', '0', '0', '0', '26109', '0', 'Anatoly');
INSERT INTO `creature_text` VALUES ('26971', '3', '0', 'We\'ll all end up serving the Lich King, kid.  Better this way than becoming a rotten corpse.', '12', '0', '100', '0', '0', '0', '26108', '0', 'Anatoly');
INSERT INTO `creature_text` VALUES ('26971', '4', '0', 'Stop!  Do not shoot!  Do not hurt Tatjana!', '12', '0', '100', '0', '0', '0', '26885', '0', 'Anatoly');
INSERT INTO `creature_text` VALUES ('26971', '5', '0', 'The brat\'s held prisoner in the wolf den on the other side of the mountain.  She was to be taken to Arugal.  Are we free to go now?', '12', '0', '100', '0', '0', '0', '26886', '0', 'Anatoly');
INSERT INTO `creature_text` VALUES ('26971', '6', '0', 'Forgive me, Tatjana... ', '12', '0', '100', '0', '0', '0', '26887', '0', 'Anatoly');
INSERT INTO `creature_text` VALUES ('26971', '7', '0', 'Nothing you can do can compare to what Arugal can do to us!  I will tear you apart myself!', '12', '0', '100', '0', '0', '0', '26888', '0', 'Anatoly');
INSERT INTO `creature_text` VALUES ('26935', '6', '0', 'Anya!!  You\'re all right!', '12', '0', '100', '0', '0', '0', '26915', '0', 'Sasha');
INSERT INTO `creature_text` VALUES ('26935', '7', '0', 'Badmoon?  You mean Bloodmoon?', '12', '0', '100', '0', '0', '0', '26916', '0', 'Sasha');
INSERT INTO `creature_text` VALUES ('26935', '8', '0', 'Don\'t worry, sister... no one can hurt you now.', '12', '0', '100', '0', '0', '0', '26917', '0', 'Sasha');
INSERT INTO `creature_text` VALUES ('27646', '0', '0', 'You won\'t eat me, will you?', '12', '0', '100', '0', '0', '0', '26914', '0', 'Anya');
INSERT INTO `creature_text` VALUES ('27646', '1', '0', 'Sister!  The mean men were going to take me to Badmoon Isle.  They wanted to turn me into one of them.', '12', '0', '100', '0', '0', '0', '26918', '0', 'Anya');
INSERT INTO `creature_text` VALUES ('27646', '2', '0', 'That\'s what I said!  Badmoon!  Sasha... don\'t ever leave me again!  First they took papa, then they took you... I don\'t want to be alone with them!', '12', '0', '100', '0', '0', '0', '26919', '0', 'Anya');
INSERT INTO `creature_text` VALUES ('25610', '0', '0', 'Don\'t let them turn me into one of those aberrations!', '12', '1', '100', '1', '0', '0', '24828', '0', 'Scourge Prisoner');
INSERT INTO `creature_text` VALUES ('25610', '0', '1', 'Kill me... Kill me now!', '12', '1', '100', '1', '0', '0', '24829', '0', 'Scourge Prisoner');
INSERT INTO `creature_text` VALUES ('25610', '0', '2', 'Somebody please... Help...', '12', '1', '100', '1', '0', '0', '24827', '0', 'Scourge Prisoner');
INSERT INTO `creature_text` VALUES ('25610', '0', '3', 'Don\'t let them turn me into one of those aberrations!', '12', '1', '100', '1', '0', '0', '24828', '0', 'Scourge Prisoner');
INSERT INTO `creature_text` VALUES ('25610', '1', '0', 'Freedom at last! I must return to Warsong Hold at once!', '12', '1', '100', '1', '0', '0', '24832', '0', 'Scourge Prisoner');
