INSERT INTO `creature_text` VALUES ('25208', '3', '0', 'Thank you for rescuing me, $R. Please tell the king that I am back.', '12', '0', '100', '0', '0', '0', '24313', '0', 'Lurgglbr');
INSERT INTO `creature_text` VALUES ('25208', '2', '0', 'This is far enough. I can make it on my own from here.', '12', '0', '100', '0', '0', '0', '24312', '0', 'Lurgglbr');
INSERT INTO `creature_text` VALUES ('25208', '0', '0', 'Together we will fight our way out of here. Are you ready?', '12', '0', '100', '0', '0', '0', '24310', '0', 'Lurgglbr');
INSERT INTO `creature_text` VALUES ('25208', '1', '0', 'You can\'t hold me against my will! You\'ve all been brainwashed!', '12', '0', '100', '0', '0', '0', '24316', '0', 'Lurgglbr');
INSERT INTO `creature_text` VALUES ('37597', '0', '0', 'Thank the Light for seeing you here safely! We have much work to do if we are to defeat the Lich King and put an end to the Scourge.', '12', '0', '100', '0', '0', '16617', '37370', '0', 'jaina SAY_INTRO_1');
INSERT INTO `creature_text` VALUES ('37597', '1', '0', 'Our allies within the Argent Crusade and the Knights of the Ebon Blade have broken through the front gate of Icecrown and are attempting to establish a foothold within the citadel. ', '12', '0', '100', '0', '0', '16618', '37371', '0', 'jaina SAY_INTRO_2');
INSERT INTO `creature_text` VALUES ('37597', '2', '0', 'Their success hinges upon what we discover within these cursed halls. Although our mission is fraught with peril, we must persevere!', '12', '0', '100', '0', '0', '16619', '37372', '0', 'jaina SAY_INTRO_3');
INSERT INTO `creature_text` VALUES ('37597', '3', '0', 'With the attention of the Lich King drawn towards the front gate, we will be working our way through the side in search of information that will enable us to defeat the Scourge once and for all.', '12', '0', '100', '0', '0', '16620', '37373', '0', 'jaina SAY_INTRO_4');
INSERT INTO `creature_text` VALUES ('37597', '4', '0', 'King Varian\'s SI:7 agents have gathered information about a private sanctum of the Lich King\'s deep within a place called the Halls of Reflection. ', '12', '0', '100', '0', '0', '16621', '37374', '0', 'jaina SAY_INTRO_5');
INSERT INTO `creature_text` VALUES ('37597', '5', '0', 'We will carve a path through this wretched place and find a way to enter the Halls of Reflection. I sense powerful magic hidden away within those halls. Magic that could be the key to destroying the Scourge!', '12', '0', '100', '0', '0', '16622', '37375', '0', 'jaina SAY_INTRO_6');
INSERT INTO `creature_text` VALUES ('37597', '6', '0', 'Your first mission is to destroy the machines of death within this malevolent engine of souls and clear a path for our soldiers. ', '12', '0', '100', '0', '0', '16623', '37376', '0', 'jaina SAY_INTRO_7');
INSERT INTO `creature_text` VALUES ('37597', '7', '0', 'Make haste, champions! I will prepare the troops to fall in behind you.', '12', '0', '100', '0', '0', '16624', '37377', '0', 'jaina SAY_INTRO_8');
INSERT INTO `creature_text` VALUES ('37596', '0', '0', 'The Argent Crusade and the Knights of the Ebon Blade have assaulted the gates of the Icecrown Citadel and are preparing for a massive attack upon the Scourge. Our mission is a bit more subtle, but equally as important.', '12', '0', '100', '0', '0', '17038', '37380', '0', 'sylvanas SAY_INTRO_1');
INSERT INTO `creature_text` VALUES ('37596', '1', '0', 'With the attention of the Lich King turned towards the front gate, we\'ll be working our way through the side in search of information that will enable us to defeat him - once and for all.', '12', '0', '100', '0', '0', '17039', '37381', '0', 'sylvanas SAY_INTRO_2');
INSERT INTO `creature_text` VALUES ('37596', '2', '0', 'Our scouts have reported that the Lich King has a private chamber, outside of the Frozen Throne, deep within a place called the Halls of Reflection. That is our target, champions. ', '12', '0', '100', '0', '0', '17040', '37382', '0', 'sylvanas SAY_INTRO_3');
INSERT INTO `creature_text` VALUES ('37596', '3', '0', 'We will cut a swath of destruction through this cursed place and find a way to enter the Halls of Reflection. If there is anything of value to be found here, it will be found in the halls.', '12', '0', '100', '0', '0', '17041', '37383', '0', 'sylvanas SAY_INTRO_4');
INSERT INTO `creature_text` VALUES ('37596', '4', '0', 'Your first mission is to destroy the machines of death within this malevolent engine of souls and clear a path for our soldiers. ', '12', '0', '100', '0', '0', '17042', '37376', '0', 'sylvanas SAY_INTRO_5');
INSERT INTO `creature_text` VALUES ('37596', '5', '0', 'The Dark Lady watches over you. Make haste!', '12', '0', '100', '0', '0', '17043', '37384', '0', 'sylvanas SAY_INTRO_6');
INSERT INTO `creature_text` VALUES ('38112', '0', '0', 'Men, women, and children... None were spared the master\'s wrath. Your death will be no different.', '14', '0', '100', '0', '0', '16710', '37931', '0', 'falric SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('38112', '1', '0', 'Sniveling maggot!', '14', '0', '100', '0', '0', '16711', '37941', '0', 'falric SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('38112', '1', '1', 'The children of Stratholme fought with more ferocity!', '14', '0', '100', '0', '0', '16712', '37942', '0', 'falric SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('38112', '2', '0', 'Marwyn, finish them...', '14', '0', '100', '0', '0', '16713', '37943', '0', 'falric SAY_DEATH');
INSERT INTO `creature_text` VALUES ('38112', '3', '0', 'Despair... so delicious...', '14', '0', '100', '0', '0', '16715', '37945', '0', 'falric SAY_IMPENDING_DESPAIR');
INSERT INTO `creature_text` VALUES ('38112', '4', '0', 'Fear... so exhilarating...', '14', '0', '100', '0', '0', '16716', '37946', '0', 'falric SAY_DEFILING_HORROR');
INSERT INTO `creature_text` VALUES ('37221', '3', '0', 'Stand back! Touch that blade and your soul will be scarred for all eternity! I must attempt to commune with the spirits locked away within Frostmourne. Give me space. Back up, please. ', '14', '0', '100', '0', '0', '16634', '37594', '0', 'Jaina SAY_JAINA_INTRO_4');
INSERT INTO `creature_text` VALUES ('37221', '2', '0', 'Frostmourne: the blade that destroyed our kingdom...', '14', '0', '100', '0', '0', '16633', '37593', '0', 'Jaina SAY_JAINA_INTRO_3');
INSERT INTO `creature_text` VALUES ('37221', '1', '0', 'What is that! Up ahead! Could it be? Heroes, at my side!', '14', '0', '100', '0', '0', '16632', '37592', '0', 'Jaina SAY_JAINA_INTRO_2');
INSERT INTO `creature_text` VALUES ('37221', '0', '0', 'The chill of this place... I can feel my blood freezing.', '14', '0', '100', '0', '0', '16631', '37591', '0', 'Jaina SAY_JAINA_INTRO_1');
INSERT INTO `creature_text` VALUES ('37221', '4', '0', 'Uther! Dear Uther! I... I\'m so sorry.', '12', '0', '100', '0', '0', '16635', '37595', '0', 'Jaina SAY_JAINA_INTRO_5');
INSERT INTO `creature_text` VALUES ('37221', '5', '0', 'Arthas is here? Maybe I...', '12', '0', '100', '0', '0', '16636', '37596', '0', 'Jaina SAY_JAINA_INTRO_6');
INSERT INTO `creature_text` VALUES ('37221', '6', '0', 'But Uther, if there\'s any hope of reaching Arthas. I... I must try.', '12', '0', '100', '0', '0', '16637', '37597', '0', 'Jaina SAY_JAINA_INTRO_7');
INSERT INTO `creature_text` VALUES ('37221', '7', '0', 'Tell me how, Uther? How do I destroy my prince? My...', '12', '0', '100', '0', '0', '16638', '37598', '0', 'Jaina SAY_JAINA_INTRO_8');
INSERT INTO `creature_text` VALUES ('37221', '8', '0', 'You\'re right, Uther. Forgive me. I... I don\'t know what got a hold of me. We will deliver this information to the King and the knights that battle the Scourge within Icecrown Citadel.', '12', '0', '100', '0', '0', '16639', '37599', '0', 'Jaina SAY_JAINA_INTRO_9');
INSERT INTO `creature_text` VALUES ('37221', '9', '0', 'Who could bear such a burden?', '12', '0', '100', '0', '0', '16640', '37600', '0', 'Jaina SAY_JAINA_INTRO_10');
INSERT INTO `creature_text` VALUES ('37221', '10', '0', 'Then maybe there is still hope...', '12', '0', '100', '0', '0', '16641', '37601', '0', 'Jaina SAY_JAINA_INTRO_11');
INSERT INTO `creature_text` VALUES ('37221', '11', '0', 'You won\'t deny me this, Arthas! I must know... I must find out... ', '14', '0', '100', '0', '0', '16642', '37602', '0', 'Jaina SAY_JAINA_INTRO_END');
INSERT INTO `creature_text` VALUES ('37226', '0', '0', 'SILENCE, PALADIN!', '14', '0', '100', '0', '0', '17225', '37613', '0', 'Lich King SAY_LK_INTRO_1');
INSERT INTO `creature_text` VALUES ('37226', '1', '0', 'So you wish to commune with the dead? You shall have your wish.', '14', '0', '100', '0', '0', '17226', '37614', '0', 'Lich King SAY_LK_INTRO_2');
INSERT INTO `creature_text` VALUES ('37226', '2', '0', 'Falric. Marwyn. Bring their corpses to my chamber when you are through.', '14', '0', '100', '0', '0', '17227', '37615', '0', 'Lich King SAY_LK_INTRO_3');
INSERT INTO `creature_text` VALUES ('38113', '0', '0', 'Death is all that you will find here!', '14', '0', '100', '0', '0', '16734', '37948', '0', 'marwyn SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('38112', '5', '0', 'As you wish, my lord.', '14', '0', '100', '0', '0', '16717', '37953', '0', 'Falric SAY_FALRIC_INTRO_1');
INSERT INTO `creature_text` VALUES ('38112', '6', '0', 'Soldiers of Lordaeron, rise to meet your master\'s call!', '14', '0', '100', '0', '0', '16714', '37944', '0', 'Falric SAY_FALRIC_INTRO_2');
INSERT INTO `creature_text` VALUES ('37223', '0', '0', 'I... I don\'t believe it! Frostmourne stands before us - unguarded - just as the gnome claimed. Come, heroes!', '14', '0', '100', '0', '0', '17049', '37568', '0', 'Sylvanas SAY_SYLVANAS_INTRO_1');
INSERT INTO `creature_text` VALUES ('37223', '1', '0', 'Standing this close to the blade that ended my life... The pain... It is renewed.', '14', '0', '100', '0', '0', '17050', '37569', '0', 'Sylvanas SAY_SYLVANAS_INTRO_2');
INSERT INTO `creature_text` VALUES ('37223', '2', '0', 'I dare not touch it. Stand back! Stand back as I attempt to commune with the blade! Perhaps our salvation lies within...', '14', '0', '100', '0', '0', '17051', '37570', '0', 'Sylvanas SAY_SYLVANAS_INTRO_3');
INSERT INTO `creature_text` VALUES ('37223', '3', '0', 'Uther... Uther the Lightbringer. How...', '12', '0', '100', '0', '0', '17052', '37571', '0', 'Sylvanas SAY_SYLVANAS_INTRO_4');
INSERT INTO `creature_text` VALUES ('37223', '4', '0', 'The Lich King is here? Then my destiny shall be fulfilled on this day! ', '12', '0', '100', '0', '0', '17053', '37572', '0', 'Sylvanas SAY_SYLVANAS_INTRO_5');
INSERT INTO `creature_text` VALUES ('37223', '5', '0', 'There must be a way...', '12', '0', '100', '0', '0', '17054', '37573', '0', 'Sylvanas SAY_SYLVANAS_INTRO_6');
INSERT INTO `creature_text` VALUES ('37223', '6', '0', 'Who could bear such a burden?', '12', '0', '100', '0', '0', '17055', '37600', '0', 'Sylvanas SAY_SYLVANAS_INTRO_7');
INSERT INTO `creature_text` VALUES ('37223', '7', '0', 'The Frozen Throne...', '12', '0', '100', '0', '0', '17056', '37575', '0', 'Sylvanas SAY_SYLVANAS_INTRO_8');
INSERT INTO `creature_text` VALUES ('37223', '8', '0', 'You will not escape me that easily, Arthas! I will have my vengeance! ', '14', '0', '100', '0', '0', '17057', '37576', '0', 'Sylvanas SAY_SYLVANAS_INTRO_END');
INSERT INTO `creature_text` VALUES ('37225', '0', '0', 'Jaina! Could it truly be you?', '14', '0', '100', '0', '0', '16666', '37603', '0', 'Uther SAY_UTHER_INTRO_A2_1');
INSERT INTO `creature_text` VALUES ('37225', '1', '0', 'Jaina, you haven\'t much time. The Lich King sees what the sword sees. He will be here shortly. ', '12', '0', '100', '0', '0', '16667', '37604', '0', 'Uther SAY_UTHER_INTRO_A2_2');
INSERT INTO `creature_text` VALUES ('37225', '2', '0', 'No, girl. Arthas is not here. Arthas is merely a presence within the Lich King\'s mind. A dwindling presence...', '12', '0', '100', '0', '0', '16668', '37605', '0', 'Uther SAY_UTHER_INTRO_A2_3');
INSERT INTO `creature_text` VALUES ('37225', '3', '0', 'Jaina, listen to me. You must destroy the Lich King. You cannot reason with him. He will kill you and your allies and raise you all as powerful soldiers of the Scourge.', '12', '0', '100', '0', '0', '16669', '37606', '0', 'Uther SAY_UTHER_INTRO_A2_4');
INSERT INTO `creature_text` VALUES ('37225', '4', '0', 'Snap out of it, girl. You must destroy the Lich King at the place where he merged with Ner\'zhul - atop the spire, at the Frozen Throne. It is the only way.', '12', '0', '100', '0', '0', '16670', '37607', '0', 'Uther SAY_UTHER_INTRO_A2_5');
INSERT INTO `creature_text` VALUES ('37225', '5', '0', 'There is... something else that you should know about the Lich King. Control over the Scourge must never be lost. Even if you were to strike down the Lich King, another would have to take his place. For without the control of its master, the Scourge would run rampant across the world - destroying all living things.', '12', '0', '100', '0', '0', '16671', '37608', '0', 'Uther SAY_UTHER_INTRO_A2_6');
INSERT INTO `creature_text` VALUES ('37225', '6', '0', 'A grand sacrifice by a noble soul...', '12', '0', '100', '0', '0', '16672', '37609', '0', 'Uther SAY_UTHER_INTRO_A2_7');
INSERT INTO `creature_text` VALUES ('37225', '7', '0', 'I do not know, Jaina. I suspect that the piece of Arthas that might be left inside the Lich King is all that holds the Scourge from annihilating Azeroth.', '12', '0', '100', '0', '0', '16673', '37610', '0', 'Uther SAY_UTHER_INTRO_A2_8');
INSERT INTO `creature_text` VALUES ('37225', '8', '0', 'No, Jaina! ARRRRRRGHHHH... He... He is coming. You... You must...', '12', '0', '100', '0', '0', '16674', '37611', '0', 'Uther SAY_UTHER_INTRO_A2_9');
INSERT INTO `creature_text` VALUES ('37225', '9', '0', 'Careful, girl. I\'ve heard talk of that cursed blade saving us before. Look around you and see what has been born of Frostmourne.', '12', '0', '100', '0', '0', '16659', '37583', '0', 'Uther SAY_UTHER_INTRO_H2_1');
INSERT INTO `creature_text` VALUES ('37225', '10', '0', 'You haven\'t much time. The Lich King sees what the sword sees. He will be here shortly. ', '12', '0', '100', '0', '0', '16660', '37584', '0', 'Uther SAY_UTHER_INTRO_H2_2');
INSERT INTO `creature_text` VALUES ('37225', '11', '0', 'You cannot defeat the Lich King. Not here. You would be a fool to try. He will kill those that follow you and raise them as powerful soldiers of the Scourge. But for you, Sylvanas, his reward for you would be worse than the last.', '12', '0', '100', '0', '0', '16661', '37585', '0', 'Uther SAY_UTHER_INTRO_H2_3');
INSERT INTO `creature_text` VALUES ('37225', '12', '0', 'Perhaps, but know this: there must always be a Lich King. Even if you were to strike down Arthas, another would have to take his place, for without the control of the Lich King, the Scourge would wash over this world like locusts, destroying all that they touched.', '12', '0', '100', '0', '0', '16662', '37586', '0', 'Uther SAY_UTHER_INTRO_H2_4');
INSERT INTO `creature_text` VALUES ('37225', '13', '0', 'I do not know, Banshee Queen. I suspect that the piece of Arthas that might be left inside the Lich King is all that holds the Scourge from annihilating Azeroth.', '12', '0', '100', '0', '0', '16663', '37587', '0', 'Uther SAY_UTHER_INTRO_H2_5');
INSERT INTO `creature_text` VALUES ('37225', '14', '0', 'Alas, the only way to defeat the Lich King is to destroy him at the place where he was created.', '12', '0', '100', '0', '0', '16664', '37588', '0', 'Uther SAY_UTHER_INTRO_H2_6');
INSERT INTO `creature_text` VALUES ('37225', '15', '0', 'Aye. ARRRRRRGHHHH... He... He is coming. You... You must...', '12', '0', '100', '0', '0', '16665', '37589', '0', 'Uther SAY_UTHER_INTRO_H2_7');
INSERT INTO `creature_text` VALUES ('36477', '0', '0', 'Our work must not be interrupted! Ick, take care of them!', '14', '0', '100', '0', '0', '16926', '37222', '0', 'Krick SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36477', '1', '0', 'We could probably use these parts.', '14', '0', '100', '0', '0', '16927', '37223', '0', 'Krick SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('36477', '1', '1', 'Arms and legs are in short supply! Thanks for your contribution!', '14', '0', '100', '0', '0', '16928', '37224', '0', 'Krick SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('36477', '2', '0', 'Enough moving around! Hold still while I blow them all up!', '14', '0', '100', '0', '0', '16929', '36538', '0', 'Krick SAY_BARRAGE_1');
INSERT INTO `creature_text` VALUES ('36477', '3', '0', '%s begins rapidly conjuring explosive mines!', '41', '0', '100', '0', '0', '0', '36550', '0', 'Krick SAY_BARRAGE_2');
INSERT INTO `creature_text` VALUES ('36477', '4', '0', 'Quickly! Poison them all while they\'re still close!', '14', '0', '100', '0', '0', '16930', '36537', '0', 'Krick SAY_POISON_NOVA');
INSERT INTO `creature_text` VALUES ('36477', '5', '0', 'No, that one! That one! Get that one!', '14', '0', '100', '0', '0', '16931', '36536', '0', 'Krick SAY_CHASE_1');
INSERT INTO `creature_text` VALUES ('36477', '5', '1', 'I\'ve changed my mind, go get that one instead!', '14', '0', '100', '0', '0', '16932', '37225', '0', 'Krick SAY_CHASE_2');
INSERT INTO `creature_text` VALUES ('36477', '5', '2', 'What are you attacking him for? The dangerous one is over there!', '14', '0', '100', '0', '0', '16933', '37226', '0', 'Krick SAY_CHASE_3');
INSERT INTO `creature_text` VALUES ('36477', '6', '0', 'Wait! Stop! Don\'t kill me, please! I\'ll tell you everything!', '14', '0', '100', '0', '0', '16934', '36841', '0', 'Krick SAY_KRICK_OUTRO_1');
INSERT INTO `creature_text` VALUES ('36477', '7', '0', 'What you seek is in the master\'s lair, but you must destroy Tyrannus to gain entry. Now, within the Halls of Reflection you will find Frostmourne. It... It holds the truth.', '14', '0', '100', '0', '0', '16935', '36842', '0', 'Krick SAY_KRICK_OUTRO_3');
INSERT INTO `creature_text` VALUES ('36477', '8', '0', 'I swear it is true! Please, don\'t kill me!!', '14', '0', '100', '0', '0', '16936', '36843', '0', 'Krick SAY_KRICK_OUTRO_5');
INSERT INTO `creature_text` VALUES ('36477', '9', '0', 'Urg... no!!', '14', '0', '100', '0', '0', '16937', '36844', '0', 'Krick SAY_KRICK_OUTRO_8');
INSERT INTO `creature_text` VALUES ('36476', '0', '0', '%s begins to unleash a toxic poison cloud!', '41', '0', '100', '0', '0', '0', '36531', '0', 'Ick SAY_ICK_POISON_NOVA');
INSERT INTO `creature_text` VALUES ('36476', '1', '0', '%s is chasing you. Run!', '41', '0', '100', '0', '0', '0', '36529', '0', 'Ick SAY_ICK_CHASE_1');
INSERT INTO `creature_text` VALUES ('36658', '0', '0', 'Another shall take his place. You waste your time.', '14', '0', '100', '0', '0', '16752', '36765', '0', 'Tyrannus SAY_TYRANNUS_DEATH');
INSERT INTO `creature_text` VALUES ('36794', '1', '0', 'Worthless gnat! Death is all that awaits you!', '14', '0', '100', '0', '0', '16753', '36852', '0', 'Tyrannus SAY_TYRANNUS_OUTRO_7');
INSERT INTO `creature_text` VALUES ('36794', '2', '0', 'Do not think that I shall permit you entry into my master\'s sanctum so easily. Pursue me if you dare.', '14', '0', '100', '0', '0', '16754', '36853', '0', 'Tyrannus SAY_TYRANNUS_OUTRO_9');
INSERT INTO `creature_text` VALUES ('36658', '3', '0', 'Your pursuit shall be in vain, intruders, for the Lich King has placed an army of undead at my command! Behold!', '14', '0', '100', '0', '0', '16755', '36708', '0', 'Tyrannus SAY_AMBUSH_1');
INSERT INTO `creature_text` VALUES ('36658', '4', '0', 'Persistent whelps! You will not reach the entrance of my lord\'s lair! Soldiers, destroy them!', '14', '0', '100', '0', '0', '16756', '36709', '0', 'Tyrannus SAY_AMBUSH_2');
INSERT INTO `creature_text` VALUES ('36658', '5', '0', 'Rimefang, trap them within the tunnel! BURY THEM ALIVE!', '14', '0', '100', '0', '0', '16757', '36714', '0', 'Tyrannus SAY_GAUNTLET_START');
INSERT INTO `creature_text` VALUES ('36658', '6', '0', 'Alas, brave, brave adventurers, your meddling has reached its end. Do you hear the clatter of bone and steel coming up the tunnel behind you? That is the sound of your impending demise.', '14', '0', '100', '0', '0', '16758', '37233', '0', 'Tyrannus SAY_TYRANNUS_INTRO_1');
INSERT INTO `creature_text` VALUES ('36658', '7', '0', 'Ha, such an amusing gesture from the rabble. When I have finished with you, my master\'s blade will feast upon your souls. Die.', '14', '0', '100', '0', '0', '16759', '37234', '0', 'Tyrannus SAY_TYRANNUS_INTRO_3');
INSERT INTO `creature_text` VALUES ('36658', '8', '0', 'I shall not fail the Lich King. Come and meet your end!', '14', '0', '100', '0', '0', '16760', '38718', '0', 'Tyrannus SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36658', '9', '0', 'Such a shameful display. You are better off dead.', '14', '0', '100', '0', '0', '16761', '38715', '0', 'Tyrannus SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('36658', '9', '1', 'Perhaps you should have stayed... in the mountains!', '14', '0', '100', '0', '0', '16762', '38716', '0', 'Tyrannus SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('36658', '10', '0', 'Impossible.... Rimefang.... warn....', '14', '0', '100', '0', '0', '16763', '38714', '0', 'Tyrannus SAY_DEATH');
INSERT INTO `creature_text` VALUES ('36658', '11', '0', 'Rimefang, destroy this fool!', '14', '0', '100', '0', '0', '16764', '36648', '0', 'Tyrannus SAY_MARK_RIMEFANG_1');
INSERT INTO `creature_text` VALUES ('36658', '12', '0', 'The frostwyrm Rimefang gazes at $N and readies an icy attack!', '41', '0', '100', '0', '0', '0', '36649', '0', 'Tyrannus SAY_MARK_RIMEFANG_2');
INSERT INTO `creature_text` VALUES ('36658', '13', '0', 'Power... overwhelming!', '14', '0', '100', '0', '0', '16765', '38717', '0', 'Tyrannus SAY_DARK_MIGHT_1');
INSERT INTO `creature_text` VALUES ('36658', '14', '0', '%s roars and swells with dark might!', '41', '0', '100', '0', '0', '0', '36628', '0', 'Tyrannus SAY_DARK_MIGHT_2');
INSERT INTO `creature_text` VALUES ('37592', '0', '0', 'Heroes! We will hold off the undead as long as we can, even to our dying breath. Deal with the Scourgelord!', '14', '0', '100', '0', '0', '17150', '37232', '0', 'Gorkun SAY_GORKUN_INTRO_2');
INSERT INTO `creature_text` VALUES ('37592', '1', '0', 'Brave champions, we owe you our lives, our freedom.... Though it be a tiny gesture in the face of this enormous debt, I pledge that from this day forth, all will know of your deeds, and the blazing path of light you cut through the shadow of this dark citadel. ', '14', '0', '100', '0', '0', '0', '37320', '0', 'Gorkun SAY_GORKUN_OUTRO_1');
INSERT INTO `creature_text` VALUES ('37592', '2', '0', 'This day will stand as a testament not only to your valor, but to the fact that no foe, not even the Lich King himself, can stand when Alliance and Horde set aside their differences and---', '14', '0', '100', '0', '0', '0', '37321', '0', 'Gorkun SAY_GORKUN_OUTRO_2');
INSERT INTO `creature_text` VALUES ('36993', '0', '0', 'I\'m not so naive as to believe your appeal for clemency, but I will listen.', '14', '0', '100', '0', '0', '16611', '36854', '0', 'Jaina SAY_JAINA_OUTRO_2');
INSERT INTO `creature_text` VALUES ('36993', '1', '0', 'Frostmourne lies unguarded? Impossible!', '14', '0', '100', '0', '0', '16612', '36856', '0', 'Jaina SAY_JAINA_OUTRO_4');
INSERT INTO `creature_text` VALUES ('36993', '2', '0', 'What a cruel end. Come, heroes. We must see if the gnome\'s story is true. If we can separate Arthas from Frostmourne, we might have a chance at stopping him.', '14', '0', '100', '0', '0', '16613', '36855', '0', 'Jaina SAY_JAINA_OUTRO_10');
INSERT INTO `creature_text` VALUES ('36993', '3', '0', 'Heroes, to me!', '14', '0', '100', '0', '0', '16614', '37479', '0', 'Jaina SAY_JAYNA_OUTRO_3');
INSERT INTO `creature_text` VALUES ('36993', '4', '0', 'The Frost Queen is gone. We must keep moving - our objective is near.', '12', '0', '100', '0', '0', '16615', '37478', '0', 'Jaina SAY_JAYNA_OUTRO_4');
INSERT INTO `creature_text` VALUES ('36993', '5', '0', 'I... I could not save them... Damn you, Arthas! DAMN YOU!', '12', '0', '100', '0', '0', '16616', '37480', '0', 'Jaina SAY_JAYNA_OUTRO_5');
INSERT INTO `creature_text` VALUES ('36990', '0', '0', 'Why should the Banshee Queen spare your miserable life?', '14', '0', '100', '0', '0', '17033', '36845', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_2');
INSERT INTO `creature_text` VALUES ('36990', '1', '0', 'Frostmourne? The Lich King is never without his blade. If you are lying to me...', '14', '0', '100', '0', '0', '17034', '36846', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_4');
INSERT INTO `creature_text` VALUES ('36990', '2', '0', 'A fitting end for a traitor. Come, we must free the slaves and see what is within the Lich King\'s chamber for ourselves.', '14', '0', '100', '0', '0', '17035', '36847', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_10');
INSERT INTO `creature_text` VALUES ('36990', '3', '0', 'Take cover behind me! Quickly!', '14', '0', '100', '0', '0', '17037', '37540', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_3');
INSERT INTO `creature_text` VALUES ('36990', '4', '0', 'I thought he\'d never shut up. At last, Sindragosa silenced that long-winded fool. To the Halls of Reflection, champions! Our objective is near... I can sense it.', '12', '0', '100', '0', '0', '17036', '37541', '0', 'Sylvanas SAY_SYLVANAS_OUTRO_4');
INSERT INTO `creature_text` VALUES ('26588', '0', '0', 'Um... I think one of those wolves is back...', '12', '0', '100', '0', '0', '0', '25803', '0', 'emily SAY_WORGHAGGRO1');
INSERT INTO `creature_text` VALUES ('26588', '1', '0', 'He\'s going for Mr. Floppy!', '12', '0', '100', '0', '0', '0', '25804', '0', 'emily SAY_WORGHAGGRO2');
INSERT INTO `creature_text` VALUES ('26588', '2', '0', 'Oh, no! Look, it\'s another wolf, and it\'s a biiiiiiig one!', '12', '0', '100', '0', '0', '0', '25806', '0', 'emily SAY_WORGRAGGRO3');
INSERT INTO `creature_text` VALUES ('26588', '3', '0', 'He\'s gonna eat Mr. Floppy! You gotta help Mr. Floppy! You just gotta!', '12', '0', '100', '0', '0', '0', '26892', '0', 'emily SAY_WORGRAGGRO4');
INSERT INTO `creature_text` VALUES ('26588', '4', '0', 'There\'s a big meanie attacking Mr. Floppy! Help!', '12', '0', '100', '0', '0', '0', '26893', '0', 'emily SAY_RANDOMAGGRO');
