INSERT INTO `creature_text` VALUES ('26588', '6', '0', 'Don\'t go toward the light, Mr. Floppy!', '12', '0', '100', '0', '0', '0', '25809', '0', 'emily SAY_VICTORY2');
INSERT INTO `creature_text` VALUES ('26588', '7', '0', 'Mr. Floppy, you\'re ok! Thank you so much for saving Mr. Floppy!', '12', '0', '100', '0', '0', '0', '25810', '0', 'emily SAY_VICTORY3');
INSERT INTO `creature_text` VALUES ('26588', '8', '0', 'I think I see the camp! We\'re almost home, Mr. Floppy! Let\'s go!', '12', '0', '100', '0', '0', '0', '26894', '0', 'emily SAY_VICTORY4');
INSERT INTO `creature_text` VALUES ('26588', '9', '0', 'Mr. Floppy revives', '15', '0', '100', '0', '0', '0', '0', '0', 'emily TEXT_EMOTE_WP1');
INSERT INTO `creature_text` VALUES ('26588', '10', '0', 'The Ravenous Worg chomps down on Mr. Floppy', '15', '0', '100', '0', '0', '0', '0', '0', 'emily TEXT_EMOTE_AGGRO');
INSERT INTO `creature_text` VALUES ('26588', '11', '0', 'Are you ready, Mr. Floppy? Stay close to me and watch out for those wolves!', '12', '0', '100', '0', '0', '0', '25802', '0', 'emily SAY_QUEST_ACCEPT');
INSERT INTO `creature_text` VALUES ('26588', '12', '0', 'Thank you for helping me to get back to the camp. Go tell Walter that I\'m safe now!', '12', '0', '100', '0', '0', '0', '26891', '0', 'emily SAY_QUEST_COMPLETE');
INSERT INTO `creature_text` VALUES ('27570', '0', '0', 'We\'re all gonna die!', '12', '0', '100', '0', '0', '0', '26848', '0', 'venture co straggler SAY1');
