INSERT INTO `creature_text` VALUES ('28217', '0', '0', 'The Injured Rainspeaker Oracle rises clumsily to its feet.', '16', '0', '100', '0', '0', '0', '27785', '0', 'Injured Rainspeaker Oracle');
INSERT INTO `creature_text` VALUES ('28315', '0', '0', 'Frenzyheart kill you if you come back. You no welcome here no more!', '12', '0', '100', '0', '0', '0', '27837', '0', 'vekjik SAY_TEXTID_VEKJIK1');
INSERT INTO `creature_text` VALUES ('28787', '6', '1', 'We made it! Thank you for getting me out of that hell hole. Tell Hemet to expect me!', '12', '0', '100', '0', '0', '0', '28865', '0', 'engineer helice SAY_WP_7');
INSERT INTO `creature_text` VALUES ('28787', '5', '1', 'You really shouldn\'t play with this stuff. Someone could get hurt.', '12', '0', '100', '0', '0', '0', '28864', '0', 'engineer helice SAY_WP_6');
INSERT INTO `creature_text` VALUES ('28787', '4', '1', 'Oh, look, it\'s another cartload of explosives! Let\'s help them dispose of it.', '12', '0', '100', '0', '0', '0', '28862', '0', 'engineer helice SAY_WP_5');
INSERT INTO `creature_text` VALUES ('28787', '3', '0', 'It\'s getting a little hot over here. Shall we move on?', '12', '0', '100', '0', '0', '0', '28860', '0', 'engineer helice SAY_WP_4');
INSERT INTO `creature_text` VALUES ('28787', '2', '1', 'Or THIS is what you get.', '12', '0', '100', '0', '0', '0', '28859', '0', 'engineer helice SAY_WP_3');
INSERT INTO `creature_text` VALUES ('28787', '1', '1', 'Listen up, Venture Company goons! Rule #1: Never keep the prisoner near the explosives.', '12', '0', '100', '0', '0', '0', '28857', '0', 'engineer helice SAY_WP_2');
INSERT INTO `creature_text` VALUES ('28787', '0', '1', 'Let\'s get the hell out of here.', '12', '0', '100', '0', '0', '0', '28856', '0', 'engineer helice SAY_WP_1');
