INSERT INTO `creature_text` VALUES ('28604', '1', '0', 'Glad I could help!', '12', '0', '100', '0', '0', '0', '28593', '0', 'adventurous dwarf SAY_DWARF_HELP');
INSERT INTO `creature_text` VALUES ('30154', '0', '0', 'I\'m not afraid of anything -- bring it on!', '12', '0', '100', '0', '0', '0', '30807', '0', 'agnetta SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('29434', '0', '0', 'Let me know when you\'re ready. I\'d prefer sooner than later... what with the slowly dying from poison and all.', '12', '0', '100', '0', '0', '0', '30645', '0', 'injured goblin SAY_QUEST_START');
INSERT INTO `creature_text` VALUES ('29434', '1', '0', 'I\'m going to bring the venom sac to Ricket... and then... you know... collapse. Thank you for helping me!', '12', '0', '100', '0', '0', '0', '30646', '0', 'injured goblin SAY_END_WP_REACHED');
