INSERT INTO `creature_text` VALUES ('27977', '2', '0', 'Uuuuhhhhhhhhhh......', '14', '0', '100', '0', '0', '14179', '29560', '0', 'krystallus SAY_DEATH');
INSERT INTO `creature_text` VALUES ('27977', '3', '0', 'Break.... you....', '14', '0', '100', '0', '0', '14178', '29559', '0', 'krystallus SAY_SHATTER');
INSERT INTO `creature_text` VALUES ('27975', '0', '0', 'You shouldn\'t have come... now you will die!', '14', '0', '100', '0', '0', '13487', '29563', '0', 'maiden of grief SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('27975', '1', '0', 'Why must it be this way?', '14', '0', '100', '0', '0', '13488', '29564', '0', 'maiden of grief SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('27975', '1', '1', 'You had it coming!', '14', '0', '100', '0', '0', '13489', '29566', '0', 'maiden of grief SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('27975', '1', '2', 'My burden grows heavier...', '14', '0', '100', '0', '0', '13490', '29567', '0', 'maiden of grief SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('27975', '1', '3', 'This is your fault!', '14', '0', '100', '0', '0', '13491', '29568', '0', 'maiden of grief SAY_SLAY_4');
INSERT INTO `creature_text` VALUES ('27975', '2', '0', 'I hope you all rot! I never... wanted... this.', '14', '0', '100', '0', '0', '13493', '29574', '0', 'maiden of grief SAY_DEATH');
INSERT INTO `creature_text` VALUES ('27975', '3', '0', 'So much lost time... that you\'ll never get back!', '14', '0', '100', '0', '0', '13492', '29570', '0', 'maiden of grief SAY_STUN');
INSERT INTO `creature_text` VALUES ('27978', '0', '0', 'Soft, vulnerable shells. Brief fragile lives. You cannot escape the curse of flesh!', '14', '0', '100', '0', '0', '14180', '29582', '0', 'Sjonnir SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('27978', '1', '0', 'Flesh is no match for iron!', '14', '0', '100', '0', '0', '14182', '29584', '0', 'Sjonnir SAY_SLAY_1');
INSERT INTO `creature_text` VALUES ('27978', '1', '1', 'Armies of iron will smother the world!', '14', '0', '100', '0', '0', '14183', '29585', '0', 'Sjonnir SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('27978', '1', '2', 'Folvynn buul hrom onn!', '12', '0', '100', '0', '0', '0', '29583', '0', 'Sjonnir SAY_SLAY_3');
INSERT INTO `creature_text` VALUES ('27978', '2', '0', 'Loken will not rest... until the forge is retaken! You change... nothing!', '14', '0', '100', '0', '0', '14184', '29586', '0', 'Sjonnir SAY_DEATH');
INSERT INTO `creature_text` VALUES ('28070', '0', '0', 'Now that\'s earnin\' your supper!', '14', '0', '100', '0', '0', '14244', '30525', '0', 'brann SAY_KILL_1');
INSERT INTO `creature_text` VALUES ('28070', '0', '1', 'Press on; that\'s the way!', '14', '0', '100', '0', '0', '14245', '30526', '0', 'brann SAY_KILL_2');
INSERT INTO `creature_text` VALUES ('28070', '0', '2', 'Keep it up now. Plenty of death-dealing for everyone!', '14', '0', '100', '0', '0', '14246', '0', '0', 'brann SAY_KILL_3');
INSERT INTO `creature_text` VALUES ('28070', '1', '0', 'I\'m all kinds of busted up... might not... make it.', '14', '0', '100', '0', '0', '14257', '28121', '0', 'brann SAY_LOW_HEALTH');
INSERT INTO `creature_text` VALUES ('28070', '2', '0', 'Not yet... not ye--', '14', '0', '100', '0', '0', '14258', '30522', '0', 'brann SAY_DEATH');
INSERT INTO `creature_text` VALUES ('28070', '3', '0', 'I\'m doin\' everything I can!', '14', '0', '100', '0', '0', '14260', '30562', '0', 'brann SAY_PLAYER_DEATH_1');
INSERT INTO `creature_text` VALUES ('28070', '3', '1', 'Light preserve ye!', '14', '0', '100', '0', '0', '14261', '30563', '0', 'brann SAY_PLAYER_DEATH_2');
INSERT INTO `creature_text` VALUES ('28070', '3', '2', 'I hope this is all worth it!', '14', '0', '100', '0', '0', '14262', '30564', '0', 'brann SAY_PLAYER_DEATH_3');
INSERT INTO `creature_text` VALUES ('28070', '4', '0', 'Time to get some answers! Let\'s get this show on the road!', '14', '0', '100', '0', '0', '14259', '30561', '0', 'brann SAY_ESCORT_START');
INSERT INTO `creature_text` VALUES ('28070', '5', '0', 'Don\'t worry! Ol\' Brann\'s got yer back! Keep that metal monstrosity busy, and I\'ll see if I can\'t sweet talk this machine into helping ye!', '14', '0', '100', '0', '0', '14274', '31555', '0', 'brann SAY_SPAWN_DWARF');
INSERT INTO `creature_text` VALUES ('28070', '6', '0', 'This is a wee bit trickier than before... Oh, bloody--incomin\'!', '14', '0', '100', '0', '0', '14275', '27589', '0', 'brann SAY_SPAWN_TROGG');
INSERT INTO `creature_text` VALUES ('28070', '7', '0', 'What in the name o\' Madoran did THAT do? Oh! Wait: I just about got it...', '14', '0', '100', '0', '0', '14276', '27590', '0', 'brann SAY_SPAWN_OOZE');
INSERT INTO `creature_text` VALUES ('28070', '8', '0', 'Ha, that did it! Help\'s a-comin\'! Take this, ya glowin\' iron brute!', '14', '0', '100', '0', '0', '14277', '27591', '0', 'brann SAY_SPAWN_EARTHEN');
INSERT INTO `creature_text` VALUES ('28070', '9', '0', 'Take a moment and relish this with me. Soon... all will be revealed. Okay then, let\'s do this!', '14', '0', '100', '0', '0', '14247', '30523', '0', 'brann SAY_EVENT_INTRO_1');
INSERT INTO `creature_text` VALUES ('28070', '10', '0', 'Now keep an eye out! I\'ll have this licked in two shakes of a--', '14', '0', '100', '0', '0', '14248', '30552', '0', 'brann SAY_EVENT_INTRO_2');
INSERT INTO `creature_text` VALUES ('28070', '11', '0', 'Oh, that doesn\'t sound good. We might have a complication or two...', '14', '0', '100', '0', '0', '14249', '30553', '0', 'brann SAY_EVENT_A_1');
INSERT INTO `creature_text` VALUES ('28070', '12', '0', 'Ah, you want to play hardball, eh? That\'s just my game!', '14', '0', '100', '0', '0', '14250', '30554', '0', 'brann SAY_EVENT_A_3');
INSERT INTO `creature_text` VALUES ('28070', '13', '0', 'Couple more minutes and I\'ll--', '14', '0', '100', '0', '0', '14251', '30555', '0', 'brann SAY_EVENT_B_1');
INSERT INTO `creature_text` VALUES ('28070', '14', '0', 'Heightened? What\'s the good news?', '14', '0', '100', '0', '0', '14252', '30556', '0', 'brann SAY_EVENT_B_3');
INSERT INTO `creature_text` VALUES ('28070', '15', '0', 'So that was the problem? Now I\'m makin\' progress...', '14', '0', '100', '0', '0', '14253', '30557', '0', 'brann SAY_EVENT_C_1');
INSERT INTO `creature_text` VALUES ('28070', '16', '0', 'Hang on! Nobody\'s gonna\' be sanitized as long as I have a say in it!', '14', '0', '100', '0', '0', '14254', '30558', '0', 'brann SAY_EVENT_C_3');
INSERT INTO `creature_text` VALUES ('28070', '17', '0', 'Ha! The old magic fingers finally won through! Now let\'s get down to--', '14', '0', '100', '0', '0', '14255', '30559', '0', 'brann SAY_EVENT_D_1');
INSERT INTO `creature_text` VALUES ('28070', '18', '0', 'Purge? No no no no no.. where did I-- Aha, this should do the trick...', '14', '0', '100', '0', '0', '14256', '30560', '0', 'brann SAY_EVENT_D_3');
INSERT INTO `creature_text` VALUES ('28070', '19', '0', 'Query? What do you think I\'m here for, tea and biscuits? Spill the beans already!', '14', '0', '100', '0', '0', '14263', '30565', '0', 'brann SAY_EVENT_END_01');
INSERT INTO `creature_text` VALUES ('28070', '20', '0', 'Tell me how the dwarves came to be, and start at the beginning!', '14', '0', '100', '0', '0', '14264', '30566', '0', 'brann SAY_EVENT_END_02');
INSERT INTO `creature_text` VALUES ('28070', '21', '0', 'Right, right... I know the earthen were made from stone to shape the deep regions o\' the world. But what about the anomalies? Matrix non-stabilizin\' and what-not?', '14', '0', '100', '0', '0', '14265', '30567', '0', 'brann SAY_EVENT_END_04');
INSERT INTO `creature_text` VALUES ('28070', '22', '0', 'Necrowhatinthe-- Speak bloody Common, will ye?', '14', '0', '100', '0', '0', '14266', '30568', '0', 'brann SAY_EVENT_END_06');
INSERT INTO `creature_text` VALUES ('28070', '23', '0', 'Old Gods, huh? So they zapped the earthen with this Curse of Flesh... and then what?', '14', '0', '100', '0', '0', '14267', '30577', '0', 'brann SAY_EVENT_END_08');
INSERT INTO `creature_text` VALUES ('28070', '24', '0', 'If they killed the Old Gods, Azeroth would\'ve been destroyed...', '14', '0', '100', '0', '0', '14268', '30578', '0', 'brann SAY_EVENT_END_10');
INSERT INTO `creature_text` VALUES ('28070', '25', '0', 'What protectors?', '14', '0', '100', '0', '0', '14269', '30579', '0', 'brann SAY_EVENT_END_12');
INSERT INTO `creature_text` VALUES ('28070', '26', '0', 'Aesir and Vanir... Okay, so the Forge o\' Wills started makin\' new earthen... but what happened to the old ones?', '14', '0', '100', '0', '0', '14270', '30580', '0', 'brann SAY_EVENT_END_14');
INSERT INTO `creature_text` VALUES ('28070', '27', '0', 'Hold everything! The Aesir and Vanir went to war? Why?', '14', '0', '100', '0', '0', '14271', '30581', '0', 'brann SAY_EVENT_END_16');
INSERT INTO `creature_text` VALUES ('28070', '28', '0', 'This \"Loken\" sounds like a nasty character. Glad we don\'t have to worry about the likes o\' him anymore. So... if I\'m understandin\' ye right, the original earthen eventually woke up from this stasis, and by that time the destabili-whatever had turned \'em into proper dwarves. Or at least... dwarf ancestors.', '14', '0', '100', '0', '0', '14272', '30582', '0', 'brann SAY_EVENT_END_18');
INSERT INTO `creature_text` VALUES ('28070', '29', '0', 'Well, now... that\'s a lot to digest. I\'m gonna need some time to take all this in. Thank ye.', '14', '0', '100', '0', '0', '14273', '30583', '0', 'brann SAY_EVENT_END_20');
INSERT INTO `creature_text` VALUES ('28070', '30', '0', 'Loken?! That\'s downright bothersome... We might\'ve neutralized the iron dwarves, but I\'d lay odds there\'s another machine somewhere else churnin\' out a whole mess o\' these iron vrykul!', '14', '0', '100', '0', '0', '14278', '28591', '0', 'brann SAY_VICTORY_SJONNIR_1');
INSERT INTO `creature_text` VALUES ('28070', '31', '0', 'I\'ll use the forge to make batches o\' earthen to stand guard... But our greatest challenge still remains: find and stop Loken!', '14', '0', '100', '0', '0', '14279', '30584', '0', 'brann SAY_VICTORY_SJONNIR_2');
INSERT INTO `creature_text` VALUES ('28070', '32', '0', 'I think it\'s time to see what\'s behind the door near the entrance. I\'m going to sneak over there, nice and quiet. Meet me at the door and I\'ll get us in.', '14', '0', '100', '0', '0', '0', '31660', '0', 'brann SAY_ENTRANCE_MEET');
INSERT INTO `creature_text` VALUES ('30897', '0', '0', 'Threat index threshold exceeded.  Celestial archive aborted. Security level heightened.', '14', '0', '100', '0', '0', '13761', '29576', '0', 'brann SAY_EVENT_B_2_MARN');
INSERT INTO `creature_text` VALUES ('30897', '1', '0', 'Additional background is relevant to your query: following global combat between Aesir and Vanir--', '14', '0', '100', '0', '0', '13762', '30606', '0', 'brann SAY_EVENT_END_15_MARN');
INSERT INTO `creature_text` VALUES ('30897', '2', '0', 'Unknown. Data suggests that impetus for global combat originated with prime designate Loken, who neutralized all remaining Aesir and Vanir, affecting termination of conflict. Prime designate Loken then initiated stasis of several seed races, including earthen, giants and vrykul, at designated holding facilities.', '14', '0', '100', '0', '0', '13763', '30607', '0', 'brann SAY_EVENT_END_17_MARN');
INSERT INTO `creature_text` VALUES ('30897', '3', '0', 'Essentially that is correct.', '14', '0', '100', '0', '0', '13764', '30608', '0', 'brann SAY_EVENT_END_19_MARN');
INSERT INTO `creature_text` VALUES ('30898', '0', '0', 'Security breach in progress. Analysis of historical archives transferred to lower-priority queue. Countermeasures engaged.', '14', '0', '100', '0', '0', '13756', '29575', '0', 'brann SAY_EVENT_A_2_KADD');
INSERT INTO `creature_text` VALUES ('30898', '1', '0', 'Accessing... Creators arrived to extirpate symbiotic infection. Assessment revealed that Old God infestation had grown malignant. Excising parasites would result in loss of host--', '14', '0', '100', '0', '0', '13757', '30603', '0', 'brann SAY_EVENT_END_09_KADD');
INSERT INTO `creature_text` VALUES ('30898', '2', '0', 'Correct. Creators neutralized parasitic threat and contained it within the host. Forge of Wills and other systems were instituted to create new earthen. Safeguards were implemented, and protectors were appointed.', '14', '0', '100', '0', '0', '13758', '30604', '0', 'brann SAY_EVENT_END_11_KADD');
INSERT INTO `creature_text` VALUES ('30898', '3', '0', 'Designations: Aesir and Vanir. Or in the common nomenclature, storm and earth giants. Sentinel Loken designated supreme. Dragon Aspects appointed to monitor evolution on Azeroth.', '14', '0', '100', '0', '0', '13759', '30605', '0', 'brann SAY_EVENT_END_13_KADD');
INSERT INTO `creature_text` VALUES ('30899', '0', '0', 'Warning: life form pattern not recognized. Archival processing terminated.  Continued interference will result in targeted response.', '14', '0', '100', '0', '0', '13765', '29561', '0', 'brann SAY_EVENT_INTRO_3_ABED');
INSERT INTO `creature_text` VALUES ('30899', '1', '0', 'Critical threat index. Void analysis diverted. Initiating sanitization protocol.', '14', '0', '100', '0', '0', '13767', '29577', '0', 'brann SAY_EVENT_C_2_ABED');
INSERT INTO `creature_text` VALUES ('30899', '2', '0', 'Alert: security fail-safes deactivated. Beginning memory purge and... ', '14', '0', '100', '0', '0', '13768', '29579', '0', 'brann SAY_EVENT_D_2_ABED');
INSERT INTO `creature_text` VALUES ('30899', '3', '0', 'System online. Life form pattern recognized.  Welcome, Branbronzan.  Query?', '14', '0', '100', '0', '0', '13769', '29580', '0', 'brann SAY_EVENT_D_4_ABED');
INSERT INTO `creature_text` VALUES ('30899', '4', '0', 'Accessing prehistoric data... retrieved. In the beginning the earthen were created to--', '14', '0', '100', '0', '0', '13770', '30611', '0', 'brann SAY_EVENT_END_03_ABED');
INSERT INTO `creature_text` VALUES ('30899', '5', '0', 'Accessing... In the early stages of it\'s development cycle, Azeroth suffered infection by parasitic necrophotic symbiotes.', '14', '0', '100', '0', '0', '13771', '30612', '0', 'brann SAY_EVENT_END_05_ABED');
INSERT INTO `creature_text` VALUES ('30899', '6', '0', 'Designation: Old Gods. Old Gods rendered all systems, including earthen, defenseless in order to facilitate assimilation. This matrix destabilization has been termed \"the Curse of Flesh\". Effects of destabilization increased over time.', '14', '0', '100', '0', '0', '13772', '30613', '0', 'brann SAY_EVENT_END_07_ABED');
INSERT INTO `creature_text` VALUES ('30899', '7', '0', 'Acknowledged, Branbronzan. Session terminated.', '14', '0', '100', '0', '0', '13773', '30614', '0', 'brann SAY_EVENT_END_21_ABED');
INSERT INTO `creature_text` VALUES ('29305', '5', '0', '%s begins to transform!', '41', '0', '100', '0', '0', '0', '30385', '0', 'Moorabi - Begin Transform');
INSERT INTO `creature_text` VALUES ('27655', '3', '0', 'Poor blind fools!', '14', '0', '100', '0', '0', '13638', '27412', '0', 'Mage-Lord Urom - SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('27655', '4', '0', '%s begins to cast Empowered Arcane Explosion!', '41', '0', '100', '0', '0', '0', '27903', '0', 'Mage-Lord Urom - EMOTE_ARCANE_EXPLOSION');
INSERT INTO `creature_text` VALUES ('27655', '5', '0', 'A taste... just a small taste... of the Spell-Weaver\'s power!', '14', '0', '100', '0', '0', '13639', '31829', '0', 'Mage-Lord Urom - SAY_ARCANE_EXPLOSION');
INSERT INTO `creature_text` VALUES ('27655', '5', '1', 'So much unstable energy... but worth the risk to destroy you!', '14', '0', '100', '0', '0', '13640', '31830', '0', 'Mage-Lord Urom - SAY_ARCANE_EXPLOSION');
INSERT INTO `creature_text` VALUES ('27655', '6', '0', 'Everything I have done... has been for Azeroth....', '14', '0', '100', '0', '0', '13644', '31834', '0', 'Mage-Lord Urom - SAY_DEATH');
INSERT INTO `creature_text` VALUES ('27655', '7', '0', 'If only you understood!', '14', '0', '100', '0', '0', '13641', '31831', '0', 'Mage-Lord Urom - SAY_PLAYER_KILL');
INSERT INTO `creature_text` VALUES ('27655', '7', '1', 'Now do you see? Do you?!', '14', '0', '100', '0', '0', '13642', '31832', '0', 'Mage-Lord Urom - SAY_PLAYER_KILL');
INSERT INTO `creature_text` VALUES ('27655', '7', '2', 'Unfortunate, but necessary.', '14', '0', '100', '0', '0', '13643', '31833', '0', 'Mage-Lord Urom - SAY_PLAYER_KILL');
