INSERT INTO `creature_text` VALUES ('16808', '0', '0', 'Ours is the TRUE Horde! The only Horde!', '14', '0', '100', '0', '0', '10323', '17614', '0', 'kargath SAY_AGGRO1');
INSERT INTO `creature_text` VALUES ('16808', '0', '1', 'I\'ll carve the meat from your bones!', '14', '0', '100', '0', '0', '10324', '17616', '0', 'kargath SAY_AGGRO2');
