INSERT INTO `creature_text` VALUES ('17085', '0', '0', 'Avruu\'s magic... it still controls me.  You must fight me, mortal.  It\'s the only way to break the spell!', '12', '0', '100', '0', '0', '0', '13448', '0', 'aeranas SAY_SUMMON');
