INSERT INTO `creature_text` VALUES ('17077', '0', '0', '%s lifts its head into the air, as if listening for something.', '16', '0', '100', '0', '0', '0', '13430', '0', 'ancestral wolf EMOTE_WOLF_LIFT_HEAD');
