INSERT INTO `creature_text` VALUES ('17023', '0', '0', 'Gul\'dan speaks the truth! We should return at once to tell our brothers of the news! Retreat back through the portal!', '14', '0', '100', '0', '0', '0', '19911', '0', 'ancestral wolf SAY_WOLF_WELCOME');
INSERT INTO `creature_text` VALUES ('16993', '0', '0', 'Thank you for agreeing to help. Now, let\'s get out of here, $N!', '12', '0', '100', '0', '0', '0', '13289', '0', 'wounded elf SAY_ELF_START');
INSERT INTO `creature_text` VALUES ('16993', '1', '0', 'Over there! They\'re following us!', '12', '0', '100', '0', '0', '0', '13255', '0', 'wounded elf SAY_ELF_SUMMON1');
INSERT INTO `creature_text` VALUES ('16993', '2', '0', 'Allow me a moment to rest. The journey taxes what little strength I have.', '12', '0', '100', '0', '0', '0', '13290', '0', 'wounded elf SAY_ELF_RESTING');
INSERT INTO `creature_text` VALUES ('16993', '3', '0', 'Did you hear something?', '12', '0', '100', '0', '0', '0', '27926', '0', 'wounded elf SAY_ELF_SUMMON2');
INSERT INTO `creature_text` VALUES ('16993', '4', '0', 'Falcon Watch, at last! Now, where\'s my... Oh no! My pack, it\'s missing! Where has - ', '12', '0', '100', '0', '0', '0', '13292', '0', 'wounded elf SAY_ELF_COMPLETE');
INSERT INTO `creature_text` VALUES ('16993', '5', '0', 'You won\'t keep me from getting to Falcon Watch!', '12', '0', '100', '0', '0', '0', '13659', '0', 'wounded elf SAY_ELF_AGGRO');
INSERT INTO `creature_text` VALUES ('18210', '4', '0', 'It is best that we split up now, in case they send more after us. Hopefully one of us will make it back to Garrosh. Farewell, stranger.', '12', '1', '100', '0', '0', '0', '15001', '0', 'maghar captive SAY_MAG_COMPLETE');
INSERT INTO `creature_text` VALUES ('18445', '0', '0', 'Thanks, $N! How many times have you saved my life now? Three? That makes us blood in my book!', '12', '0', '100', '0', '0', '0', '15424', '0', 'corki NPC_CORKI');
INSERT INTO `creature_text` VALUES ('20812', '0', '0', 'This is the last time I get caught! I promise! Bye!', '12', '0', '100', '0', '0', '0', '15308', '0', 'corki NPC_CORKI_2');
