INSERT INTO `creature_text` VALUES ('19720', '0', '0', 'Time to teach you a lesson in manners, little $g boy:girl;!', '12', '0', '100', '0', '0', '0', '17353', '0', '\"Dirty\" Larry');
INSERT INTO `creature_text` VALUES ('20912', '0', '0', 'It is a small matter to control the mind of the weak... for I bear allegiance to powers untouched by time, unmoved by fate. No force on this world or beyond harbors the strength to bend our knee... not even the mighty Legion! ', '14', '0', '100', '0', '0', '11122', '19112', '0', 'skyriss SAY_INTRO');
INSERT INTO `creature_text` VALUES ('20912', '1', '0', 'Bear witness to the agent of your demise!', '14', '0', '100', '0', '0', '11123', '19113', '0', 'skyriss SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('20912', '2', '0', 'Your fate is written.', '14', '0', '100', '0', '0', '11124', '19813', '0', 'skyriss SAY_KILL_1');
INSERT INTO `creature_text` VALUES ('20912', '2', '1', 'The chaos I have sown here is but a taste....', '14', '0', '100', '0', '0', '11125', '19814', '0', 'skyriss SAY_KILL_2');
INSERT INTO `creature_text` VALUES ('20912', '3', '0', 'You will do my bidding, weakling.', '14', '0', '100', '0', '0', '11127', '19816', '0', 'skyriss SAY_MIND_1');
INSERT INTO `creature_text` VALUES ('20912', '3', '1', 'Your will is no longer your own.', '14', '0', '100', '0', '0', '11128', '34363', '0', 'skyriss SAY_MIND_2');
INSERT INTO `creature_text` VALUES ('20912', '4', '0', 'Flee in terror.', '14', '0', '100', '0', '0', '11129', '19818', '0', 'skyriss SAY_FEAR_1');
INSERT INTO `creature_text` VALUES ('20912', '4', '1', 'I will show you horrors undreamed of.', '14', '0', '100', '0', '0', '11130', '19819', '0', 'skyriss SAY_FEAR_2');
INSERT INTO `creature_text` VALUES ('20912', '5', '0', 'We span the universe, as countless as the stars!', '14', '0', '100', '0', '0', '11131', '19821', '0', 'skyriss SAY_IMAGE');
INSERT INTO `creature_text` VALUES ('20912', '6', '0', 'I am merely one of... infinite multitudes.', '14', '0', '100', '0', '0', '11126', '19815', '0', 'skyriss SAY_DEATH');
