INSERT INTO `creature_text` VALUES ('18805', '5', '0', 'Enough of this!  Now I call upon the fury of the cosmos itself.', '14', '0', '100', '0', '0', '0', '20372', '0', 'solarian SAY_VOIDA');
INSERT INTO `creature_text` VALUES ('18805', '6', '0', 'I become ONE... with the VOID!', '14', '0', '100', '0', '0', '0', '20373', '0', 'solarian SAY_VOIDB');
INSERT INTO `creature_text` VALUES ('19622', '0', '0', 'Energy. Power. My people are addicted to it... a dependence made manifest after the Sunwell was destroyed. Welcome to the future. A pity you are too late to stop it. No one can stop me now! Selama ashal\'anore!', '14', '0', '100', '0', '0', '11256', '20195', '0', 'kaelthas SAY_INTRO');
INSERT INTO `creature_text` VALUES ('19622', '1', '0', 'Capernian will see to it that your stay here is a short one.', '14', '0', '100', '0', '0', '11257', '19004', '0', 'kaelthas SAY_INTRO_CAPERNIAN');
INSERT INTO `creature_text` VALUES ('19622', '2', '0', 'Well done, you have proven worthy to test your skills against my master engineer, Telonicus.', '14', '0', '100', '0', '0', '11258', '19005', '0', 'kaelthas SAY_INTRO_TELONICUS');
INSERT INTO `creature_text` VALUES ('19622', '3', '0', 'Let us see how your nerves hold up against the Darkener, Thaladred! ', '14', '0', '100', '0', '0', '11259', '19006', '0', 'kaelthas SAY_INTRO_THALADRED');
INSERT INTO `creature_text` VALUES ('19622', '4', '0', 'You have persevered against some of my best advisors... but none can withstand the might of the Blood Hammer. Behold, Lord Sanguinar!', '14', '0', '100', '0', '0', '11260', '19003', '0', 'kaelthas SAY_INTRO_SANGUINAR');
INSERT INTO `creature_text` VALUES ('19622', '5', '0', 'As you see, I have many weapons in my arsenal....', '14', '0', '100', '0', '0', '11261', '19053', '0', 'kaelthas SAY_PHASE2_WEAPON');
INSERT INTO `creature_text` VALUES ('19622', '6', '0', 'Perhaps I underestimated you. It would be unfair to make you fight all four advisors at once, but... fair treatment was never shown to my people. I\'m just returning the favor.', '14', '0', '100', '0', '0', '11262', '19060', '0', 'kaelthas SAY_PHASE3_ADVANCE');
INSERT INTO `creature_text` VALUES ('19622', '7', '0', 'Alas, sometimes one must take matters into one\'s own hands. Balamore shanal!', '14', '0', '100', '0', '0', '11263', '19195', '0', 'kaelthas SAY_PHASE4_INTRO2');
INSERT INTO `creature_text` VALUES ('19622', '8', '0', 'I have not come this far to be stopped! The future I have planned will not be jeopardized! Now you will taste true power!!', '14', '0', '100', '0', '0', '11273', '20208', '0', 'kaelthas SAY_PHASE5_NUTS');
INSERT INTO `creature_text` VALUES ('19622', '9', '0', 'You will not prevail!', '14', '0', '100', '0', '0', '11270', '20204', '0', 'kaelthas SAY_SLAY1');
INSERT INTO `creature_text` VALUES ('19622', '9', '1', 'You gambled. And lost.', '14', '0', '100', '0', '0', '11271', '20205', '0', 'kaelthas SAY_SLAY2');
