INSERT INTO `creature_text` VALUES ('15341', '0', '0', 'The time of our retribution is at hand! Let darkness reign in the hearts of our enemies!', '14', '0', '100', '0', '0', '8612', '11440', '0', 'rajaxx SAY_WAVE3');
INSERT INTO `creature_text` VALUES ('15341', '1', '0', 'No longer will we wait behind barred doors and walls of stone! No longer will our vengeance be denied! The dragons themselves will tremble before our wrath!\n', '14', '0', '100', '0', '0', '8610', '11439', '0', 'rajaxx SAY_WAVE4');
