INSERT INTO `creature_text` VALUES ('15341', '4', '0', 'Fandral! Your time has come! Go and hide in the Emerald Dream and pray we never find you!\n', '14', '0', '100', '0', '0', '8607', '11436', '0', 'rajaxx SAY_WAVE7');
