INSERT INTO `creature_text` VALUES ('16589', '0', '0', 'So there we were - me and the battalion, doing a routine recon down the bone road. We\'d sent the scout up ahead to look around in case anything dangerous come up.', '12', '1', '100', '0', '0', '0', '12617', '0', 'Guard Captain Cragtar - Speech Text 0');
INSERT INTO `creature_text` VALUES ('16589', '1', '0', 'Sure enough, scout came runnin\' back, talkin\' bout some orcs down the path. They hadn\'t seen us yet so we figured we\'d go get acquainted.', '12', '1', '100', '0', '0', '0', '12618', '0', 'Guard Captain Cragtar - Speech Text 1');
INSERT INTO `creature_text` VALUES ('16589', '2', '0', 'Big red suckers! Bigger than any orc I\'d ever seen. Muscles out to here, tusks out to here... ', '12', '1', '100', '0', '0', '0', '12625', '0', 'Guard Captain Cragtar - Speech Text 2');
INSERT INTO `creature_text` VALUES ('16589', '3', '0', '%s nods.', '16', '0', '100', '0', '0', '0', '37791', '0', 'Guard Captain Cragtar - Speech Text 3');
INSERT INTO `creature_text` VALUES ('16589', '4', '0', 'Fel orcs...', '12', '1', '100', '0', '0', '0', '12628', '0', 'Guard Captain Cragtar - Speech Text 4');
INSERT INTO `creature_text` VALUES ('16589', '5', '0', 'I dunno, grunt. I just - don\'t - know...', '12', '1', '100', '0', '0', '0', '12636', '0', 'Guard Captain Cragtar - Speech Text 5');
INSERT INTO `creature_text` VALUES ('16589', '6', '0', 'But that\'s not the worst of it. Before I could pull us back, a group of \'em spotted us and attacked.', '12', '1', '100', '0', '0', '0', '12637', '0', 'Guard Captain Cragtar - Speech Text 6');
INSERT INTO `creature_text` VALUES ('16589', '7', '0', 'Lost some of my best soldiers that day...', '12', '1', '100', '0', '0', '0', '12638', '0', 'Guard Captain Cragtar - Speech Text 7');
