INSERT INTO `creature_text` VALUES ('29579', '1', '0', 'How did you get my communicator from the Explorers\' League?', '12', '0', '100', '5', '0', '0', '30625', '0', 'Brann Bronzebeard text1');
INSERT INTO `creature_text` VALUES ('29579', '2', '0', 'If the Explorers\' League sent men, I didn\'t see any trace of them. I found your note buried in a camp overrun by iron dwarves.', '12', '1', '100', '0', '0', '0', '30622', '0', 'Player text2');
INSERT INTO `creature_text` VALUES ('29579', '3', '0', 'You have my thanks for dispatching the iron dwarves. But why would the Horde have an interest in me?', '12', '0', '100', '6', '0', '0', '30626', '0', 'Brann Bronzebeard text3');
INSERT INTO `creature_text` VALUES ('29579', '4', '0', 'A scout found the remains of your journal in Thor Modan. We\'ve been looking for you ever since.', '12', '1', '100', '0', '0', '0', '30623', '0', 'Player text4');
INSERT INTO `creature_text` VALUES ('29579', '5', '0', 'That wouldn\'t be Scout Vor\'takh, would it? Even I know of his reputation for ruthlessness. He means to abduct me, then?', '12', '0', '100', '5', '0', '0', '30627', '0', 'Brann Bronzebeard text5');
INSERT INTO `creature_text` VALUES ('29579', '6', '0', 'If you\'ve seen the journal, then you know what I\'ve been discovering. The titans\' own creations are at war with each other!', '12', '0', '100', '5', '0', '0', '30628', '0', 'Brann Bronzebeard text6');
INSERT INTO `creature_text` VALUES ('29579', '7', '0', 'Loken and his iron dwarf minions have ousted the Earthen from Ulduar and taken over the city.', '12', '0', '100', '5', '0', '0', '30629', '0', 'Brann Bronzebeard text7');
INSERT INTO `creature_text` VALUES ('29579', '8', '0', 'If we spend our time and strength fighting with each other, Loken will use Ulduar\'s resources to destroy both Horde and Alliance.', '12', '0', '100', '6', '0', '0', '30630', '0', 'Brann Bronzebeard text8');
INSERT INTO `creature_text` VALUES ('29579', '9', '0', 'Speak to the commander at your post, $g lad:lass;, and persuade him to abandon Vor\'takh\'s foolish plan.', '12', '0', '100', '6', '0', '0', '30631', '0', 'Brann Bronzebeard Text10');
