INSERT INTO `creature_text` VALUES ('26648', '9', '0', 'Very well, $N, you have failed to act. The prophecy is not yours to learn. Do not call upon me again until you have found your backbone!', '14', '0', '100', '33', '0', '11557', '25866', '0', 'Oacha\'noa');
INSERT INTO `creature_text` VALUES ('30299', '0', '0', 'You didn\'t think that I was going to let you walk in here and take the Heart of the Storm, did you?', '12', '0', '100', '6', '0', '0', '30972', '0', 'Overseer Narvir');
INSERT INTO `creature_text` VALUES ('30299', '1', '0', 'You may have killed Valduran, but that will not stop me from completing the colossus.', '12', '0', '100', '1', '0', '0', '30973', '0', 'Overseer Narvir');
INSERT INTO `creature_text` VALUES ('37226', '3', '0', 'Foolish girl. You seek that which I killed long ago. He is merely a ghost now. A faint echo in my mind... ', '14', '0', '100', '0', '0', '17229', '37617', '0', 'Lich King SAY_LK_JAINA_INTRO_END');
INSERT INTO `creature_text` VALUES ('37226', '4', '0', 'I will not make the same mistake again, Sylvanas. This time there will be no escape. You will all serve me in death!', '14', '0', '100', '0', '0', '17228', '37616', '0', 'Lich King SAY_LK_SYLVANAS_INTRO_END');
INSERT INTO `creature_text` VALUES ('36723', '0', '0', 'You are not worthy to face the Lich King!', '14', '0', '100', '0', '0', '16921', '38664', '0', 'general SAY_AGGRO');
INSERT INTO `creature_text` VALUES ('36723', '1', '0', 'Master, I have failed...', '14', '0', '100', '0', '0', '16922', '36921', '0', 'general SAY_DEATH');
INSERT INTO `creature_text` VALUES ('36954', '3', '0', 'Succumb to the chill of the grave.', '14', '0', '100', '0', '0', '17218', '37175', '0', 'Lich King SAY_LICH_KING_WALL_02');
INSERT INTO `creature_text` VALUES ('36954', '2', '0', 'There is no escape!', '14', '0', '100', '0', '0', '17217', '37177', '0', 'Lich King SAY_LICH_KING_WALL_01');
INSERT INTO `creature_text` VALUES ('36954', '1', '0', 'I will not make the same mistake again, Sylvanas. This time there will be no escape. You will all serve me in death!', '14', '0', '100', '0', '0', '17213', '37173', '0', 'Lich King SAY_LICH_KING_AGGRO_H');
INSERT INTO `creature_text` VALUES ('30824', '0', '0', 'FIRE! FIRE!', '14', '0', '100', '0', '0', '16732', '38681', '0', 'Sky-Reaver Korm Blackscar - Fire');
INSERT INTO `creature_text` VALUES ('36955', '0', '0', 'He is too powerful. We must leave this place at once! My magic can hold him in place for only a short time. Come quickly, heroes!', '14', '0', '100', '1', '0', '16644', '36821', '0', 'Jaina SAY_JAINA_AGGRO');
INSERT INTO `creature_text` VALUES ('36955', '1', '0', 'I will destroy this barrier. You must hold the undead back!', '14', '0', '100', '0', '0', '16607', '38536', '0', 'Jaina SAY_JAINA_WALL_01');
INSERT INTO `creature_text` VALUES ('36955', '2', '0', 'Another ice wall! Keep the undead from interrupting my incantations so that I may bring this wall down.', '14', '0', '100', '0', '0', '16608', '38537', '0', 'Jaina SAY_JAINA_WALL_02');
INSERT INTO `creature_text` VALUES ('36955', '3', '0', 'He is toying with us! I will show him what happens to ice when it meets fire!', '14', '0', '100', '0', '0', '16609', '0', '0', 'Jaina SAY_JAINA_WALL_03');
INSERT INTO `creature_text` VALUES ('36955', '4', '0', 'Your barriers can\'t hold us back much longer, monster! I will shatter them all!', '14', '0', '100', '0', '0', '16610', '38539', '0', 'Jaina SAY_JAINA_WALL_04');
INSERT INTO `creature_text` VALUES ('36955', '5', '0', 'There\'s an opening up ahead. GO NOW!', '14', '0', '100', '1', '0', '16645', '38551', '0', 'Jaina SAY_JAINA_ESCAPE_01');
INSERT INTO `creature_text` VALUES ('36955', '6', '0', 'We\'re almost there... Don\'t give up!', '14', '0', '100', '0', '0', '16646', '38538', '0', 'Jaina SAY_JAINA_ESCAPE_02');
INSERT INTO `creature_text` VALUES ('36955', '7', '0', 'It... It\'s a dead end. We have no choice but to fight. Steel yourselves, heroes, for this is our last stand!', '14', '0', '100', '1', '0', '16647', '36992', '0', 'Jaina SAY_JAINA_TRAP');
INSERT INTO `creature_text` VALUES ('36955', '8', '0', 'Forgive me, heroes. I should have listened to Uther. I... I just had to see for myself. To look into his eyes one last time. I am sorry.', '14', '0', '100', '1', '0', '16648', '36990', '0', 'Jaina SAY_JAINA_FINAL_2');
INSERT INTO `creature_text` VALUES ('36955', '9', '0', 'We now know what must be done. I will deliver this news to King Varian and Highlord Fordring.', '14', '0', '100', '1', '0', '16649', '36991', '0', 'Jaina SAY_JAINA_FINAL_3');
INSERT INTO `creature_text` VALUES ('37554', '0', '0', 'He\'s... too powerful. Heroes, quickly... come to me! We must leave this place at once! I will do what I can to hold him in place while we flee.', '14', '0', '100', '0', '0', '17058', '37168', '0', 'Sylvanas SAY_SYLVANA_AGGRO');
INSERT INTO `creature_text` VALUES ('37554', '1', '0', 'No wall can hold the Banshee Queen. Keep the undead at bay, heroes. I will tear this barrier down!', '14', '0', '100', '0', '0', '17029', '38540', '0', 'Sylvanas SAY_SYLVANA_WALL_01');
INSERT INTO `creature_text` VALUES ('37554', '2', '0', 'Another barrier? Stand strong, champions. I will bring the wall down.', '14', '0', '100', '0', '0', '17030', '38541', '0', 'Sylvanas SAY_SYLVANA_WALL_02');
INSERT INTO `creature_text` VALUES ('37554', '3', '0', 'I grow tired of these games, Arthas! Your walls can\'t stop me!', '14', '0', '100', '0', '0', '17031', '38542', '0', 'Sylvanas SAY_SYLVANA_WALL_03');
INSERT INTO `creature_text` VALUES ('37554', '4', '0', 'You won\'t impede our escape, fiend! Keep the undead off me while I bring this barrier down.', '14', '0', '100', '0', '0', '17032', '38543', '0', 'Sylvanas SAY_SYLVANA_WALL_04');
INSERT INTO `creature_text` VALUES ('37554', '5', '0', 'There\'s an opening up ahead. GO NOW!', '14', '0', '100', '1', '0', '17059', '38551', '0', 'Sylvanas SAY_SYLVANA_ESCAPE_01');
INSERT INTO `creature_text` VALUES ('37554', '6', '0', 'We\'re almost there... Don\'t give up!', '14', '0', '100', '0', '0', '17060', '38538', '0', 'Sylvanas SAY_SYLVANA_ESCAPE_02');
INSERT INTO `creature_text` VALUES ('37554', '7', '0', 'BLASTED DEAD END! So this is how it ends. Prepare yourselves, heroes, for today we make our final stand!', '14', '0', '100', '5', '0', '17061', '37170', '0', 'Sylvanas SAY_SYLVANA_TRAP');
INSERT INTO `creature_text` VALUES ('37554', '8', '0', 'We are safe... for now. His strength has increased tenfold since our last battle. It will take a mighty army to destroy the Lich King. An army greater than even the Horde can rouse.', '14', '0', '100', '1', '0', '17062', '37171', '0', 'Sylvanas SAY_SYLVANA_FINAL_2');
INSERT INTO `creature_text` VALUES ('36954', '7', '0', 'Minions, sieze them.  Bring their corpses back to me.', '14', '0', '100', '0', '0', '17222', '38670', '0', 'Lich King SAY_LICH_KING_ABON');
INSERT INTO `creature_text` VALUES ('36954', '4', '0', 'Another dead end.', '14', '0', '100', '0', '0', '17219', '37176', '0', 'Lich King SAY_LICH_KING_WALL_03');
INSERT INTO `creature_text` VALUES ('36954', '5', '0', 'How long can you fight it?', '14', '0', '100', '0', '0', '17220', '38668', '0', 'Lich King SAY_LICH_KING_WALL_04');
INSERT INTO `creature_text` VALUES ('29542', '0', '0', 'I will not die so quickly this time!', '14', '0', '100', '0', '0', '0', '32624', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '0', '0', 'Well done. Let\'s get this over with.', '12', '0', '100', '1', '0', '0', '30223', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '1', '0', 'Come, Landgren, cough up your soul so that I can ask you a very important question.', '12', '0', '100', '0', '0', '0', '30231', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '2', '0', 'YOU WILL TELL ME WHERE WESTWIND IS OR I WILL DESTROY YOUR SOUL!', '14', '0', '100', '25', '0', '0', '30241', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '3', '0', 'Then you leave me no choice. I won\'t say that I won\'t enjoy this.', '12', '0', '100', '1', '0', '0', '30244', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '4', '0', 'Very well. Tell me!', '12', '0', '100', '0', '0', '0', '30246', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '5', '0', 'A hidden hollow? How very interesting. You\'ve served your purpose, Landgren, but I\'m afraid there\'ll be no resurrection for you this time!', '12', '0', '100', '6', '0', '0', '30248', '0', '');
INSERT INTO `creature_text` VALUES ('29560', '6', '0', 'Now that the unpleasantness is finished with, let\'s talk about you dealing with Grand Admiral Westwind.', '12', '0', '100', '11', '0', '0', '30251', '0', '');
INSERT INTO `creature_text` VALUES ('29572', '0', '0', 'You\'ll get nothing out of me, monster. I am beyond your ability to influence.', '12', '0', '100', '0', '0', '0', '30242', '0', '');
INSERT INTO `creature_text` VALUES ('29572', '1', '0', 'No.', '12', '0', '100', '0', '0', '0', '39548', '0', '');
INSERT INTO `creature_text` VALUES ('29572', '2', '0', 'STOP! It isn\'t worth it. I\'ll tell you where he is.', '12', '0', '100', '0', '0', '0', '30245', '0', '');
INSERT INTO `creature_text` VALUES ('29572', '3', '0', 'On the south end of the island is a cave -- a hidden hollow. The grand admiral has holed himself up in there, preparing for the final battle against the Lich King.', '12', '0', '100', '0', '0', '0', '30247', '0', '');
INSERT INTO `creature_text` VALUES ('29572', '4', '0', 'AAAEEEEIIIiiiiiiiiiiiiiiiiiiiiiiiiiiii........................................', '14', '0', '100', '0', '0', '0', '30250', '0', '');
INSERT INTO `creature_text` VALUES ('29919', '1', '0', 'By order of Duke Lankral, Commander of the Knights of the Ebon Blade, Vanquisher of the Scourge and Conqueror of The Shadow Vault, you are ordered to stand down. What is your answer?', '12', '0', '100', '0', '0', '0', '30712', '0', 'Thane');
INSERT INTO `creature_text` VALUES ('29919', '2', '0', 'What is this?', '14', '0', '100', '0', '0', '0', '32584', '0', 'Thane');
INSERT INTO `creature_text` VALUES ('29919', '3', '0', 'My answer? Here\'s my answer, little messenger!', '14', '0', '100', '0', '0', '0', '30713', '0', 'Thane');
INSERT INTO `creature_text` VALUES ('29919', '4', '0', 'I will feed you to the dogs!', '14', '0', '100', '0', '0', '0', '30499', '0', 'Thane');
INSERT INTO `creature_text` VALUES ('30924', '0', '0', 'Now fight me, $N! Kill Iskalder!', '14', '0', '100', '0', '0', '1167', '31540', '0', 'Possessed Iskalder');
INSERT INTO `creature_text` VALUES ('30945', '0', '0', 'Iskalder, there you are. What is this? Engaged in battle already?', '14', '0', '100', '457', '0', '13824', '31528', '0', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('30945', '1', '0', 'NO! How is this possible?', '14', '0', '100', '457', '0', '13825', '31530', '0', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('30945', '2', '0', 'I know not how this was possible, but you must still be judged Iskalder. Wait... what is this?', '14', '0', '100', '457', '0', '13824', '31531', '0', 'Vardmadra');
INSERT INTO `creature_text` VALUES ('30945', '3', '0', 'Stay away from me creature! Do not touch me!', '14', '0', '100', '0', '0', '1168', '31533', '0', 'Vardmadra to Nightswood');
INSERT INTO `creature_text` VALUES ('30945', '4', '0', 'Ahahahahahaha! It is done. Return to my cave. We have much to discuss!', '14', '0', '100', '457', '0', '1167', '31534', '0', 'Vardmadra to ');
INSERT INTO `creature_text` VALUES ('30955', '0', '0', 'This? This is me taking control of you. This is me setting up my final revenge!', '14', '0', '100', '457', '0', '13824', '31532', '0', 'Lady Nightswood');
INSERT INTO `creature_text` VALUES ('27455', '0', '0', 'Captain, why are the guards not at their posts?', '12', '0', '100', '6', '0', '12725', '26714', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '1', '0', 'Uther had my troops recalled? Damn it! If my warriors abandon me, I\'ll never defeat Mal\'Ganis. The ships must be burned before the men reach the shore!\n', '12', '0', '100', '5', '0', '12726', '26716', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '2', '0', 'Burned down to their frames! No one goes home until our job here is done!\n', '12', '0', '100', '15', '0', '12727', '26718', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '3', '0', 'Spare me, Muradin. You weren\'t there to see what Mal\'Ganis did to my homeland.\n', '12', '0', '100', '1', '0', '12728', '26732', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '4', '0', 'Behold, Muradin, our salvation, Frostmourne.\n', '12', '0', '100', '1', '0', '12729', '27207', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '5', '0', 'I would gladly bear any curse to save my homeland.\n', '12', '0', '100', '1', '0', '12730', '27211', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '6', '0', 'Damn the men! Nothing shall prevent me from having my revenge, old friend. Not even you.\n', '12', '0', '100', '5', '0', '12731', '27214', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27455', '7', '0', 'Now, I call out to the spirits of this place. I will give anything or pay any price, if only you will help me save my people.\n', '12', '0', '100', '396', '0', '12732', '27215', '0', 'Prince Arthas');
INSERT INTO `creature_text` VALUES ('27480', '0', '0', 'Isn\'t that a bit much, lad?\n', '12', '0', '100', '6', '0', '12733', '26717', '0', 'Muradin');
INSERT INTO `creature_text` VALUES ('27480', '1', '0', 'You lied to your men and betrayed the mercenaries who fought for you. What\'s happening to you, Arthas? Is vengeance all that\'s important to you?\n', '12', '0', '100', '1', '0', '12734', '26731', '0', 'Muradin');
INSERT INTO `creature_text` VALUES ('27480', '2', '0', 'Hold, lad. There\'s an inscription on the dais. It\'s a warning. It says, \"Whomsoever takes up this blade shall wield power eternal. Just as the blade rends flesh, so must power scar the spirit.\" Oh, I should\'ve known. The blade is cursed! Let\'s get the hell out of here!\n', '12', '0', '100', '0', '0', '12735', '27209', '0', 'Muradin');
INSERT INTO `creature_text` VALUES ('27480', '3', '0', 'Leave it be, Arthas. Forget this business and lead your men home.\n', '12', '0', '100', '396', '0', '12736', '27212', '0', 'Muradin');
INSERT INTO `creature_text` VALUES ('27480', '4', '0', 'O\' my head... Wh... Where am I?\n', '12', '0', '100', '0', '0', '0', '27217', '0', 'Muradin');
INSERT INTO `creature_text` VALUES ('27480', '5', '0', 'Who... Who am I?\n', '12', '0', '100', '0', '0', '0', '27218', '0', 'Muradin');
INSERT INTO `creature_text` VALUES ('27851', '0', '0', 'The walls of Frostmourne Cavern shudder and shake.', '41', '0', '100', '0', '0', '0', '27196', '0', 'Thel\'zan Spell Dummy');
INSERT INTO `creature_text` VALUES ('21293', '0', '0', 'Hide! Hide before they see you! Back up!', '15', '0', '100', '0', '0', '0', '19180', '0', 'Borak');
INSERT INTO `creature_text` VALUES ('21293', '1', '0', 'The time to strike is at hand. Terminate Icarius.', '15', '0', '100', '0', '0', '0', '19181', '0', 'Borak');
INSERT INTO `creature_text` VALUES ('21293', '2', '0', 'You have it. Do not lose it again!', '12', '0', '100', '5', '0', '0', '19183', '0', 'Borak');
INSERT INTO `creature_text` VALUES ('21410', '0', '0', 'What is it, my lord?', '12', '0', '100', '6', '0', '0', '19172', '0', 'Zarath');
INSERT INTO `creature_text` VALUES ('21410', '1', '0', 'My lord, surely you do not expect me to leave you unattended. Lord Illidan would have my head if anything were to happen to you.', '12', '0', '100', '1', '0', '0', '19174', '0', 'Zarath');
INSERT INTO `creature_text` VALUES ('21410', '2', '0', 'As you wish, my lord.', '12', '0', '100', '66', '0', '0', '37953', '0', 'Zarath');
INSERT INTO `creature_text` VALUES ('21409', '0', '0', 'Halt!', '12', '0', '100', '1', '0', '0', '19511', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('21409', '1', '0', 'Zarath you must return to the Black Temple at once! I... I seem to have misplaced Lord Illidan\'s orders. Quickly!', '12', '0', '100', '5', '0', '0', '19173', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('21409', '2', '0', 'Zarath, I am perfectly capable of making it up this road to Eclipse Point. If we do not deliver the missive, Lord Illidan will have both of our heads! You are dismissed!', '12', '0', '100', '1', '0', '0', '19175', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('21409', '3', '0', 'Ah, sweet, sweet bloodthistle... Probably left behind by one of those filthy addicts at Eclipse Point.', '12', '0', '100', '1', '0', '0', '19177', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('21409', '4', '0', 'Their loss is most definitely my gain...', '12', '0', '100', '275', '0', '0', '19178', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('21409', '5', '0', '%s puts the bundle of bloodthistle in his robe.', '16', '0', '100', '0', '0', '0', '19179', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('21409', '6', '0', 'Ah, damn it all! Clever trap. Too bad you\'ll never live to tell the tale...', '12', '0', '100', '1', '0', '0', '19184', '0', 'Icarius');
INSERT INTO `creature_text` VALUES ('30409', '0', '0', 'You\'ve come for the doctor\'s plans! You\'ll only find death!', '12', '0', '0', '0', '0', '0', '31119', '0', 'Apprentice Osterkilgr - Say 0');
INSERT INTO `creature_text` VALUES ('30409', '1', '0', 'The doctor entrusted me with the plans to Nergeld, our flesh giant amalgamation made entirely of vargul! It will be the most powerful creation of its kind and a whole legion of them will be created to destroy your pitiful forces!', '12', '0', '0', '0', '0', '0', '31090', '0', 'Apprentice Osterkilgr - Say 1');
INSERT INTO `creature_text` VALUES ('25729', '0', '0', 'This is it, $N. The start of the end of the world. And you\'re here to see it off...', '12', '1', '100', '1', '0', '0', '24909', '0', 'Shadowstalker Getry');
INSERT INTO `creature_text` VALUES ('25729', '1', '0', 'Let\'s go...', '12', '1', '100', '0', '0', '0', '24910', '0', 'Shadowstalker Getry');
INSERT INTO `creature_text` VALUES ('25729', '2', '0', 'I... I can\'t believe it... Saurfang... I... I am honored... honored to have fought along side you, sir.', '12', '1', '100', '0', '0', '0', '24933', '0', 'Shadowstalker Getry');
INSERT INTO `creature_text` VALUES ('25729', '3', '0', 'You should return to Warsong Hold now, friend. Hellscream will surely want to hear of what just happened here... of Saurfang...', '12', '1', '100', '0', '0', '0', '24934', '0', 'Shadowstalker Getry');
INSERT INTO `creature_text` VALUES ('25618', '0', '0', 'Is this it? Is this all the mighty Horde could muster?', '12', '0', '100', '6', '0', '0', '24911', '0', 'Varidus the Flenser');
INSERT INTO `creature_text` VALUES ('25618', '1', '0', 'Pathetic.', '12', '0', '100', '11', '0', '0', '35768', '0', 'Varidus the Flenser');
INSERT INTO `creature_text` VALUES ('25618', '2', '0', 'I\'ve never understood your type. The hero...', '12', '0', '100', '1', '0', '0', '24913', '0', 'Varidus the Flenser');
INSERT INTO `creature_text` VALUES ('25618', '3', '0', 'Why won\'t you just let go? Why do you fight the inevitable?', '12', '0', '100', '6', '0', '0', '24914', '0', 'Varidus the Flenser');
INSERT INTO `creature_text` VALUES ('25618', '4', '0', 'The Lich King cannot be stopped. Accept it.', '12', '0', '100', '274', '0', '0', '24915', '0', 'Varidus the Flenser');
INSERT INTO `creature_text` VALUES ('25618', '5', '0', 'This world is coming to an end. Let. It. Burn.', '12', '0', '100', '397', '0', '0', '24916', '0', 'Varidus the Flenser');
INSERT INTO `creature_text` VALUES ('25618', '6', '0', 'Take $g him:her; away and prepare $g him:her; for reanimation.', '12', '0', '100', '1', '0', '0', '24917', '0', 'Varidus the Flenser');
INSERT INTO `creature_text` VALUES ('25618', '7', '0', 'What\'s this now?', '12', '0', '100', '1', '0', '0', '24921', '0', 'Varidus the Flenser');
INSERT INTO `creature_text` VALUES ('25618', '8', '0', 'Then you are a fool.', '12', '0', '100', '25', '0', '0', '24926', '0', 'Varidus the Flenser');
INSERT INTO `creature_text` VALUES ('25618', '9', '0', 'You? A lone orc? Against me and...', '12', '0', '100', '397', '0', '0', '24928', '0', 'Varidus the Flenser');
INSERT INTO `creature_text` VALUES ('25618', '10', '0', 'RISE!', '14', '0', '100', '0', '0', '0', '31198', '0', 'Varidus the Flenser');
INSERT INTO `creature_text` VALUES ('25618', '11', '0', 'I will reanimate them all as servants of the Lich King! Bring them here...', '14', '0', '100', '0', '0', '0', '24836', '0', 'Varidus the Flenser');
INSERT INTO `creature_text` VALUES ('25751', '0', '0', 'I\'ll rip your shriveled heart out with my bare hands before any harm comes to $N, necromancer.', '12', '1', '100', '5', '0', '0', '24918', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25751', '1', '0', 'You were never alone, $N.', '12', '1', '100', '1', '0', '0', '24919', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25751', '2', '0', 'This world that you seek to destroy is our home.', '12', '1', '100', '1', '0', '0', '24920', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25751', '3', '0', 'We will fight you with every fiber of our being - until we are nothing more than dust and debris. We will fight until the end.', '12', '1', '100', '1', '0', '0', '24924', '0', 'High Overlord Saurfang');
