INSERT INTO `creature_text` VALUES ('35116', '2', '0', 'See to it that I\'m not followed.', '12', '0', '0', '1', '0', '0', '35359', '0', '');
INSERT INTO `creature_text` VALUES ('35127', '0', '0', 'Gladly.', '12', '0', '1', '0', '0', '0', '35360', '0', '');
INSERT INTO `creature_text` VALUES ('14823', '0', '0', 'Everyone enjoying themselves so far?  That\'s great!  Welcome to the Darkmoon Faire, the greatest show on all of Azeroth!  Make sure that you speak with Yebb and his friends here while you\'re taking in Neblegear\'s Darkmoon Zoo Bizarre.', '12', '0', '100', '0', '0', '0', '10153', '0', 'Silas Darkmoon');
INSERT INTO `creature_text` VALUES ('14823', '0', '1', 'We\'re back and better than ever!  It\'s the Darkmoon Faire friend, and it\'s your lucky day!  Sparing no expense, we\'ve gathered wonders and treats from around the world for your delight.  Whether you\'re young or old, rich or poor, the Darkmoon Faire has it all!  Be sure to turn in your Darkmoon Faire Prize Tickets to Gelvas Grimegate, and choose from several exotic and wondrous prizes!', '12', '0', '100', '0', '0', '0', '10160', '0', 'Silas Darkmoon');
INSERT INTO `creature_text` VALUES ('14823', '0', '2', 'Come one, come all.  Welcome to the Darkmoon Faire!  Don\'t be shy.  Step right up to Lhara and buy yourself an exotic artifact from far off lands.  If you\'re one of the lucky few who have found Darkmoon Cards, have a word with Professor Paleo.  Hungry?  Thirsty?  You\'re in luck!  Refreshments are available right here from Sylannia and Stamp.  I recommend the Darkmoon Special Reserve to wash down some Red Hot Wings.  And if the future is what you seek, then run, don\'t walk, to speak with Sayge.', '12', '0', '100', '0', '0', '0', '10154', '0', 'Silas Darkmoon');
INSERT INTO `creature_text` VALUES ('14823', '0', '3', 'Welcome one and all to the Darkmoon Faire, the greatest event in all the world!  We have it all... delicious food, strong drink, exotic artifacts, fortunes read, amazing prizes and excitement without end!  Don\'t forget to turn in your Darkmoon Faire Prize Tickets to Gelvas Grimegate!  All it takes is five or more and you\'re on your way to the most wondrous prizes on all of Azeroth.  Everybody is a winner!', '12', '0', '100', '0', '0', '0', '10155', '0', 'Silas Darkmoon');
INSERT INTO `creature_text` VALUES ('8256', '0', '0', 'Poor Dorius. If I ever get my hands on those Dark Irons, so help me...', '12', '0', '100', '0', '0', '0', '4287', '0', 'Curator Thorius');
INSERT INTO `creature_text` VALUES ('9082', '0', '0', 'Well done, soldiers! At ease!', '12', '1', '100', '0', '0', '0', '4692', '0', 'Thal\'trak Proudtusk');
INSERT INTO `creature_text` VALUES ('23567', '0', '0', 'Mysterious hoofprints, scorched shields, stray badges... how do we make sense of all this?', '12', '0', '100', '6', '0', '0', '22038', '0', 'Inspector Tarem');
INSERT INTO `creature_text` VALUES ('23573', '0', '0', 'I don\'t think Drazzit\'s going to have any luck finding anyone to kill this raptor. I mean, he\'s already raised the reward twice...', '12', '0', '100', '1', '0', '0', '22379', '0', 'Krixil Slogswitch');
INSERT INTO `creature_text` VALUES ('23995', '0', '0', 'I thought we talked about this, Brogg. You can\'t stay in this building. Sure, it can house you, but there\'s no room for anyone else!', '12', '0', '100', '1', '0', '0', '22588', '0', 'Axle');
INSERT INTO `creature_text` VALUES ('23995', '1', '0', 'Yes, we said you can stay here, but this building is our headquarters! All of our manuals and blueprints are stored here!', '12', '0', '100', '1', '0', '0', '22590', '0', 'Axle');
INSERT INTO `creature_text` VALUES ('23995', '2', '0', 'I\'m inclined to say they made ogres too big! I can see I\'m not going to convince you to move now, but don\'t think this is permanent!', '12', '0', '100', '1', '0', '0', '22592', '0', 'Axle');
INSERT INTO `creature_text` VALUES ('23579', '0', '0', 'You said Brogg could stay here! You promised Brogg you\'d help regain Stonemaul clan mound!', '12', '0', '100', '1', '0', '0', '22589', '0', 'Brogg');
INSERT INTO `creature_text` VALUES ('23579', '1', '0', 'But this the only building that fit Brogg! Goblins make buildings too small!', '12', '0', '100', '1', '0', '0', '22591', '0', 'Brogg');
INSERT INTO `creature_text` VALUES ('10799', '0', '0', '%s cries.', '16', '0', '100', '0', '0', '0', '29772', '0', 'Warosh');
INSERT INTO `creature_text` VALUES ('10799', '0', '1', '%s snorts.', '16', '0', '100', '0', '0', '0', '14502', '0', 'Warosh');
INSERT INTO `creature_text` VALUES ('10799', '0', '2', '%s grumbles.', '16', '1', '100', '0', '0', '0', '25783', '0', 'Warosh');
INSERT INTO `creature_text` VALUES ('10220', '0', '0', '%s lets loose a guttural growl as her body collapses. A horrifying howl can be heard echoing through the halls of Blackrock Spire. Something is very, very angry.', '16', '0', '100', '0', '0', '0', '5548', '0', 'Halycon DEATH');
INSERT INTO `creature_text` VALUES ('10584', '0', '0', 'You have summoned me, fool!  Now DIE!', '14', '0', '100', '0', '0', '0', '5832', '0', 'Urok Doomhowl SUMMON');
INSERT INTO `creature_text` VALUES ('10584', '1', '0', 'Me smash! You die!', '12', '0', '100', '0', '0', '0', '1926', '0', 'Urok Doomhowl AGGRO');
INSERT INTO `creature_text` VALUES ('23089', '0', '0', 'Be wary, friends. The Betrayer meditates in the court just beyond.', '14', '0', '100', '0', '0', '11388', '20902', '0', 'Akama SAY_AKAMA_BEWARE');
INSERT INTO `creature_text` VALUES ('23089', '1', '0', 'I will deal with these mongrels! Strike now, friends! Strike at the Betrayer!', '14', '0', '100', '0', '0', '11390', '21250', '0', 'Akama SAY_AKAMA_LEAVE');
INSERT INTO `creature_text` VALUES ('22917', '0', '0', 'Come, my minions. Deal with this traitor as he deserves!', '14', '0', '100', '0', '0', '11465', '21251', '0', 'Illidan SAY_ILLIDAN_MINION');
INSERT INTO `creature_text` VALUES ('22917', '1', '0', 'Who shall be next to taste my blades?', '14', '0', '100', '0', '0', '11473', '21499', '0', 'Illidan SAY_ILLIDAN_KILL');
INSERT INTO `creature_text` VALUES ('22917', '1', '1', 'This is too easy!', '14', '0', '100', '0', '0', '11472', '21498', '0', 'Illidan SAY_ILLIDAN_KILL');
INSERT INTO `creature_text` VALUES ('22917', '2', '0', 'I will not be touched by rabble such as you!', '14', '0', '100', '0', '0', '11479', '21252', '0', 'Illidan SAY_ILLIDAN_TAKEOFF');
INSERT INTO `creature_text` VALUES ('22917', '3', '0', 'Behold the Flames of Azzinoth!', '14', '0', '100', '0', '0', '11480', '21275', '0', 'Illidan SAY_ILLIDAN_SUMMONFLAMES');
INSERT INTO `creature_text` VALUES ('22917', '4', '0', 'Stare into the eyes of the Betrayer!', '14', '0', '100', '0', '0', '11481', '21497', '0', 'Illidan SAY_ILLIDAN_EYE_BLAST');
INSERT INTO `creature_text` VALUES ('22917', '5', '0', 'Behold the power... of the demon within!', '14', '0', '100', '0', '0', '11475', '21066', '0', 'Illidan SAY_ILLIDAN_MORPH');
INSERT INTO `creature_text` VALUES ('22917', '6', '0', 'You\'ve wasted too much time, mortals. Now you shall fall.', '14', '0', '100', '0', '0', '11474', '22036', '0', 'Illidan SAY_ILLIDAN_ENRAGE');
INSERT INTO `creature_text` VALUES ('22917', '7', '0', 'I can feel your hatred.', '14', '0', '100', '0', '0', '11467', '0', '0', 'Illidan SAY_ILLIDAN_TAUNT');
INSERT INTO `creature_text` VALUES ('22917', '7', '1', 'Give in to your fear!', '14', '0', '100', '0', '0', '11468', '0', '0', 'Illidan SAY_ILLIDAN_TAUNT');
INSERT INTO `creature_text` VALUES ('22917', '7', '2', 'You know nothing of power!', '14', '0', '100', '0', '0', '11469', '21500', '0', 'Illidan SAY_ILLIDAN_TAUNT');
INSERT INTO `creature_text` VALUES ('22917', '7', '3', 'Such... arrogance!', '14', '0', '100', '0', '0', '11471', '0', '0', 'Illidan SAY_ILLIDAN_TAUNT');
INSERT INTO `creature_text` VALUES ('22997', '0', '0', '%s sets eyes on $N!', '16', '0', '100', '0', '0', '0', '11074', '0', 'Flame of Azzinoth EMOTE_AZZINOTH_GAZE');
INSERT INTO `creature_text` VALUES ('23197', '0', '0', 'That is for Naisha!', '14', '0', '100', '0', '0', '11493', '21489', '0', 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_TAUNT');
INSERT INTO `creature_text` VALUES ('23197', '0', '1', 'Bleed as I have bled!', '14', '0', '100', '0', '0', '11494', '0', '0', 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_TAUNT');
INSERT INTO `creature_text` VALUES ('23197', '0', '2', 'There shall be no prison for you this time!', '14', '0', '100', '0', '0', '11495', '22208', '0', 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_TAUNT');
INSERT INTO `creature_text` VALUES ('23197', '0', '3', 'Meet your end, demon!', '14', '0', '100', '0', '0', '11500', '0', '0', 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_TAUNT');
INSERT INTO `creature_text` VALUES ('17807', '3', '0', 'Show $N the meaning of pain, Sunstriker!', '14', '0', '100', '0', '7000', '0', '14463', '0', 'Master Kelerun Bloodmourn <Blood Knight>');
INSERT INTO `creature_text` VALUES ('17807', '4', '0', 'Well done, $N! You have passed the second trial!', '14', '0', '100', '0', '7000', '0', '14464', '0', 'Master Kelerun Bloodmourn <Blood Knight>');
INSERT INTO `creature_text` VALUES ('30124', '0', '0', 'Oh, hello, little $R.', '12', '0', '100', '15', '0', '0', '31148', '0', 'Snorri text');
INSERT INTO `creature_text` VALUES ('30219', '0', '0', 'The worg sprints off in search of enemies.', '16', '0', '100', '0', '0', '1108', '30909', '0', 'Ethereal Frostworg');
INSERT INTO `creature_text` VALUES ('30219', '1', '0', 'The worg growls as it closes in on its prey.', '16', '0', '100', '0', '0', '0', '30914', '0', 'Ethereal Frostworg');
INSERT INTO `creature_text` VALUES ('30219', '2', '0', 'The frostworg howls, exposing its prey!', '16', '0', '100', '53', '0', '9036', '30915', '0', 'Ethereal Frostworg');
INSERT INTO `creature_text` VALUES ('23191', '0', '0', 'Broken of the Ashtongue tribe, your leader speaks!', '14', '0', '100', '0', '0', '0', '21342', '0', 'Akama SAY_BROKEN_FREE_0');
INSERT INTO `creature_text` VALUES ('23191', '1', '0', 'The Betrayer no longer holds sway over us.  His dark magic over the Ashtongue soul has been destroyed!', '14', '0', '100', '0', '0', '0', '21343', '0', 'Akama SAY_BROKEN_FREE_1');
INSERT INTO `creature_text` VALUES ('23191', '2', '0', 'Come out from the shadows!  I\'ve returned to lead you against our true enemy!  Shed your chains and raise your weapons against your Illidari masters!', '14', '0', '100', '0', '0', '0', '21344', '0', 'Akama SAY_BROKEN_FREE_2');
INSERT INTO `creature_text` VALUES ('31301', '0', '0', 'You dare to think you have thwarted my will? That you have broken my plans?', '12', '0', '100', '25', '0', '0', '31973', '0', 'LK SAY 1');
INSERT INTO `creature_text` VALUES ('31301', '1', '0', 'You have accomplished nothing!', '12', '0', '100', '22', '0', '0', '31974', '0', 'LK SAY 2');
INSERT INTO `creature_text` VALUES ('31301', '2', '0', 'Your feeble resistance has only hastened your doom.', '12', '0', '100', '25', '0', '0', '31976', '0', 'LK SAY 3');
INSERT INTO `creature_text` VALUES ('31301', '3', '0', 'So spout your platitudes and flail until you are spent. In the end, you will be mine, and Morbidus will be the harbinger of your death!', '12', '0', '100', '22', '0', '0', '31979', '0', 'LK SAY 4');
INSERT INTO `creature_text` VALUES ('31301', '4', '0', 'Morbidus, show them what happens to those who defy the will of their king!', '12', '0', '100', '25', '0', '0', '31981', '0', 'LK SAY 5');
INSERT INTO `creature_text` VALUES ('31428', '0', '0', 'You will pay for what you\'ve done! For the Argent Crusade!', '12', '0', '100', '0', '0', '0', '32198', '0', 'OLAKIN SAY 1');
INSERT INTO `creature_text` VALUES ('31306', '0', '0', 'Knights of the Ebon Blade, prepare for battle!', '12', '0', '100', '1', '0', '0', '32015', '0', 'DHAKAR SAY 1');
INSERT INTO `creature_text` VALUES ('32149', '0', '0', 'It was awful... I dreamt I was fighting against my friends.', '12', '0', '100', '1', '0', '0', '35293', '0', 'Fallen Hero\'s Spirit');
INSERT INTO `creature_text` VALUES ('32149', '0', '1', 'I\'m so tired. Just let me rest for a moment.', '12', '0', '100', '1', '0', '0', '35295', '0', 'Fallen Hero\'s Spirit');
INSERT INTO `creature_text` VALUES ('32149', '0', '2', 'I can\'t hear the screams anymore. Is this the end?', '12', '0', '100', '1', '0', '0', '35294', '0', 'Fallen Hero\'s Spirit');
INSERT INTO `creature_text` VALUES ('32149', '0', '3', 'At last... now I can rest.', '12', '0', '100', '1', '0', '0', '35291', '0', 'Fallen Hero\'s Spirit');
INSERT INTO `creature_text` VALUES ('32149', '0', '4', 'My nightmare, is it finally over?', '12', '0', '100', '1', '0', '0', '35292', '0', 'Fallen Hero\'s Spirit');
INSERT INTO `creature_text` VALUES ('11832', '0', '0', 'We will locate the origin of the Nightmare through the fragments you collected, $N. From there we will pull Eranikus through a rift in the Dream. Steel yourself, $C. We are inviting the embodiment of the Nightmare into our world.', '12', '0', '100', '0', '0', '0', '11282', '0', 'Keeper Remulos Say 0');
INSERT INTO `creature_text` VALUES ('11832', '1', '1', 'The rift will be opened there, above Lake Elun\'ara. Prepare yourself, $N. Eranikus\'s entry into our world will be wrought with chaos and strife.\n', '12', '0', '100', '0', '0', '0', '11290', '0', 'Keeper Remulos Say 1');
INSERT INTO `creature_text` VALUES ('11832', '2', '2', 'He will stop at nothing to get to Malfurion\'s physical manifestation. That must not happen... We must keep the beast occupied long enough for Tyrande to arrive.', '12', '0', '100', '0', '0', '0', '11291', '0', 'Keeper Remulos Say 2');
INSERT INTO `creature_text` VALUES ('11832', '3', '3', 'Defend Nighthaven, hero...', '12', '0', '100', '0', '0', '0', '11292', '0', 'Keeper Remulos Say 3');
INSERT INTO `creature_text` VALUES ('11832', '4', '4', 'Fiend! Face the might of Cenarius!', '14', '0', '100', '0', '0', '0', '11293', '0', 'Keeper Remulos Say 4');
INSERT INTO `creature_text` VALUES ('11832', '5', '5', 'Who is the predictable one, beast? Surely you did not think that we would summon you on top of Malfurion? Your redemption comes, Eranikus. You will be cleansed of this madness - this corruption.\n', '14', '0', '100', '0', '0', '0', '11295', '0', 'Keeper Remulos Say 4');
INSERT INTO `creature_text` VALUES ('11832', '6', '6', 'Hurry, $N! We must find protective cover!', '12', '0', '100', '0', '0', '0', '11300', '0', 'Keeper Remulos Say 6');
INSERT INTO `creature_text` VALUES ('11832', '7', '7', 'Please, champion, protect our people.', '12', '0', '100', '0', '0', '0', '11301', '0', 'Keeper Remulos Say 7');
INSERT INTO `creature_text` VALUES ('11832', '8', '8', 'It will be done, Eranikus. Be well, ancient one.', '12', '0', '100', '0', '0', '0', '11303', '0', 'Keeper Remulos Say 8');
INSERT INTO `creature_text` VALUES ('11832', '9', '9', 'Let us leave Nighthaven, hero. Seek me out at the grove.', '12', '0', '100', '0', '0', '0', '11329', '0', 'Keeper Remulos Say 9');
INSERT INTO `creature_text` VALUES ('11832', '10', '10', 'Come, $N. The lake is just around the bend.', '12', '0', '100', '1', '0', '0', '10866', '0', 'Keeper Remulos - say Text 0');
INSERT INTO `creature_text` VALUES ('11832', '11', '11', 'Stand near me, $N. I will protect you should anything go wrong.', '12', '0', '100', '1', '0', '0', '10867', '0', 'Keeper Remulos - say Text 1');
INSERT INTO `creature_text` VALUES ('11832', '12', '12', 'Malfurion!', '12', '0', '100', '5', '0', '0', '10868', '0', 'Keeper Remulos - say Text 2');
INSERT INTO `creature_text` VALUES ('11832', '13', '13', 'It was shrouded in nightmares, Malfurion. What is happening in the Dream? What could cause such atrocities?', '12', '0', '100', '6', '0', '0', '10870', '0', 'Keeper Remulos - say Text 3');
INSERT INTO `creature_text` VALUES ('11832', '14', '14', 'I sensed as much, Malfurion. Dark days loom ahead.', '12', '0', '100', '1', '0', '0', '10872', '0', 'Keeper Remulos - say Text 4');
INSERT INTO `creature_text` VALUES ('11832', '15', '15', 'You have been gone too long, Malfurion. Peace between the children of Azeroth is tenuous at best. What of my father? Of your brother? Have you any news?\n', '12', '0', '100', '6', '0', '0', '10874', '0', 'Keeper Remulos - say Text 5');
INSERT INTO `creature_text` VALUES ('11832', '16', '16', 'Farewell, old friend... Farewell...', '12', '0', '100', '1', '0', '0', '10877', '0', 'Keeper Remulos - say Text 6');
INSERT INTO `creature_text` VALUES ('11832', '17', '17', 'Let us return to the grove, mortal.', '12', '0', '100', '1', '0', '0', '10879', '0', 'Keeper Remulos - say Text 7');
INSERT INTO `creature_text` VALUES ('15491', '0', '0', 'Pitiful, predictable mortals... You know not what you have done! The Master\'s will fulfilled. The Moonglade shall be destroyed and Malfurion along with it!', '14', '0', '100', '0', '0', '0', '11030', '0', 'Eranikus Say 0');
INSERT INTO `creature_text` VALUES ('15491', '1', '1', '%s lets loose a sinister laugh.', '16', '0', '100', '0', '0', '0', '11296', '0', 'Eranikus Say 1');
INSERT INTO `creature_text` VALUES ('15491', '2', '2', 'You are certainly not your father, insect. Should it interest me, I would crush you with but a swipe of my claws. Turn Shan\'do Stormrage over to me and your pitiful life will be spared along with the lives of your people.', '14', '0', '100', '0', '0', '0', '11294', '0', 'Eranikus Say 2');
INSERT INTO `creature_text` VALUES ('15491', '3', '3', 'My redemption? You are bold, little one. My redemption comes by the will of my god.', '14', '0', '100', '0', '0', '0', '11297', '0', 'Eranikus Say 3');
INSERT INTO `creature_text` VALUES ('15491', '4', '4', '%s roars furiously.', '16', '0', '100', '0', '0', '0', '11298', '0', 'Eranikus Say 4');
INSERT INTO `creature_text` VALUES ('15491', '5', '5', 'Rise, servants of the Nightmare! Rise and destroy this world! Let there be no survivors...', '14', '0', '100', '0', '0', '0', '11299', '0', 'Eranikus Say 5');
INSERT INTO `creature_text` VALUES ('15491', '6', '6', 'Where is your savior? How long can you hold out against my attacks? ', '14', '0', '100', '0', '0', '0', '11304', '0', 'Eranikus Say 6');
INSERT INTO `creature_text` VALUES ('15491', '7', '7', 'Remulos, look how easy they fall before me? You can stop this, fool. Turn the druid over to me and it will all be over...', '14', '0', '100', '0', '0', '0', '11306', '0', 'Eranikus Say 7');
INSERT INTO `creature_text` VALUES ('15491', '8', '8', 'Defeated my minions? Then face me, mortals!', '14', '0', '100', '0', '0', '0', '11305', '0', 'Eranikus Say 8');
INSERT INTO `creature_text` VALUES ('15491', '9', '9', 'IT BURNS! THE PAIN... SEARING...', '14', '0', '100', '0', '0', '0', '11314', '0', 'Eranikus Say 9');
INSERT INTO `creature_text` VALUES ('15491', '10', '10', 'WHY? Why did this happen to ... to me? Where were you Tyrande? Where were you when I fell from the grace of Elune?', '14', '0', '100', '0', '0', '0', '11315', '0', 'Eranikus Say 10');
INSERT INTO `creature_text` VALUES ('15491', '11', '11', 'I... I feel... I feel the touch of Elune upon my being once more... She smiles upon me... Yes... I...', '14', '0', '100', '0', '0', '0', '11316', '0', 'Eranikus Say 11');
INSERT INTO `creature_text` VALUES ('15491', '12', '12', '%s falls to one knee.', '16', '0', '100', '0', '0', '0', '11319', '0', 'Eranikus Say 12');
INSERT INTO `creature_text` VALUES ('15633', '0', '0', 'Seek absolution, Eranikus. All will be forgiven...\n', '14', '0', '100', '0', '0', '0', '11310', '0', 'Tyrande Say 0');
INSERT INTO `creature_text` VALUES ('15633', '1', '1', 'You will be forgiven, Eranikus. Elune will always love you. Break free of the bonds that command you!', '14', '0', '100', '0', '0', '0', '11311', '0', 'Tyrande Say 0');
INSERT INTO `creature_text` VALUES ('15633', '2', '2', 'The grasp of the Old Gods is unmoving. He is consumed by their dark thoughts... I... I cannot... cannot channel much longer... Elune aid me...', '12', '0', '100', '0', '0', '0', '11312', '0', 'Tyrande Say 0');
INSERT INTO `creature_text` VALUES ('15628', '0', '0', 'For so long, I was lost... The Nightmare\'s corruption had consumed me... And now, you... all of you... you have saved me. Released me from its grasp.', '12', '0', '100', '0', '0', '0', '11323', '0', 'Eranikus the Redeemed Say 0');
INSERT INTO `creature_text` VALUES ('15628', '1', '1', 'But... Malfurion, Cenarius, Ysera... they still fight. They need me. I must return to the Dream at once.', '12', '0', '100', '0', '0', '0', '11324', '0', 'Eranikus the Redeemed Say 0');
INSERT INTO `creature_text` VALUES ('15628', '2', '2', 'My lady, I am unworthy of your prayer. Truly, you are an angel of light. Please, assist me in returning to the barrow den so that I may return to the Dream. I, like Malfurion, also have a love awaiting me... I must return to her... to protect her...', '12', '0', '100', '0', '0', '0', '11326', '0', 'Eranikus the Redeemed Say 0');
INSERT INTO `creature_text` VALUES ('15628', '3', '3', 'And heroes... I hold that which you seek. May it once more see the evil dissolved. Remulos, see to it that our champion receives the shard of the Green Flight.', '12', '0', '100', '0', '0', '0', '11327', '0', 'Eranikus the Redeemed Say 0');
INSERT INTO `creature_text` VALUES ('15629', '0', '0', '%s drinks in the suffering of the fallen.', '16', '0', '100', '0', '0', '0', '11289', '0', 'Nightmare Phantasm Say 0');
INSERT INTO `creature_text` VALUES ('17949', '0', '0', 'Remulos, old friend. It is good to see you once more. I knew that the message would find its way to you - one way or another.', '12', '0', '100', '1', '0', '0', '10869', '0', 'Malfurion Stormrage - say Text 0');
INSERT INTO `creature_text` VALUES ('17949', '1', '0', 'I fear for the worst, old friend. Within the Dream we fight a new foe, born of an ancient evil. Ysera\'s noble brood has fallen victim to the old whisperings. It seems as if the Nightmare has broken through the realm and seeks a new host on Azeroth.', '12', '0', '100', '1', '0', '0', '10871', '0', 'Malfurion Stormrage - say Text 1');
INSERT INTO `creature_text` VALUES ('17949', '2', '0', 'Aye, Remulos. Prepare the mortal races.', '12', '0', '100', '1', '0', '0', '10873', '0', 'Malfurion Stormrage - say Text 2');
INSERT INTO `creature_text` VALUES ('17949', '3', '0', 'Cenarius fights at my side. Illidan sits atop his throne in the Outland - brooding. I am afraid that the loss to Arthas proved to be his breaking point. Madness has embraced him, Remulos. He replays the events in his mind a thousand times per day, but in his mind, he is the victor and Arthas is utterly defeated. He is too far gone, old friend. I fear that the time may soon come that our bond is tested and it will not be as it was at the Well in Zin-Azshari. ', '12', '0', '100', '1', '0', '0', '10876', '0', 'Malfurion Stormrage - say Text 3');
INSERT INTO `creature_text` VALUES ('17949', '4', '0', 'Remulos, I am being drawn back... Tyrande... send her my love... Tell her I am safe. Tell her... Tell her I will return... Farewell...', '12', '0', '100', '1', '0', '0', '10878', '0', 'Malfurion Stormrage - say Text 4');
INSERT INTO `creature_text` VALUES ('7271', '1', '1', 'Sands consume you!', '14', '14', '100', '0', '0', '5872', '6221', '0', 'Witch Doctor Zum\'rah');
INSERT INTO `creature_text` VALUES ('7271', '0', '0', 'How dare you enter my sanctum!', '12', '0', '100', '0', '0', '0', '3622', '0', 'Witch Doctor Zum\'rah');
INSERT INTO `creature_text` VALUES ('26786', '0', '1', 'No one will keep us from our destiny!', '12', '0', '100', '0', '0', '0', '25983', '0', 'Iron Rune Avenger');
INSERT INTO `creature_text` VALUES ('26786', '0', '0', 'For the glory of Loken!', '12', '0', '100', '0', '0', '0', '25984', '0', 'Iron Rune Avenger');
INSERT INTO `creature_text` VALUES ('16042', '0', '0', '%s goes into a frenzy!', '16', '0', '100', '0', '0', '0', '38630', '0', 'Lord Valthalak - Cast Frenzy');
INSERT INTO `creature_text` VALUES ('26631', '5', '0', '%s calls for assistance!', '41', '0', '100', '0', '0', '0', '29588', '0', 'Novos the Summoner - EMOTE_SUMMONING_ADDS');
INSERT INTO `creature_text` VALUES ('23035', '0', '0', 'Awaken, my children and assist your master!', '14', '0', '100', '0', '0', '0', '20991', '0', 'Anzu - Summon Brood');
INSERT INTO `creature_text` VALUES ('23035', '1', '0', 'Your spells... ke-kaw... are weak magics... easy to turn against you...', '15', '0', '100', '0', '0', '0', '21017', '0', 'Anzu - Spell Bomb');
INSERT INTO `creature_text` VALUES ('23035', '1', '1', 'Yes... cast your precious little spells, ak-a-ak!', '15', '0', '100', '0', '0', '0', '21012', '0', 'Anzu - Spell Bomb');
INSERT INTO `creature_text` VALUES ('27199', '2', '0', 'How dare you summon me without an offering!', '41', '0', '0', '0', '0', '0', '26098', '0', 'Warlord Jinarrak - Say Text');
INSERT INTO `creature_text` VALUES ('27719', '0', '0', 'Ladies and gentlemen!  Well... let\'s just say you don\'t want to miss this one!', '14', '0', '100', '0', '0', '0', '27032', '0', 'Grennix Shivwiggle - Quest Accepted');
INSERT INTO `creature_text` VALUES ('27727', '0', '0', 'You went behind my back.  You neglected my orders.  You will pay the price.', '12', '0', '100', '0', '0', '0', '27030', '0', 'Conqueror Krenna - Say 0');
INSERT INTO `creature_text` VALUES ('27727', '1', '1', 'Enough talk.  By the time I\'m through with you, not even the worgs are going to want to go near your corpse.', '12', '0', '100', '0', '0', '0', '27031', '0', 'Conqueror Krenna - Say 1');
INSERT INTO `creature_text` VALUES ('27726', '0', '0', 'I used good judgment when you showed none.  I don\'t want to see you lead us into ruin, Krenna!', '12', '0', '100', '0', '0', '0', '27027', '0', 'Gorgonna - Say 0');
INSERT INTO `creature_text` VALUES ('27726', '1', '1', 'It doesn\'t have to end this way, you know?', '12', '0', '100', '0', '0', '0', '27026', '0', 'Gorgonna - Say 1');
INSERT INTO `creature_text` VALUES ('27726', '2', '2', 'We\'ll see about that.', '12', '0', '100', '0', '0', '0', '27028', '0', 'Gorgonna - Say 2');
INSERT INTO `creature_text` VALUES ('27726', '3', '3', 'Why... why sister?  Why did it have to come to this?', '12', '0', '100', '0', '0', '0', '27033', '0', 'Gorgonna - Say 3');
INSERT INTO `creature_text` VALUES ('23162', '0', '0', 'Furious winds envelop Vakkiz as he writhes menacingly towards you.', '16', '0', '100', '0', '0', '0', '21905', '0', 'Vakkiz the Windrager');
INSERT INTO `creature_text` VALUES ('23162', '1', '0', '%s\'s flesh shatters in an unearthly rage!', '16', '0', '100', '0', '0', '0', '21075', '0', 'Vakkiz the Windrager');
INSERT INTO `creature_text` VALUES ('30292', '0', '0', 'This bear\'s flank is frozen solid.... try again.', '42', '0', '0', '0', '0', '0', '30986', '0', '');
INSERT INTO `creature_text` VALUES ('28160', '0', '0', 'I\'ll send you on my flying machine. Keep your eyes peeled for clues about what might\'ve happened to Hemet!', '12', '0', '100', '25', '0', '0', '27620', '0', 'Archmage Pentarus');
INSERT INTO `creature_text` VALUES ('7271', '2', '2', 'Sands consume you!', '14', '14', '100', '0', '0', '5872', '6221', '0', 'Witch Doctor Zum\'rah');
INSERT INTO `creature_text` VALUES ('7271', '2', '3', 'Fall!', '14', '14', '100', '0', '0', '5873', '6222', '0', 'Witch Doctor Zum\'rah');
INSERT INTO `creature_text` VALUES ('26660', '0', '0', 'About time!  Get that lumber and get moving.  The horde won\'t be up there fighting forever, there\'s money to be made!', '12', '0', '100', '25', '1000', '0', '26079', '0', 'Xink');
INSERT INTO `creature_text` VALUES ('27061', '0', '0', 'Xink\'s Shredder ready and available for woodland destruction.', '16', '0', '100', '0', '0', '0', '26166', '0', 'Xink\'s Shredder');
INSERT INTO `creature_text` VALUES ('27061', '1', '0', 'Xink\'s Shredder security features activated.  Returning to base.', '16', '0', '100', '0', '0', '0', '26167', '0', 'Xink\'s Shredder');
INSERT INTO `creature_text` VALUES ('22458', '0', '0', 'Aright, listen up! Form a circle around me and move out!', '12', '0', '100', '0', '0', '0', '20314', '0', '');
INSERT INTO `creature_text` VALUES ('22458', '1', '0', 'Aright, $R, just keep us safe from harm while we work. We\'ll pay you when we return.', '12', '0', '100', '0', '0', '0', '20328', '0', '');
INSERT INTO `creature_text` VALUES ('22458', '2', '0', 'The dig site is just north of here.', '12', '0', '100', '0', '0', '0', '20329', '0', '');
INSERT INTO `creature_text` VALUES ('22458', '3', '0', 'We\'re here! Start diggin\'!', '12', '0', '100', '0', '0', '0', '20330', '0', '');
INSERT INTO `creature_text` VALUES ('22458', '4', '0', 'I think there\'s somethin\' buried here, beneath the sand!', '12', '0', '100', '396', '0', '0', '20331', '0', '');
INSERT INTO `creature_text` VALUES ('22458', '5', '0', 'Almost got it!', '12', '0', '100', '396', '0', '0', '20332', '0', '');
INSERT INTO `creature_text` VALUES ('22458', '6', '0', 'By Brann\'s brittle bananas! What is it!? It... It looks like a drum.', '12', '0', '100', '396', '0', '0', '20333', '0', '');
INSERT INTO `creature_text` VALUES ('22458', '7', '0', 'Have ye gone mad? You expect me to leave behind a drum without first beatin\' on it? Not this son of Ironforge! No sir!', '12', '0', '100', '0', '0', '0', '20337', '0', '');
INSERT INTO `creature_text` VALUES ('22458', '8', '0', 'Shut yer yaps! I\'m gonna bang on this drum and that\'s that!', '12', '0', '100', '0', '0', '0', '20344', '0', '');
INSERT INTO `creature_text` VALUES ('22458', '9', '0', 'IN YOUR FACE! I told you there was somethin\' here!', '12', '0', '100', '0', '0', '0', '20348', '0', '');
INSERT INTO `creature_text` VALUES ('22458', '10', '0', 'Don\'t just stand there! Help $g him:her; out!', '12', '0', '100', '0', '0', '0', '20361', '0', '');
INSERT INTO `creature_text` VALUES ('22458', '11', '0', 'You\'ve been a tremendous help, $R! Let\'s get out of here before more of those things show up! I\'ll let Dwarfowitz know you did the job asked of ya\' admirably.', '12', '0', '100', '0', '0', '0', '20363', '0', '');
INSERT INTO `creature_text` VALUES ('22464', '0', '0', 'Wow... a drum.', '12', '0', '100', '0', '0', '0', '20334', '0', '');
INSERT INTO `creature_text` VALUES ('22464', '1', '0', 'This discovery will surely rock the foundation of modern archaeology.', '12', '0', '100', '0', '0', '0', '20335', '0', '');
INSERT INTO `creature_text` VALUES ('22464', '2', '0', 'Yea, great. Can we leave now? This desert is giving me hives.', '12', '0', '100', '0', '0', '0', '20336', '0', '');
INSERT INTO `creature_text` VALUES ('22464', '3', '0', 'This reminds me of that one time where you made us search Silithus for evidence of sand gnomes.', '12', '0', '100', '0', '0', '0', '20339', '0', '');
INSERT INTO `creature_text` VALUES ('22464', '4', '0', 'Or that time when you told us that you\'d discovered the cure for the plague of the 20th century. What is that even? 20th century?', '12', '0', '100', '0', '0', '0', '20340', '0', '');
INSERT INTO `creature_text` VALUES ('22464', '5', '0', 'I don\'t think it can top the one time where he told us that he\'d heard that Arthas\'s \"cousin\'s\" skeleton was frozen beneath a glacier in Winterspring. I\'ll never forgive you for that one, Letoll. I mean honestly... Arthas\'s cousin?', '12', '0', '100', '0', '0', '0', '20342', '0', '');
INSERT INTO `creature_text` VALUES ('22464', '6', '0', 'I dunno. It can\'t possibly beat the time he tried to convince us that we\'re all actually a figment of some being\'s imagination and that they only use us for their own personal amusement. That went over well during dinner with the family.', '12', '0', '100', '0', '0', '0', '20343', '0', '');
INSERT INTO `creature_text` VALUES ('22464', '7', '0', 'Say, do you guys hear that?', '12', '0', '100', '0', '0', '0', '20346', '0', '');
INSERT INTO `creature_text` VALUES ('27986', '2', '0', 'It\'s got my vote! That\'ll put hair on your chest like nothing else will.', '12', '0', '100', '1', '0', '0', '28551', '0', 'Hemet Nesingwary');
INSERT INTO `creature_text` VALUES ('27986', '3', '0', 'What a fight you missed, Dorian! It was one for the ages!', '12', '0', '100', '5', '0', '0', '28026', '0', 'Hemet Nesingwary');
INSERT INTO `creature_text` VALUES ('27986', '4', '0', 'There we were, riding into battle at full speed on the back of Stampy...', '12', '0', '100', '5', '0', '0', '28027', '0', 'Hemet Nesingwary');
INSERT INTO `creature_text` VALUES ('27986', '5', '0', 'Well, lad, you can start by readin\' The Green Hills of Stranglethorn. Then, maybe you can join me on one of my safaris. What do you say?', '12', '0', '100', '0', '0', '0', '34687', '0', 'Hemet Nesingwary');
INSERT INTO `creature_text` VALUES ('28047', '0', '0', 'I\'m always up for something of Grimbooze\'s.', '12', '0', '100', '273', '0', '0', '28554', '0', 'Hadrius Harlowe');
INSERT INTO `creature_text` VALUES ('28047', '1', '0', 'Well, so far, it tastes like something my wife would drink...', '12', '0', '100', '5', '0', '0', '28556', '0', 'Hadrius Harlowe');
INSERT INTO `creature_text` VALUES ('28047', '2', '0', 'Now, there\'s the kick I\'ve come to expect from Grimbooze\'s drinks! I like it!', '12', '0', '100', '5', '0', '0', '28557', '0', 'Hadrius Harlowe');
INSERT INTO `creature_text` VALUES ('28568', '0', '0', 'Sure!', '12', '0', '100', '273', '0', '0', '28546', '0', 'Tamara Wobblesprocket');
INSERT INTO `creature_text` VALUES ('28568', '1', '0', 'Oh my...', '12', '0', '100', '0', '0', '0', '30307', '0', 'Tamara Wobblesprocket');
INSERT INTO `creature_text` VALUES ('28568', '2', '0', 'Tastes like I\'m drinking... engine degreaser!', '12', '0', '100', '1', '0', '0', '28548', '0', 'Tamara Wobblesprocket');
INSERT INTO `creature_text` VALUES ('24669', '0', '0', 'Interference with the wakening ritual causes the vrykul to die.', '16', '0', '100', '0', '0', '0', '31589', '0', 'Dormant Vrykul emote text');
INSERT INTO `creature_text` VALUES ('24669', '0', '1', 'The vrykul dies from being awakened improperly.', '16', '0', '100', '0', '0', '0', '31588', '0', 'Dormant Vrykul emote text');
INSERT INTO `creature_text` VALUES ('24669', '0', '2', 'The incorrect application of the awakening rod causes the dormant vrykul to expire.', '16', '0', '100', '0', '0', '0', '31590', '0', 'Dormant Vrykul emote text');
INSERT INTO `creature_text` VALUES ('27619', '0', '0', 'Rise, Arugal!  The power of the Lich King commands you!', '12', '0', '100', '0', '0', '0', '26865', '0', 'Prince Valanar - Silverpine');
INSERT INTO `creature_text` VALUES ('27624', '0', '0', 'Yes... he shall suit our needs perfectly.  Have him sent to Solstice Village when you\'re ready.  Arthas will not be pleased if we fall behind schedule.', '12', '0', '100', '0', '0', '0', '26867', '0', 'Prince Theraldis - Silverpine');
INSERT INTO `creature_text` VALUES ('9543', '0', '0', 'No!  Get away from me!  Help!!', '12', '0', '100', '0', '0', '0', '4973', '0', 'Ribbly Screwspigot');
INSERT INTO `creature_text` VALUES ('28750', '0', '1', 'Mphmm rmphhimm rrhumghph?', '12', '0', '100', '1', '0', '0', '28655', '0', 'Blight Geist');
INSERT INTO `creature_text` VALUES ('28750', '0', '2', 'Mhrrumph rummrhum phurr!', '12', '0', '100', '1', '0', '0', '28757', '0', 'Blight Geist');
INSERT INTO `creature_text` VALUES ('28793', '0', '1', 'Your disguise doesn\'t fool me, fleshling! Your defeat will please Drakuru!', '12', '0', '100', '1', '0', '0', '28858', '0', 'Darmuk');
INSERT INTO `creature_text` VALUES ('23980', '0', '0', 'I return! A second chance to carve your skull!', '14', '0', '100', '0', '0', '13209', '29977', '0', 'Ingvar the Plunderer - SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES ('23980', '1', '0', 'I am a warrior born!', '14', '0', '100', '0', '0', '13214', '29975', '0', 'Ingvar the Plunderer - SAY_SLAY_2');
INSERT INTO `creature_text` VALUES ('23980', '2', '0', 'No! I can do... better! I can...', '14', '0', '100', '0', '0', '13211', '29979', '0', 'Ingvar the Plunderer - SAY_DEATH_2');
INSERT INTO `creature_text` VALUES ('31279', '1', '0', 'The Light\'s blessing be upon you for aiding me in my time of need, $N.', '12', '0', '100', '0', '0', '0', '31941', '0', 'Father Kamaros');
INSERT INTO `creature_text` VALUES ('31279', '2', '0', 'I\'ve had my fill of this place. Let us depart.', '12', '0', '100', '0', '0', '0', '31942', '0', 'Father Kamaros');
INSERT INTO `creature_text` VALUES ('31279', '3', '0', 'You must tell my brothers that I live.', '12', '0', '100', '0', '0', '0', '31944', '0', 'Father Kamaros');
INSERT INTO `creature_text` VALUES ('31279', '4', '0', 'I have you to thank for my life. I will return to my comrades and spread word of your bravery. Fight the Scourge with all the strength you can muster, and we will be by your side.', '12', '0', '100', '0', '0', '0', '31943', '0', 'Father Kamaros');
INSERT INTO `creature_text` VALUES ('27411', '0', '0', 'Were you bitten?', '12', '7', '100', '6', '0', '0', '26640', '0', 'Freed Alliance Scout Say1');
INSERT INTO `creature_text` VALUES ('27411', '1', '1', 'Answer me!  Were you bloody bitten?', '14', '7', '100', '22', '0', '0', '26641', '0', 'Freed Alliance Scout Yell');
INSERT INTO `creature_text` VALUES ('27411', '2', '2', 'You don\'t even know, do you?  The trappers?  They\'re not human... we have to get out of here!  Come, quick!', '12', '7', '100', '0', '0', '0', '26539', '0', 'Freed Alliance Scout Say2');
INSERT INTO `creature_text` VALUES ('27411', '3', '3', 'Take Ducal\'s horse, he\'s one of them now!', '12', '7', '100', '0', '0', '0', '26540', '0', 'Freed Alliance Scout Say3');
INSERT INTO `creature_text` VALUES ('27411', '4', '4', 'There\'s lamp oil in the back - make use of it.  The fiends hate fire!', '12', '7', '100', '0', '0', '0', '26644', '0', 'Freed Alliance Scout Say4');
INSERT INTO `creature_text` VALUES ('12997', '0', '0', 'Into the box me pretties! Thas it. One by one ye go.', '12', '0', '0', '0', '0', '0', '8465', '0', 'Monty');
INSERT INTO `creature_text` VALUES ('28105', '0', '0', 'My treasure! You no steal from Tartek, dumb big-tongue traitor thing.', '14', '0', '100', '0', '0', '0', '27893', '0', 'Warlord Tartek');
INSERT INTO `creature_text` VALUES ('28105', '1', '0', 'Tartek and nasty dragon going to kill you! You so dumb.', '14', '0', '100', '0', '0', '0', '27895', '0', 'Warlord Tartek');
INSERT INTO `creature_text` VALUES ('24480', '0', '0', 'I thought you\'d never ask!', '15', '0', '100', '0', '0', '0', '23477', '0', 'Mojo');
INSERT INTO `creature_text` VALUES ('24480', '0', '1', 'I promise not to give you warts...', '15', '0', '100', '0', '0', '0', '23480', '0', 'Mojo');
INSERT INTO `creature_text` VALUES ('24480', '0', '2', 'This won\'t take long, did it?', '15', '0', '100', '0', '0', '0', '23479', '0', 'Mojo');
INSERT INTO `creature_text` VALUES ('24480', '0', '3', 'Now that\'s what I call froggy-style!', '15', '0', '100', '0', '0', '0', '23478', '0', 'Mojo');
INSERT INTO `creature_text` VALUES ('24480', '0', '4', 'Listen, $N, I know of a little swamp not too far from here....', '15', '0', '100', '0', '0', '0', '23482', '0', 'Mojo');
INSERT INTO `creature_text` VALUES ('24480', '0', '5', 'Your lily pad or mine?', '15', '0', '100', '0', '0', '0', '23483', '0', 'Mojo');
INSERT INTO `creature_text` VALUES ('24480', '0', '6', 'Feelin\' a little froggy, are ya?', '15', '0', '100', '0', '0', '0', '23484', '0', 'Mojo');
INSERT INTO `creature_text` VALUES ('24480', '0', '7', 'There\'s just never enough Mojo to go around...', '15', '0', '100', '0', '0', '0', '23481', '0', 'Mojo');
INSERT INTO `creature_text` VALUES ('27899', '1', '0', 'Hey there, $N, don\'t be alarmed. It\'s me... you... from the future.  I\'m here to help.', '15', '0', '100', '396', '0', '0', '27239', '0', 'Future You');
INSERT INTO `creature_text` VALUES ('27899', '2', '0', 'Heads up... here they come. I\'ll help as much as I can. Let\'s just keep them off of the hourglass!', '15', '0', '100', '396', '0', '0', '27251', '0', 'Future You');
INSERT INTO `creature_text` VALUES ('27899', '3', '0', 'No matter what, you can\'t die, because that would mean that I would cease to exist, right? But, I was here before when I was you. I\'m so confused!', '15', '0', '100', '0', '0', '0', '27306', '0', 'Future You');
INSERT INTO `creature_text` VALUES ('27899', '4', '0', 'I can\'t believe that I used to wear that.', '15', '0', '100', '0', '0', '0', '27301', '0', 'Future You');
INSERT INTO `creature_text` VALUES ('27899', '5', '0', 'Sorry, but Chromie said that I couldn\'t reveal anything about your future to you. She said that if I did, I would cease to exist.', '15', '0', '100', '0', '0', '0', '27311', '0', 'Future You');
INSERT INTO `creature_text` VALUES ('27899', '6', '0', 'Wow, I\'d forgotten how inexperienced I used to be.', '15', '0', '100', '0', '0', '0', '27307', '0', 'Future You');
INSERT INTO `creature_text` VALUES ('27899', '7', '0', 'Look at you fight; no wonder I turned to drinking.', '15', '0', '100', '0', '0', '0', '27304', '0', 'Future You');
INSERT INTO `creature_text` VALUES ('27899', '8', '0', 'What? Am I here alone. We both have a stake in this, you know!', '15', '0', '100', '0', '0', '0', '27305', '0', 'Future You');
INSERT INTO `creature_text` VALUES ('27899', '9', '0', 'Listen. I\'m not supposed to tell you this, but there\'s going to be this party that you\'re invited to. Whatever you do, DO NOT DRINK THE PUNCH!', '15', '0', '100', '0', '0', '0', '27313', '0', 'Future You');
INSERT INTO `creature_text` VALUES ('27899', '10', '0', 'Wish I could remember how many of the Infinite Dragonflight were going to try to stop you. This fight was so long ago.', '15', '0', '100', '0', '0', '0', '27312', '0', 'Future You');
INSERT INTO `creature_text` VALUES ('27899', '11', '0', 'Look, $N, the hourglass has revealed Nozdormu!', '15', '0', '100', '25', '0', '0', '27275', '0', 'Future You');
INSERT INTO `creature_text` VALUES ('27899', '12', '0', 'Farewell, $N. Keep us alive and get some better equipment!', '15', '0', '100', '0', '0', '0', '27287', '0', 'Future You');
INSERT INTO `creature_text` VALUES ('28948', '0', '0', 'Ahh... there you are. The master told us you\'d be arriving soon.', '12', '0', '100', '0', '0', '0', '29127', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '1', '0', 'Please, follow me, $N. There is much for you to see....', '12', '0', '100', '0', '0', '0', '29128', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '2', '0', 'Ever since his arrival from Drak\'Tharon, the master has spoken of the time you would be joining him here.', '12', '0', '100', '0', '0', '0', '29172', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '3', '0', 'You should feel honored. You are the first of the master\'s prospects to be shown our operation.', '12', '0', '100', '0', '0', '0', '29171', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '4', '0', 'The things I show you now must never be spoken of outside Voltarus. The world shall come to know our secret soon enough!', '12', '0', '100', '0', '0', '0', '29173', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '5', '0', 'Here lie our stores of blight crystal, without which our project would be impossible.', '12', '0', '100', '0', '0', '0', '29174', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '6', '0', 'I understand that you are to thank for the bulk of our supply.', '12', '0', '100', '0', '0', '0', '29175', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '7', '0', 'These trolls are among those you exposed on the battlefield. Masterfully done, indeed....', '12', '0', '100', '0', '0', '0', '29176', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '8', '0', 'We feel it best to position them here, where they might come to terms with their impending fate.', '12', '0', '100', '0', '0', '0', '29433', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '9', '0', 'This is their destiny....', '12', '0', '100', '0', '0', '0', '29434', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '10', '0', 'The blight slowly seeps into their bodies, gradually preparing them for their conversion.', '12', '0', '100', '0', '0', '0', '29435', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '11', '0', 'This special preparation grants them unique powers far greater than they would otherwise know.', '12', '0', '100', '0', '0', '0', '29436', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '12', '0', 'Soon, the master will grant them the dark gift, making them fit to serve the Lich King for eternity!', '12', '0', '100', '0', '0', '0', '29437', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '13', '0', 'Stay for as long as you like, $N. Glory in the fruits of your labor!', '12', '0', '100', '0', '0', '0', '29438', '0', 'Malmortis say');
INSERT INTO `creature_text` VALUES ('28948', '14', '0', 'Your service has been invaluable in fulfilling the master\'s plan. May you forever grow in power....', '12', '0', '100', '0', '0', '0', '29439', '0', 'Malmortis say');
