INSERT INTO `creature_text` VALUES ('31242', '0', '0', 'Ah, there you are! Remember me? Of course you do! Wait right there, I\'m coming down.', '14', '0', '0', '0', '0', '0', '31882', '0', 'Sigrid Iceborn say 1');
