INSERT INTO `creature_text` VALUES ('27355', '2', '0', 'This is not the end... death only... strengthens... ', '14', '0', '100', '1', '0', '0', '26669', '0', 'Rothin the Decaying <Cult of the Damned>');
INSERT INTO `creature_text` VALUES ('10541', '0', '0', 'It\'s 428,000 degrees Kraklenheit... What\'s happening, hot stuff!?', '12', '0', '100', '0', '0', '0', '6006', '0', 'Krakle\'s Thermometer');
INSERT INTO `creature_text` VALUES ('10541', '0', '1', 'DING! 428,000 degrees Kraklenheit, exactly! Well, approximately. Almost. Somewhere around there...', '12', '0', '100', '0', '0', '0', '6007', '0', 'Krakle\'s Thermometer');
INSERT INTO `creature_text` VALUES ('10541', '0', '2', 'Measuring by Kraklenheit, it is 428,000 degrees! That\'s Krakley!', '12', '0', '100', '0', '0', '0', '6008', '0', 'Krakle\'s Thermometer');
INSERT INTO `creature_text` VALUES ('10541', '1', '0', 'The temperature is 122 degrees Kraklenheit.', '12', '0', '100', '0', '0', '0', '5766', '0', 'Krakle\'s Thermometer');
INSERT INTO `creature_text` VALUES ('10541', '1', '1', 'The temperature is 9280 degrees Kraklenheit! That\'s HOT!', '12', '0', '100', '0', '0', '0', '5764', '0', 'Krakle\'s Thermometer');
INSERT INTO `creature_text` VALUES ('10541', '1', '2', 'Wow, it\'s 3 degrees Kraklenheit. Keep looking.', '12', '0', '100', '0', '0', '0', '6011', '0', 'Krakle\'s Thermometer');
