INSERT INTO `creature_text` VALUES ('25416', '2', '0', 'Tell Imperean that I will consider a cessation of hostilities. But first, Churn must stop his watery intrusions upon my rise!', '12', '0', '0', '0', '0', '0', '24667', '0', '');
