INSERT INTO `creature_text` VALUES ('28659', '0', '0', 'Ah, the hero. Your little friends said you would come. This certainly saves me the trouble of hunting you down myself.', '14', '0', '100', '2', '0', '0', '28646', '0', 'Artruis the Heartless');
INSERT INTO `creature_text` VALUES ('28659', '1', '0', 'I have weathered a hundred years of war and suffering. Do you truly think it wise to pit your mortal body against a being that cannot die? I\'d venture you have more to lose.', '14', '0', '100', '2', '0', '0', '28647', '0', 'Artruis the Heartless');
INSERT INTO `creature_text` VALUES ('28659', '2', '0', 'Even shattered into countless pieces, the crystals all around weaken me... perhaps I should not have underestimated the titans so...', '14', '0', '100', '2', '0', '0', '28648', '0', 'Artruis the Heartless');
INSERT INTO `creature_text` VALUES ('28659', '3', '0', 'These two brave, ignorant fools despise each other, yet somehow they both spoke of you as a hero. Perhaps you just lack the will to choose a side... let me extend you that opportunity.', '14', '0', '100', '2', '0', '0', '28649', '0', 'Artruis the Heartless');
INSERT INTO `creature_text` VALUES ('28659', '4', '0', 'Artruis is shielded. You must choose your side quickly to break his spell.', '41', '0', '100', '2', '0', '0', '33188', '0', 'Artruis the Heartless');
INSERT INTO `creature_text` VALUES ('28659', '5', '0', 'Arthas once mustered strength... of the very same sort... perhaps his is the path that you will follow.', '14', '0', '100', '2', '0', '0', '28650', '0', 'Artruis the Heartless');
INSERT INTO `creature_text` VALUES ('28667', '0', '0', 'Now you not catch us with back turned! Now we hurt you bad undead. BAD!', '12', '0', '100', '0', '0', '0', '28936', '0', 'Jaloot');
INSERT INTO `creature_text` VALUES ('28668', '0', '0', 'You going die big bad undead thing! You not catch Zepik sleeping this time!', '12', '0', '100', '0', '0', '0', '28937', '0', 'Zepik the Gorloc Hunter');
INSERT INTO `creature_text` VALUES ('17233', '4', '0', 'Why do you do this?  Did I somehow wrong you in life?', '12', '0', '100', '0', '0', '0', '13625', '0', 'Ghost of Uther Lightbringer ');
INSERT INTO `creature_text` VALUES ('17233', '5', '0', 'Ah, I see it now in your mind.  This is the work of one of my former students... Mehlar Dawnblade.  It is sad to know that his heart has turned so dark.', '12', '0', '100', '0', '0', '0', '13626', '0', 'Ghost of Uther Lightbringer');
INSERT INTO `creature_text` VALUES ('17233', '6', '0', 'Return to him.  Return to Mehlar and tell him that I forgive him and that I understand why he believes what he does.', '12', '0', '100', '0', '0', '0', '13627', '0', 'Ghost of Uther Lightbringer ');
INSERT INTO `creature_text` VALUES ('17233', '7', '0', 'I can only hope that he will see the Light and instead turn his energies to restoring once-beautiful Quel\'Thalas.', '12', '0', '100', '0', '0', '0', '13640', '0', 'Ghost of Uther Lightbringer ');
INSERT INTO `creature_text` VALUES ('1854', '1', '0', 'By the Light you will leave this tomb!', '12', '0', '100', '0', '0', '0', '13611', '0', 'High Priest Thel danis');
INSERT INTO `creature_text` VALUES ('20886', '3', '1', 'Defend yourself, for all the good it will do....', '14', '0', '100', '0', '0', '11242', '19972', '0', 'Wrath-Scryer Soccothrates - Knock Away');
INSERT INTO `creature_text` VALUES ('20886', '3', '0', 'On guard!', '14', '0', '100', '0', '0', '11241', '19971', '0', 'Wrath-Scryer Soccothrates - Knock Away');
INSERT INTO `creature_text` VALUES ('20886', '2', '0', 'Yes, that was quite satisfying.', '14', '0', '100', '0', '0', '11239', '19969', '0', 'Wrath-Scryer Soccothrates - Kill');
INSERT INTO `creature_text` VALUES ('20886', '1', '0', 'At last, a target for my frustrations!', '14', '0', '100', '0', '0', '11238', '19967', '0', 'Wrath-Scryer Soccothrates - Aggro');
INSERT INTO `creature_text` VALUES ('20886', '0', '0', 'Have you come to kill Dalliah?  Can I watch?', '14', '0', '100', '1', '0', '11237', '19968', '0', 'Wrath-Scryer Soccothrates - Aggro Dalliah First');
INSERT INTO `creature_text` VALUES ('20885', '0', '0', 'Don\'t worry about me; kill that worthless dullard instead!', '14', '0', '100', '1', '0', '11085', '19979', '0', 'Dalliah the Doomsayer - Aggro Soccothrates First');
INSERT INTO `creature_text` VALUES ('20885', '1', '0', 'It is unwise to anger me!', '14', '0', '100', '0', '0', '11086', '19978', '0', 'Dalliah the Doomsayer - Aggro');
INSERT INTO `creature_text` VALUES ('20885', '2', '0', 'Completely ineffective.  Just like someone else I know.', '14', '0', '100', '0', '0', '11087', '19980', '0', 'Dalliah the Doomsayer - Kill');
INSERT INTO `creature_text` VALUES ('20885', '2', '1', 'You chose the wrong opponent.', '14', '0', '100', '0', '0', '11088', '19981', '0', 'Dalliah the Doomsayer - Kill');
INSERT INTO `creature_text` VALUES ('20885', '3', '0', 'Reap the whirlwind!', '14', '0', '100', '0', '0', '11089', '19982', '0', 'Dalliah the Doomsayer - Cast Whirlwind');
INSERT INTO `creature_text` VALUES ('20885', '3', '1', 'I\'ll cut you to pieces!', '14', '0', '100', '0', '0', '11090', '19983', '0', 'Dalliah the Doomsayer - Cast Whirlwind');
INSERT INTO `creature_text` VALUES ('20885', '4', '0', 'That is much better.', '14', '0', '100', '0', '0', '11091', '19984', '0', 'Dalliah the Doomsayer - Cast Heal');
INSERT INTO `creature_text` VALUES ('20885', '4', '1', 'Ahh... just what I needed.', '14', '0', '100', '0', '0', '11092', '19985', '0', 'Dalliah the Doomsayer - Cast Heal');
INSERT INTO `creature_text` VALUES ('20885', '5', '0', 'Now I\'m really angry.', '14', '0', '100', '0', '0', '11093', '19986', '0', 'Dalliah the Doomsayer - Death');
INSERT INTO `creature_text` VALUES ('20885', '6', '0', 'More than you can handle, scryer?', '14', '0', '100', '1', '0', '11094', '19987', '0', 'Dalliah the Doomsayer - Soccothrates at 25%');
INSERT INTO `creature_text` VALUES ('20885', '6', '1', 'I suppose I\'ll end up fighting them all myself.', '14', '0', '100', '1', '0', '11095', '19988', '0', 'Dalliah the Doomsayer - Soccothrates at 25%');
INSERT INTO `creature_text` VALUES ('20885', '6', '2', 'I\'ve grown used to cleaning up your messes.', '14', '0', '100', '1', '0', '11096', '19989', '0', 'Dalliah the Doomsayer - Soccothrates at 25%');
INSERT INTO `creature_text` VALUES ('20885', '7', '0', 'Congratulations.  I\'ve wanted to do that for years.', '14', '0', '100', '66', '0', '11097', '19990', '0', 'Dalliah the Doomsayer - Soccothratess dies');
INSERT INTO `creature_text` VALUES ('20886', '4', '0', 'Knew this was... the only way out.', '14', '0', '100', '0', '0', '11243', '19973', '0', 'Wrath-Scryer Soccothrates - Death');
INSERT INTO `creature_text` VALUES ('20886', '5', '0', 'Having problems, Dalliah?  How nice.', '14', '0', '100', '1', '0', '11244', '19974', '0', 'Wrath-Scryer Soccothrates - Dalliah at 25%');
INSERT INTO `creature_text` VALUES ('20886', '5', '1', 'This may be the end for you, Dalliah.  What a shame that would be.', '14', '0', '100', '1', '0', '11245', '19975', '0', 'Wrath-Scryer Soccothrates - Dalliah at 25%');
INSERT INTO `creature_text` VALUES ('20886', '5', '2', 'I suggest a new strategy: you draw their attacks while I gather reinforcements.', '14', '0', '100', '1', '0', '11246', '19976', '0', 'Wrath-Scryer Soccothrates - Dalliah at 25%');
INSERT INTO `creature_text` VALUES ('20886', '6', '0', 'Finally!  Well done!', '14', '0', '100', '66', '0', '11247', '19977', '0', 'Wrath-Scryer Soccothrates - Dalliah dies');
INSERT INTO `creature_text` VALUES ('17941', '0', '0', 'The work must continue!', '14', '0', '100', '0', '0', '10376', '17888', '0', 'Mennu the Betrayer - Aggro');
INSERT INTO `creature_text` VALUES ('17941', '0', '1', 'You brought this on yourselves!', '14', '0', '100', '0', '0', '10378', '17889', '0', 'Mennu the Betrayer - Aggro');
INSERT INTO `creature_text` VALUES ('17941', '0', '2', 'Don\'t make me kill you!', '14', '0', '100', '0', '0', '10379', '17890', '0', 'Mennu the Betrayer - Aggro');
INSERT INTO `creature_text` VALUES ('17941', '1', '0', 'It had to be done....', '14', '0', '100', '0', '0', '10380', '17892', '0', 'Mennu the Betrayer - Kill');
INSERT INTO `creature_text` VALUES ('17941', '1', '1', 'You should not have come....', '14', '0', '100', '0', '0', '10381', '17893', '0', 'Mennu the Betrayer - Kill');
INSERT INTO `creature_text` VALUES ('17941', '2', '0', 'I... deserve this....', '14', '0', '100', '0', '0', '10382', '17894', '0', 'Mennu the Betrayer - Death');
INSERT INTO `creature_text` VALUES ('29196', '0', '0', 'As disciples of blood, you strive to master the very lifeforce of your enemies.', '12', '0', '100', '1', '0', '0', '29867', '0', 'Lord Thorval - (Part 1 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES ('29196', '1', '0', 'Be it by blade or incantation, blood feeds our attacks and weakens our foes.', '12', '0', '100', '1', '0', '0', '29868', '0', 'Lord Thorval - (Part 2 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES ('29196', '2', '0', 'True masters learn to make blood serve more than just their strength in battle.', '12', '0', '100', '1', '0', '0', '29869', '0', 'Lord Thorval - (Part 3 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES ('29196', '3', '0', 'Stripping energy from our foes, both fighting and fallen, allows us to persevere where lesser beings fall exhausted.', '12', '0', '100', '1', '0', '0', '29870', '0', 'Lord Thorval - (Part 4 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES ('29196', '4', '0', 'And every foe that falls, energy sapped and stolen, only further fuels our assault.', '12', '0', '100', '1', '0', '0', '29871', '0', 'Lord Thorval - (Part 5 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES ('29196', '5', '0', 'As masters of blood, we know battle without end...', '12', '0', '100', '1', '0', '0', '29872', '0', 'Lord Thorval - (Part 6 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES ('29196', '6', '0', 'We know hunger never to be quenched...', '12', '0', '100', '1', '0', '0', '29873', '0', 'Lord Thorval - (Part 7 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES ('29196', '7', '0', 'We know power never to be overcome...', '12', '0', '100', '1', '0', '0', '29874', '0', 'Lord Thorval - (Part 8 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES ('29196', '8', '0', 'As masters of blood, we are masters of life and death itself.  Against us, even hope falls drained and lifeless.', '12', '0', '100', '1', '0', '0', '29875', '0', 'Lord Thorval - (Part 9 of Speech To Deciples)');
INSERT INTO `creature_text` VALUES ('20886', '7', '0', 'Did you call on me?', '14', '0', '100', '397', '0', '11236', '20051', '0', 'Wrath-Scryer Soccothrates - Conversation with Dalliah part 1');
INSERT INTO `creature_text` VALUES ('20886', '8', '0', 'To do your heavy lifting, most likely.', '14', '0', '100', '396', '0', '0', '20052', '0', 'Wrath-Scryer Soccothrates - Conversation with Dalliah part 2');
INSERT INTO `creature_text` VALUES ('20886', '9', '0', 'Then I\'ll commit myself to ignoring you.', '14', '0', '100', '396', '0', '0', '20053', '0', 'Wrath-Scryer Soccothrates - Conversation with Dalliah part 3');
INSERT INTO `creature_text` VALUES ('20886', '10', '0', 'You\'re the one who should be-- Wait, we have company...', '14', '0', '100', '396', '0', '0', '20054', '0', 'Wrath-Scryer Soccothrates - Conversation with Dalliah part 4');
INSERT INTO `creature_text` VALUES ('20885', '8', '0', 'Why would I call on you?', '14', '0', '100', '396', '0', '0', '20055', '0', 'Dalliah the Doomsayer - Conversation with Soccothrates part 1');
INSERT INTO `creature_text` VALUES ('20885', '9', '0', 'When I need someone to prance around like an overstuffed peacock, I\'ll call on you.', '14', '0', '100', '396', '0', '0', '20056', '0', 'Dalliah the Doomsayer - Conversation with Soccothrates part 2');
INSERT INTO `creature_text` VALUES ('20885', '10', '0', 'What would you know about commitment, sheet-sah?', '14', '0', '100', '396', '0', '0', '20057', '0', 'Dalliah the Doomsayer - Conversation with Soccothrates part 3');
INSERT INTO `creature_text` VALUES ('28907', '0', '0', 'The sky itself feeds upon the suffering of the conquered!\n', '12', '0', '100', '5', '0', '0', '29048', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '1', '0', 'The Scarlet Crusade is powerless to stop us!\n', '12', '0', '100', '5', '0', '0', '29049', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '2', '0', 'The corpses of those that try only serve to feed our expanding host!', '12', '0', '100', '1', '0', '0', '29050', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '3', '0', 'The Scarlet fleet lies in ruin upon the sundered coast!\n', '12', '0', '100', '25', '0', '0', '29051', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '4', '0', 'With each enemy slain, our strength grows! We mustn\'t relent! \n', '12', '0', '100', '5', '0', '0', '29052', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '5', '0', 'The Lich King has spoken to each of you! Let his words resonate among you once more!', '12', '0', '100', '25', '0', '0', '29053', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '6', '0', 'ALL. MUST. DIE.', '12', '0', '100', '22', '0', '0', '29054', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '7', '0', 'Leave no survivors in your wake, brothers and sisters!', '12', '0', '100', '274', '0', '0', '29055', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '8', '0', 'No mercy for the weak!', '12', '0', '100', '15', '0', '0', '29056', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '9', '0', 'TERROR!', '12', '0', '100', '5', '0', '0', '29057', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '10', '0', 'CHAOS!', '12', '0', '100', '22', '0', '0', '29058', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '11', '0', 'DESTRUCTION!', '12', '0', '100', '15', '0', '0', '29059', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '12', '0', 'With the approaching darkness comes the end of the Scarlet Crusade. You will battle once more! For the Scourge! For the Lich King!', '12', '0', '100', '5', '0', '0', '29060', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('28907', '13', '0', 'SUFFER WELL, DEATH KNIGHTS!', '12', '0', '100', '71', '0', '9006', '29061', '0', 'Prince Valanar');
INSERT INTO `creature_text` VALUES ('29305', '4', '0', 'Da ground gonna swallow you up!', '14', '0', '100', '0', '0', '14723', '31429', '0', 'Moorabi - Quake');
INSERT INTO `creature_text` VALUES ('5089', '0', '0', 'Alright! Alright! We surrender... Just put your weapons down, I\'ll cooperate!', '12', '7', '100', '0', '0', '0', '1756', '0', 'Balos Jacken');
INSERT INTO `creature_text` VALUES ('3644', '0', '0', 'Anaya...? Do my eyes deceive me? Is it really you?', '12', '7', '100', '1', '0', '0', '1278', '0', 'Cerellean Whiteclaw');
INSERT INTO `creature_text` VALUES ('3644', '1', '0', 'That the fates should be so cruel as to permit us only this after a thousand years apart...', '16', '7', '100', '1', '0', '0', '1280', '0', 'Cerellean Whiteclaw');
INSERT INTO `creature_text` VALUES ('3644', '2', '0', 'Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage.', '16', '7', '100', '1', '0', '0', '1281', '0', 'Cerellean Whiteclaw');
INSERT INTO `creature_text` VALUES ('3644', '3', '0', 'No! Anaya... Anaya! Don\'t leave me! Please...', '12', '7', '100', '5', '0', '0', '1286', '0', 'Cerellean Whiteclaw');
INSERT INTO `creature_text` VALUES ('3644', '4', '0', 'How, my love? How will I find the strength to face the ages of the world without you by my side...', '12', '7', '100', '5', '0', '0', '1287', '0', 'Cerellean Whiteclaw');
INSERT INTO `creature_text` VALUES ('3843', '0', '0', 'The ages have been cruel to you and I, my love, but be assured, it is, and at long last we are reunited.', '12', '7', '100', '1', '0', '0', '1279', '0', 'Anaya');
INSERT INTO `creature_text` VALUES ('3843', '1', '0', 'Let it not trouble your heart, beloved. You have freed me from slavery, and for that I love you all the more.', '12', '7', '100', '1', '0', '0', '1282', '0', 'Anaya');
INSERT INTO `creature_text` VALUES ('3843', '2', '0', 'Sadly, even this must be cut short... The ties that bind me to this world weaken, and pull me away...', '12', '7', '100', '1', '0', '0', '1283', '0', 'Anaya');
INSERT INTO `creature_text` VALUES ('3843', '3', '0', 'Farewell, Cerellean, until we are joined once again...', '12', '7', '100', '1', '0', '0', '1284', '0', 'Anaya');
INSERT INTO `creature_text` VALUES ('3843', '4', '0', '%s\'s soft voice trails away into the mists, \"Know that I love you always...\"', '16', '7', '100', '0', '0', '0', '1285', '0', 'Anaya');
INSERT INTO `creature_text` VALUES ('3946', '0', '0', 'The numbers of my companions dwindles, goddess, and my own power shall soon be insufficient to hold back the demons of Felwood.', '12', '7', '100', '1', '0', '0', '1356', '0', 'Velinde Starsong');
INSERT INTO `creature_text` VALUES ('3946', '1', '0', 'Goddess, grant me the power to overcome my enemies! Hear me, please, my need is desperate and my cause is just!', '12', '7', '100', '1', '0', '0', '1357', '0', 'Velinde Starsong');
INSERT INTO `creature_text` VALUES ('3946', '2', '0', 'What... what is this? Could this be the answer to my prayers? Elune has granted me a weapon--this scythe--to defeat the demons.', '12', '7', '100', '1', '0', '0', '1358', '0', 'Velinde Starsong');
INSERT INTO `creature_text` VALUES ('4509', '0', '0', 'In the throes of the Sundering, Ameth\'Aran was at the whim of the terror that gripped the land. There was little hope for survival.', '12', '7', '100', '1', '0', '0', '1471', '0', 'Sargath');
INSERT INTO `creature_text` VALUES ('4509', '1', '0', 'Athrikus came to us. He told us that he could save us from harm. He cast a spell upon us to protect us from harm.', '12', '7', '100', '1', '0', '0', '1472', '0', 'Sargath');
INSERT INTO `creature_text` VALUES ('4509', '2', '0', 'When the shaking stopped, his true motives were revealed. We were trapped, and he was slowly draining our powers.', '12', '7', '100', '1', '0', '0', '1474', '0', 'Sargath');
INSERT INTO `creature_text` VALUES ('4509', '3', '0', 'There were hundreds imprisoned by his spell. Now only a few remain in their prisons. He would speak to us sometimes.', '12', '7', '100', '1', '0', '0', '1497', '0', 'Sargath');
INSERT INTO `creature_text` VALUES ('4509', '4', '0', 'He worried that his power weakened, that soon even we last of his precious soulgems would fade and die.', '12', '7', '100', '1', '0', '0', '1498', '0', 'Sargath');
INSERT INTO `creature_text` VALUES ('4509', '5', '0', 'His lieutenant, Ilkrud Magthrull possesses a tome which might indicate the location of the remaining soulgems.', '12', '7', '100', '1', '0', '0', '1475', '0', 'Sargath');
INSERT INTO `creature_text` VALUES ('35476', '0', '0', 'Of course I was the one chosen to summon a demon for the competitors. I am Wilfred Fizzlebang, master summoner! Demons quail before the mere thought of being under my will!', '12', '0', '100', '1', '0', '0', '35818', '0', 'Wilfred Fizzlebang');
INSERT INTO `creature_text` VALUES ('35476', '0', '1', 'Soon all the world will know the name of Wilfred!', '12', '0', '100', '1', '0', '0', '35816', '0', 'Wilfred Fizzlebang');
INSERT INTO `creature_text` VALUES ('28256', '0', '0', 'So the oracle has decided to aid you, outlander. Very well, I will offer you what I know.', '12', '0', '100', '0', '0', '0', '27697', '0', 'Voice of Nozronn');
INSERT INTO `creature_text` VALUES ('28256', '1', '0', 'Your companion seeks the shardhorn patriarch Farunn, who holds himself aloof from his brothers.', '12', '0', '100', '0', '0', '0', '27698', '0', 'Voice of Nozronn');
INSERT INTO `creature_text` VALUES ('28256', '2', '0', 'Farunn walks the lands north of what your people call the Bittertide Falls. You will find him alone, but you will see he requires no defenders.', '12', '0', '100', '0', '0', '0', '27699', '0', 'Voice of Nozronn');
INSERT INTO `creature_text` VALUES ('28256', '3', '0', 'Return to your companion. Tell him to go forth and claim his prize in Farunn.', '12', '0', '100', '0', '0', '0', '27701', '0', 'Voice of Nozronn');
INSERT INTO `creature_text` VALUES ('28256', '4', '0', 'Once he has done so, tell him to leave this land in peace. He does not belong here.', '12', '0', '100', '0', '0', '0', '27702', '0', 'Voice of Nozronn');
INSERT INTO `creature_text` VALUES ('2015', '0', '0', 'You will be easy prey, $C.', '14', '0', '100', '0', '0', '0', '2231', '0', 'Bloodfeather Harpy');
INSERT INTO `creature_text` VALUES ('2017', '0', '0', 'A fine trophy your head will make, $R.', '12', '0', '100', '0', '0', '0', '2229', '0', 'Bloodfeather Rogue');
INSERT INTO `creature_text` VALUES ('2017', '0', '1', 'My talons will shred your puny body, $R.', '12', '0', '100', '0', '0', '0', '2230', '0', 'Bloodfeather Rogue');
INSERT INTO `creature_text` VALUES ('2017', '0', '2', 'You will be easy prey, $C.', '14', '0', '100', '0', '0', '0', '2231', '0', 'Bloodfeather Rogue');
INSERT INTO `creature_text` VALUES ('3395', '0', '0', 'I am summoned!  Intruders, come to my tent and face your death!', '14', '1', '100', '0', '0', '0', '1079', '0', 'Verog the Dervish');
INSERT INTO `creature_text` VALUES ('3451', '0', '0', 'Looks like you\'ll have to go ahead to Ratchet and tell Sputtervalve that I\'ve wrecked the shredder.', '12', '0', '100', '0', '0', '0', '0', '0', 'Pilot Wizzlecrank');
INSERT INTO `creature_text` VALUES ('3451', '1', '0', 'I\'ll stay behind and guard the wreck. Hurry! Hopefully no one will notice the smoke...', '12', '0', '100', '0', '0', '0', '0', '0', 'Pilot Wizzlecrank');
INSERT INTO `creature_text` VALUES ('3569', '0', '0', 'Kill!!!', '12', '0', '100', '0', '0', '0', '1176', '0', 'Bogling');
INSERT INTO `creature_text` VALUES ('8761', '0', '0', '%s becomes enraged!', '16', '0', '100', '0', '0', '0', '24144', '0', 'Mosshoof Courser');
INSERT INTO `creature_text` VALUES ('8877', '0', '0', '%s goes into a frenzy!', '16', '0', '100', '0', '0', '0', '38630', '0', 'Sandfury Zealot');
INSERT INTO `creature_text` VALUES ('34980', '0', '0', 'You dare to challenge Drottinn Hrothgar, king of the seas?', '14', '0', '100', '0', '0', '0', '35227', '0', 'Drottinn Hrothgar');
INSERT INTO `creature_text` VALUES ('32576', '0', '0', 'The Helmsman comes for you!', '14', '0', '100', '0', '0', '0', '24734', '0', 'Orabus the Helmsman');
INSERT INTO `creature_text` VALUES ('32576', '1', '0', 'Now you face Orabus, fool!', '14', '0', '100', '0', '0', '0', '24845', '0', 'Orabus the Helmsman');
INSERT INTO `creature_text` VALUES ('31057', '0', '0', 'Hello. Master Fras Siabi is not in at this time. Please come back tomorrow to Fras Siabi\'s Premium Tobacco! Beep!', '12', '0', '100', '0', '0', '0', '31716', '0', 'Mechanical Greeter RY7R');
INSERT INTO `creature_text` VALUES ('30994', '0', '0', 'No remedy seems to work - the entire city has sickened...', '12', '0', '100', '0', '0', '0', '31583', '0', 'Magistrate Barthilas');
INSERT INTO `creature_text` VALUES ('30994', '0', '1', 'The soldiers are spreading panic with the rumors of bad food. Neighbors are accusing one another of poison. The city will fall into bedlam!', '12', '0', '100', '0', '0', '0', '31584', '0', 'Magistrate Barthilas');
INSERT INTO `creature_text` VALUES ('30994', '0', '2', 'Everyone is falling ill - this is an epidemic!', '12', '0', '100', '0', '0', '0', '31582', '0', 'Magistrate Barthilas');
INSERT INTO `creature_text` VALUES ('30994', '1', '0', 'I pray the illness I\'m feeling is due to stress ulcers...', '12', '0', '100', '0', '0', '0', '31587', '0', 'Magistrate Barthilas');
INSERT INTO `creature_text` VALUES ('30994', '1', '1', 'How can I possibly help the city in such a widespread crisis?', '12', '0', '100', '0', '0', '0', '31581', '0', 'Magistrate Barthilas');
INSERT INTO `creature_text` VALUES ('30994', '1', '2', 'I\'m at a loss. What can one simple man do in the face of disaster?', '12', '0', '100', '0', '0', '0', '31586', '0', 'Magistrate Barthilas');
INSERT INTO `creature_text` VALUES ('29369', '0', '0', 'Your mother was a wrench and your father smelled of seaforium!', '12', '0', '100', '0', '0', '0', '30238', '0', 'Stormforged Taskmaster');
INSERT INTO `creature_text` VALUES ('28917', '0', '0', 'Daddy!', '12', '0', '100', '0', '0', '0', '29007', '0', 'Yara');
INSERT INTO `creature_text` VALUES ('28579', '0', '0', '%s becomes enraged!', '16', '0', '100', '0', '0', '0', '24144', '0', 'Hardened Steel Berserker');
