INSERT INTO `creature_text` VALUES ('28486', '0', '1', 'M-mistress... may I p-polish your armor...?', '12', '0', '100', '0', '0', '0', '28097', '0', 'Squire Edwards');
INSERT INTO `creature_text` VALUES ('28486', '0', '2', 'M-mistress... did you n-need more corpse dust, p-perhaps...?', '12', '0', '100', '0', '0', '0', '28100', '0', 'Squire Edwards');
INSERT INTO `creature_text` VALUES ('28486', '1', '0', 'Yes, M-mistress...', '12', '0', '100', '0', '0', '0', '28101', '0', 'Squire Edwards');
