INSERT INTO `creature_text` VALUES ('19308', '0', '0', 'This war - and don\'t mistake this for anything but a war - is not going to win itself. If we do not fight, scratch, claw, and kick for every speck of land on this shattered world, all will be lost.', '12', '7', '100', '5', '0', '0', '16456', '0', 'Marshal Isildor');
