INSERT INTO `creature_text` VALUES ('8516', '1', '0', 'Okay, here we go.  It\'s going to take about five minutes to shut this thing down through the ritual.  Once I start, keep the vermin off of me or it will be the end of us all!', '12', '0', '100', '0', '0', '0', '4501', '0', 'Belnistrasz SAY_EVENT_START');
