INSERT INTO `creature_text` VALUES ('23224', '0', '35', 'Yes, No, Maybe so.', '15', '0', '100', '0', '0', '0', '21195', '0', '');
INSERT INTO `creature_text` VALUES ('23224', '0', '36', 'It won\'t matter, you\'ll be dead by tomorrow.', '15', '0', '100', '0', '0', '0', '21196', '0', '');
