INSERT INTO `creature_text` VALUES ('8421', '3', '0', 'It was the will of Ragnaros that Obsidion be built. Obsidion will destroy the Blackrock orcs of Blackrock Spire, uniting us with our bretheren in the fiery depths.', '12', '0', '100', '0', '0', '0', '4397', '0', 'Dorius');
