INSERT INTO `creature_text` VALUES ('8421', '1', '0', 'How easy it was to manipulate you into recovering the last Suntara stone from those imbeciles of the Twilight\'s Hammer.', '12', '0', '100', '0', '0', '0', '4395', '0', 'Dorius');
INSERT INTO `creature_text` VALUES ('27238', '0', '0', 'My beautiful mill. You! You\'ll die for this!', '12', '0', '100', '0', '0', '0', '26418', '0', 'Foreman Kaleiki');
