INSERT INTO `creature_text` VALUES ('27383', '1', '0', 'Our lord, Arthas saw in me the frailty of man.', '14', '0', '100', '0', '0', '0', '27159', '0', 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES ('27383', '2', '0', '\"Serve me in life and I will promise to rend the weakness from your soul. To erase it from existence!\" To be chosen... blessed by the Lich King in such a way.', '14', '0', '100', '0', '0', '0', '27160', '0', 'Thel zan the Duskbringer');
INSERT INTO `creature_text` VALUES ('27383', '3', '0', 'So serve him I did... Were it not for me the glorious dread citadel of Naxxramas may never have returned to Northrend.', '14', '0', '100', '0', '0', '0', '27161', '0', 'Thel zan the Duskbringer');
