INSERT INTO `creature_text` VALUES ('27713', '0', '1', 'Is this the end? If we stop this bastard now, is victory assured at the Wrathgate?', '12', '0', '100', '0', '0', '0', '27146', '0', '7th Legion Elite');
INSERT INTO `creature_text` VALUES ('27713', '0', '2', 'Let it be known that it has been an honor and a privilege fighting alongside all of you.', '12', '0', '100', '0', '0', '0', '27148', '0', '7th Legion Elite');
INSERT INTO `creature_text` VALUES ('27713', '0', '3', 'Death comes for us all eventually, but sometimes it comes a little faster than anticipated. Today we call that blind heroics... Tomorrow someone may call it stupidity.', '12', '0', '100', '0', '0', '0', '27150', '0', '7th Legion Elite');
INSERT INTO `creature_text` VALUES ('27713', '0', '4', 'Come on... Let\'s get this over with!', '12', '0', '100', '0', '0', '0', '27149', '0', '7th Legion Elite');
INSERT INTO `creature_text` VALUES ('27713', '0', '5', 'It looks like a gateway to hell!', '12', '0', '100', '0', '0', '0', '27144', '0', '7th Legion Elite');
INSERT INTO `creature_text` VALUES ('27713', '0', '6', 'I\'m getting a little worried. Where is this hero that is supposed to lead us into battle?', '12', '0', '100', '0', '0', '0', '27147', '0', '7th Legion Elite');
INSERT INTO `creature_text` VALUES ('27713', '0', '7', 'If I don\'t make it back to Wintergarde, let my family know that I did my best and that I love them...', '12', '0', '100', '0', '0', '0', '27145', '0', '7th Legion Elite');
INSERT INTO `creature_text` VALUES ('27713', '1', '0', 'It\'s the Highlord!', '12', '0', '100', '0', '0', '0', '27178', '0', '7th Legion Elite');
INSERT INTO `creature_text` VALUES ('27857', '0', '0', 'Steel yourselves, soldiers. $N has provided us us with the final piece of this puzzle. The dread lich, Thel\'zan, will soon come out of hiding, only to be rendered powerless against us!', '12', '0', '100', '0', '0', '0', '27155', '0', 'Legion Commander Yorik');
INSERT INTO `creature_text` VALUES ('27857', '1', '0', 'It is this phylactery, Thel\'zan\'s phylactery - that is the key to this victory!', '12', '0', '100', '0', '0', '0', '27156', '0', 'Legion Commander Yorik');
INSERT INTO `creature_text` VALUES ('27857', '2', '0', 'LEGION, STEADY YOURSELVES!', '12', '0', '100', '0', '0', '0', '27158', '0', 'Legion Commander Yorik');
INSERT INTO `creature_text` VALUES ('27858', '0', '0', 'Honor, courage, compassion and justice! Those were once the virtues you fought for as a cleric of the Argent Dawn, Thel\'zan.', '14', '0', '100', '0', '0', '0', '27173', '0', 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES ('27858', '1', '0', 'You dedicated your whole life to fighting the Scourge. You saw what they did to our beloved Lordaeron. The Lich King took everything from you!', '14', '0', '100', '0', '0', '0', '27174', '0', 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES ('27858', '2', '0', 'So how, then, did Father Inigo Montoy, bastion of virtue for the Argent Dawn, become the embodiment of that which he hated most?', '12', '0', '100', '0', '0', '0', '27175', '0', 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES ('27858', '3', '0', 'Now, be free and join the battle! Let us end this together!', '12', '0', '100', '0', '0', '0', '27182', '0', 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES ('27858', '4', '0', 'Victory for the Alliance!', '14', '0', '100', '0', '0', '0', '27180', '0', 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES ('27858', '5', '0', 'You have saved Wintergarde! With Thel\'zan\'s filth washed away, the 7th Legion will easily clean up the remaining Scourge. When you are done here, report to Angrathar!', '12', '0', '100', '0', '0', '0', '27181', '0', 'Highlord Bolvar Fordragon');
INSERT INTO `creature_text` VALUES ('32236', '1', '1', 'You\'ve got it all wrong. I\'m a subjugator!', '12', '0', '100', '0', '0', '0', '32636', '0', 'Dark Subjugator');
INSERT INTO `creature_text` VALUES ('32236', '1', '0', 'It was an accident. I was framed. Don\'t drop me!', '12', '0', '100', '0', '0', '0', '32637', '0', 'Dark Subjugator');
INSERT INTO `creature_text` VALUES ('32236', '0', '2', 'What?!', '12', '0', '100', '0', '0', '0', '32630', '0', 'Dark Subjugator');
INSERT INTO `creature_text` VALUES ('32236', '0', '1', 'Okay, who\'s the joker that threw an orb at me?', '12', '0', '100', '0', '0', '0', '32633', '0', 'Dark Subjugator');
INSERT INTO `creature_text` VALUES ('32236', '0', '0', 'I feel funny all of a sudden. Er?!', '12', '0', '100', '0', '0', '0', '32632', '0', 'Dark Subjugator');
INSERT INTO `creature_text` VALUES ('32236', '1', '2', 'NOOOOOO!', '12', '0', '100', '0', '0', '0', '32634', '0', 'Dark Subjugator');
INSERT INTO `creature_text` VALUES ('22911', '0', '0', 'You dare summon me?', '14', '0', '100', '15', '0', '0', '20773', '0', 'Vim\'gol the Vile');
INSERT INTO `creature_text` VALUES ('22911', '1', '0', 'Now me grow bigger and crush you!', '14', '0', '100', '0', '0', '0', '21264', '0', 'Vim\'gol the Vile');
INSERT INTO `creature_text` VALUES ('27247', '0', '0', 'Hey, who rang the bell?', '12', '0', '100', '0', '0', '0', '26610', '0', 'Devout Bodyguard');
INSERT INTO `creature_text` VALUES ('27439', '0', '0', 'I know a place nearby where we can speak in private, my child. Follow me.', '12', '0', '100', '1', '0', '0', '26629', '0', 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES ('27439', '1', '0', 'Did you think that I could not see through your flimsy disguise, $N?', '12', '0', '100', '6', '0', '0', '26631', '0', 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES ('27439', '2', '0', 'There is much that you do not understand, $R. The Master sees all.', '12', '0', '100', '1', '0', '0', '26632', '0', 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES ('27439', '3', '0', 'He told me that you would come for me. I won\'t die by your hand, though. I have seen what you have done to my compatriots.', '12', '0', '100', '25', '0', '0', '26633', '0', 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES ('27439', '4', '0', 'No. I will leave this world in a manner of my own choosing. And I will return, the grand admiral\'s will permitting!', '12', '0', '100', '274', '0', '0', '26634', '0', 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES ('27439', '5', '0', 'AAAEEEEIIIiiiiiiiiiiiiiiiiiiiiiiiiiiii........................................', '12', '0', '100', '0', '0', '2943', '26636', '0', 'High Abbot Landgren');
INSERT INTO `creature_text` VALUES ('23311', '0', '0', 'ARGH! BOOTERANG!', '12', '0', '100', '0', '0', '0', '21335', '0', 'Disobedient Dragonmaw Peon');
INSERT INTO `creature_text` VALUES ('23311', '0', '1', 'AYAYA! One day me have dat booterang...', '12', '0', '100', '0', '0', '0', '21336', '0', 'Disobedient Dragonmaw Peon');
INSERT INTO `creature_text` VALUES ('23311', '0', '2', 'HEY! No more booterang! Me sorry! Me work!', '12', '0', '100', '0', '0', '0', '21332', '0', 'Disobedient Dragonmaw Peon');
INSERT INTO `creature_text` VALUES ('23311', '0', '3', 'OOF! Booterang hurted me! Me tink work better den booterang!', '12', '0', '100', '0', '0', '0', '21333', '0', 'Disobedient Dragonmaw Peon');
INSERT INTO `creature_text` VALUES ('23311', '0', '4', 'OWWWW! Ok, ok, me go back to work!', '12', '0', '100', '0', '0', '0', '21331', '0', 'Disobedient Dragonmaw Peon');
INSERT INTO `creature_text` VALUES ('23311', '0', '5', 'WHY IT PUT DA BOOTERANG ON DA SKIN?? WHY??', '12', '0', '100', '0', '0', '0', '21334', '0', 'Disobedient Dragonmaw Peon');
INSERT INTO `creature_text` VALUES ('23311', '0', '6', 'You is bad orc... baaad... or... argh!', '12', '0', '100', '0', '0', '0', '21223', '0', 'Disobedient Dragonmaw Peon');
INSERT INTO `creature_text` VALUES ('27492', '1', '0', 'That\'s correct. My men report that the roads from here to the shore are held by the undead. You\'ll need to find an alternate route back to your ships.', '12', '0', '100', '378', '4000', '12724', '26712', '0', 'Alliance Emissary 1 - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES ('27492', '0', '0', 'By royal edict, you men are to return to Lordaeron immediately. Lord Uther has convinced the king to recall this expedition.', '12', '0', '100', '25', '3500', '12723', '26710', '0', 'Alliance Emissary 0 - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES ('27476', '2', '0', 'To hell with the undead! We\'ll cut our way through the woods, men!', '12', '0', '100', '22', '3000', '12721', '26713', '0', 'Valonforth 2  - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES ('27476', '3', '0', 'Well, milord, your father had our troops recalled at Lord Uther\'s request.', '12', '0', '100', '1', '3000', '12722', '26715', '0', 'Valonforth 3 - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES ('27476', '0', '0', 'I apologize, emissary, but the prince is away on an errand. Wha brings you to this desolate place?', '12', '0', '100', '1', '3000', '12719', '26709', '0', 'Valonforth 0  - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES ('27476', '1', '0', 'We\'re to just pick up and leave?', '12', '0', '100', '6', '1500', '12720', '26711', '0', 'Valonforth 1  - Q: The Truth Shall Set Us Free');
INSERT INTO `creature_text` VALUES ('26604', '0', '0', 'Shcourged trooolls roashting....', '12', '0', '100', '396', '0', '0', '27290', '0', 'Mack Fearsen');
INSERT INTO `creature_text` VALUES ('26604', '1', '0', '...on an open fire.....', '12', '0', '100', '396', '0', '0', '27291', '0', 'Mack Fearsen');
INSERT INTO `creature_text` VALUES ('26604', '2', '0', 'That\'s it! Far as I got... Whatd\'ya think?', '12', '0', '100', '396', '0', '0', '27292', '0', 'Mack Fearsen');
INSERT INTO `creature_text` VALUES ('21057', '0', '0', 'Who dares to interrupt my operation?', '14', '0', '100', '0', '0', '0', '18714', '0', 'Nexus-Prince Razaan');
INSERT INTO `creature_text` VALUES ('30381', '0', '0', 'And it very nearly didn\'t remember you. $N fought bravely against the North Wind to protect your story.', '12', '0', '100', '1', '0', '0', '32748', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '1', '0', 'Your battle with the wind was hundreds of years ago, Stormhoof. You weakened it so much that it has been silent all these years.', '12', '0', '100', '273', '0', '0', '32750', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('9537', '1', '0', 'You\'ll pay for that!', '12', '0', '100', '0', '0', '0', '4936', '0', 'Hurley Blackbreath');
INSERT INTO `creature_text` VALUES ('9537', '0', '0', 'Get away from those kegs!', '14', '0', '100', '0', '0', '0', '4934', '0', 'Hurley Blackbreath');
INSERT INTO `creature_text` VALUES ('36856', '0', '0', 'You\'re in luck. I\'ve got just what you need in the load I\'m about to wash.', '12', '0', '100', '0', '0', '0', '36761', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '1', '0', 'See the piles of laundry and the bucket of water? I\'ll run out what I need next, and you put it in the tub. Ready?', '12', '0', '100', '0', '0', '0', '36762', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '2', '0', 'Quick, add some shirts to the laundry!', '12', '0', '100', '0', '0', '0', '36788', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '3', '0', 'Add the unmentionables... uh, I mean, the \'delicates\'!', '12', '0', '100', '25', '0', '0', '36789', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '4', '0', 'The tub needs more water!', '12', '0', '100', '25', '0', '0', '36790', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '5', '0', 'Toss some pants in to the tub!', '12', '0', '100', '25', '0', '0', '36787', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '6', '0', 'I should keep you around.', '12', '0', '17', '0', '0', '0', '37989', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '6', '1', 'Well done!', '12', '0', '17', '0', '0', '0', '36764', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '6', '2', 'That\'s how it\'s done!', '12', '0', '17', '0', '0', '0', '37986', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '6', '3', 'Clean and tidy!', '12', '0', '17', '0', '0', '0', '37985', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '6', '4', 'Nice. I don\'t want to know what that stain was.', '12', '0', '17', '0', '0', '0', '37987', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '6', '5', 'Aquanos can hardly keep up!', '12', '0', '15', '0', '0', '0', '37988', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '7', '0', 'Oh, no! That wasn\'t right. Now I\'ll have to go get more detergent so we can start over!', '12', '0', '100', '0', '0', '0', '36763', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '8', '0', 'Aquanos, stop sending the clothes so high! You didn\'t have to see the look on Aethas Sunreaver\'s face when he found his pants in the fountain!', '12', '0', '100', '0', '0', '0', '36817', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('36856', '9', '0', 'See how easy that was with everyone working together? Just take what you need from the clean laundry here, but don\'t forget to return it when you\'re finished.', '12', '0', '100', '0', '0', '0', '36791', '0', 'Shandy Glossgleam');
INSERT INTO `creature_text` VALUES ('10262', '0', '6', 'Bijou lost! SQUAWK! *cough', '12', '0', '100', '0', '0', '0', '5541', '0', 'Opus - Random Say');
INSERT INTO `creature_text` VALUES ('10262', '0', '7', 'Dragons controlling orcs!? SQUAWK!', '12', '0', '100', '0', '0', '0', '5546', '0', 'Opus - Random Say');
INSERT INTO `creature_text` VALUES ('29614', '0', '0', 'Even in death there will be no escape for you!', '12', '0', '100', '0', '0', '0', '30267', '0', 'Onslaught Darkweaver');
INSERT INTO `creature_text` VALUES ('29614', '0', '1', 'I shall feast upon your soul!', '12', '0', '100', '0', '0', '0', '30268', '0', 'Onslaught Darkweaver');
INSERT INTO `creature_text` VALUES ('29614', '0', '2', 'Now you know. Now you must die!', '12', '0', '100', '0', '0', '0', '30266', '0', 'Onslaught Darkweaver');
INSERT INTO `creature_text` VALUES ('29614', '0', '3', 'You will not leave this cavern alive!', '12', '0', '100', '0', '0', '0', '30269', '0', 'Onslaught Darkweaver');
INSERT INTO `creature_text` VALUES ('29614', '1', '0', 'The darkweaver pierces you with an evil grin as he shifts into an entirely different form.', '16', '0', '100', '0', '0', '0', '30265', '0', 'Onslaught Darkweaver');
INSERT INTO `creature_text` VALUES ('2755', '0', '0', 'What?  $N, you served me well, but...', '12', '0', '0', '0', '0', '0', '842', '0', 'Myzrael');
INSERT INTO `creature_text` VALUES ('2755', '1', '0', 'Why have you summoned me so soon?  I haven\'t yet reached my full power!', '12', '0', '0', '0', '0', '0', '843', '0', 'Myzrael');
INSERT INTO `creature_text` VALUES ('2755', '2', '0', 'No matter.  You were a fool to help me, and now you will pay!', '12', '0', '0', '0', '0', '0', '844', '0', 'Myzrael');
INSERT INTO `creature_text` VALUES ('6492', '0', '0', '%s is angered and attacks!', '16', '0', '100', '0', '0', '0', '3074', '0', 'Rift Spawn');
INSERT INTO `creature_text` VALUES ('6492', '1', '0', '%s escapes into the void!', '16', '0', '100', '0', '0', '0', '2564', '0', 'Rift Spawn');
INSERT INTO `creature_text` VALUES ('3287', '0', '0', 'Go swiftly, $N. My fate is in your hands.', '12', '1', '100', '0', '0', '0', '1341', '0', 'Hana zua');
INSERT INTO `creature_text` VALUES ('1402', '0', '3', 'It\'s all their fault, stupid orcs. Had to burn my farm to the ground.', '12', '7', '100', '0', '0', '0', '187', '0', 'Topper McNabb');
INSERT INTO `creature_text` VALUES ('1402', '0', '4', 'Spare some change for a poor blind man? ...What do you mean I\'m not blind? ...I\'M NOT BLIND! I CAN SEE!! It\'s a miracle!', '12', '7', '100', '0', '0', '0', '184', '0', 'Topper McNabb');
INSERT INTO `creature_text` VALUES ('1402', '0', '5', 'Alms for the poor?', '12', '7', '100', '0', '0', '0', '181', '0', 'Topper McNabb');
INSERT INTO `creature_text` VALUES ('1402', '0', '6', 'I will gladly pay you Tuesday for a hamburger today.', '12', '7', '100', '0', '0', '0', '182', '0', 'Topper McNabb');
INSERT INTO `creature_text` VALUES ('1402', '0', '7', 'It\'s all their fault, stupid Alliance army. Just had to build their towers right behind my farm.', '12', '7', '100', '0', '0', '0', '186', '0', 'Topper McNabb');
INSERT INTO `creature_text` VALUES ('5734', '0', '0', 'Hmm, it would seem Keever needs a new subject. If that fool Abernathy keeps taking Keever\'s subjects, Keever may have to have a word with him.', '12', '33', '100', '1', '0', '0', '2061', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '1', '0', 'Ahh, there we go. Now, Keever must try this vial and see if it works.', '12', '33', '100', '1', '0', '0', '2062', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '2', '0', '%s forces the fluid down the weary man\'s throat.', '16', '0', '100', '0', '0', '0', '2075', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '3', '0', '%s pokes the small toad.', '16', '0', '100', '0', '0', '0', '2064', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '4', '0', 'Not what Keever was hoping for. Keever may have added too much earthroot. Let\'s see if the second serum will do what Keever needs.', '12', '33', '100', '1', '0', '0', '2063', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '5', '0', '%s feeds the toad some of the strange liquid.', '16', '0', '100', '0', '0', '0', '2065', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '6', '0', '%s pokes the small fuzzy squirrel with obvious disappointment.', '16', '0', '100', '0', '0', '0', '2066', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '7', '0', 'Well, that is just not right. The creature is far too small. Let us see what Keever\'s third batch will do.', '12', '33', '100', '1', '0', '0', '2067', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '8', '0', '%s feeds the squirrel some of the viscous fluid.', '16', '0', '100', '0', '0', '0', '2068', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '9', '0', '%s pokes the skittish rabbit.', '16', '0', '100', '0', '0', '0', '2069', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '10', '0', 'Keever is unhappy with this. Perhaps if Keever were to try a larger dose, that may fix this dilemma.', '12', '33', '100', '1', '0', '0', '2070', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '11', '0', '%s grabs the rabbit and pours the fluid down its throat, then sets it back inside the cage.', '16', '0', '100', '0', '0', '0', '2071', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '12', '0', 'What is this? Did Keever ask for a sheep? Keever wanted a weapon of great power and all he got was this sheep. Keever is very disappointed.', '12', '33', '100', '1', '0', '0', '2072', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '13', '0', '%s pokes the wooly sheep repeatedly.', '16', '0', '100', '0', '0', '0', '2073', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('5734', '14', '0', 'Keever is most pleased.', '12', '33', '100', '11', '0', '0', '2074', '0', 'Apothecary Keever <Royal Apothecary Society>');
INSERT INTO `creature_text` VALUES ('36217', '0', '0', 'I\'ve got my eye on you, Faranell.', '12', '1', '100', '1', '0', '0', '36299', '0', 'Overseer Kraggosh');
INSERT INTO `creature_text` VALUES ('36217', '1', '0', 'No abominations protecting you. No secrecy. No plotting in the dark.', '12', '1', '100', '0', '0', '0', '36300', '0', 'Overseer Kraggosh');
INSERT INTO `creature_text` VALUES ('36217', '2', '0', 'If you so much as spit without my permission, this place shuts down. Forcefully.', '12', '1', '100', '0', '0', '0', '36301', '0', 'Overseer Kraggosh');
INSERT INTO `creature_text` VALUES ('2055', '0', '0', 'I assure you, overseer, the Royal Apothecary Society dearly wishes to make up for the tragic misguidance which ended so many lives.', '12', '1', '100', '0', '0', '0', '36302', '0', 'Master Apothecary Faranell ');
INSERT INTO `creature_text` VALUES ('2055', '1', '0', 'We will cause you no trouble. We seek only to continue our research in peace.', '12', '1', '100', '0', '0', '0', '36303', '0', 'Master Apothecary Faranell ');
INSERT INTO `creature_text` VALUES ('36217', '3', '0', 'We\'ll see about that.', '12', '1', '100', '1', '0', '0', '36304', '0', 'Overseer Kraggosh');
INSERT INTO `creature_text` VALUES ('36217', '4', '0', 'Get that rotten nut to shut his trap!', '12', '1', '100', '5', '0', '0', '36305', '0', 'Overseer Kraggosh');
INSERT INTO `creature_text` VALUES ('36213', '0', '0', 'Shut your face, you gibbering idiot!', '12', '1', '100', '0', '0', '0', '36306', '0', 'Kor\'kron Overseer');
INSERT INTO `creature_text` VALUES ('5662', '0', '0', 'You sorry sacks of maggot flesh can do better than that, now prove it!', '12', '33', '100', '25', '0', '0', '1969', '0', 'Sergeant Houser');
INSERT INTO `creature_text` VALUES ('5662', '0', '1', 'Alright you sorry sacks of bones. Let\'s see what you\'ve got. Strike your opponent, and don\'t stop till I say so.', '12', '33', '100', '1', '0', '0', '1963', '0', 'Sergeant Houser');
INSERT INTO `creature_text` VALUES ('5662', '0', '2', 'What do you call that? If that\'s fighting then I\'m Lothar himself!', '12', '33', '100', '25', '0', '0', '1971', '0', 'Sergeant Houser');
INSERT INTO `creature_text` VALUES ('5662', '0', '3', 'These piles of wood and straw may show mercy, but the Lich King won\'t!', '12', '33', '100', '25', '0', '0', '1968', '0', 'Sergeant Houser');
INSERT INTO `creature_text` VALUES ('5662', '0', '4', 'The sword isn\'t a tool, it\'s an extension of you. I expect better!', '12', '33', '100', '25', '0', '0', '1970', '0', 'Sergeant Houser');
INSERT INTO `creature_text` VALUES ('5662', '0', '5', 'Stop! You maggots call that fighting? That is pathetic! Now put your hearts in it, you don\'t need them anymore anyway!', '12', '33', '100', '25', '0', '0', '1964', '0', 'Sergeant Houser');
INSERT INTO `creature_text` VALUES ('5662', '0', '6', 'You\'re dead, not buried. Now show me your best!', '12', '33', '100', '25', '0', '0', '1967', '0', 'Sergeant Houser');
INSERT INTO `creature_text` VALUES ('5662', '0', '7', 'You don\'t fight for yourselves anymore, maggots! You fight for the Queen now so show me something worthy of her!', '12', '33', '100', '25', '0', '0', '1966', '0', 'Sergeant Houser');
INSERT INTO `creature_text` VALUES ('5700', '0', '0', 'If I could just find \"Magic and the Ways of Power\". Ahh here it is, what was it doing over here?', '12', '33', '100', '0', '0', '0', '2034', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '1', 'Very interesting. But if that\'s the case then my theory is wrong. I must reread the other sections to see what I missed.', '12', '33', '100', '1', '0', '0', '2040', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '2', 'Magical Flows and How They Effect the World\", by Maginor Dumas. That\'s the one I was looking for.', '12', '33', '100', '0', '0', '0', '2030', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '3', 'If this is correct then I have a lot more research to do.', '12', '33', '100', '1', '0', '0', '2042', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '4', 'Ahh there it is.', '12', '33', '100', '0', '0', '0', '2029', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '5', 'I really need a better system for filing these books. Ahh that\'s the one I wanted.', '12', '33', '100', '0', '0', '0', '2031', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '6', 'But this can\'t be right. Where did I put that other book?', '12', '33', '100', '1', '0', '0', '2043', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '7', '\"Khadgar\'s Mystical Journal, Volume 8.\" I think this is the one I wanted.', '12', '33', '100', '0', '0', '0', '2032', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '8', 'Ahh, \"Mystical Conjurings of the Archmages of Dalaran.\" Perfect.', '12', '33', '100', '0', '0', '0', '2033', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '9', 'If I could just find \"Magic and the Ways of Power\". Ahh here it is, what was it doing over here?', '12', '33', '100', '0', '0', '0', '2034', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '10', '\"Magic Maladies\", no. \"Magic Matricies\", no. Ahh here we go \"Magic Mysteries\".', '12', '33', '100', '0', '0', '0', '2035', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '11', 'There\'s the mystical tome I was looking for.', '12', '33', '100', '0', '0', '0', '2036', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '12', 'Yes, yes. Hmm, it seems to cross reference another tome. Guess I have to find that one too.', '12', '33', '100', '0', '0', '0', '2037', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '13', 'That wasn\'t very helpful. Let me check the other one.', '12', '33', '100', '0', '0', '0', '2038', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '14', 'That\'s what I wanted to know. Now I just need to find where the flux variance is discussed.', '12', '33', '100', '0', '0', '0', '2039', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5700', '0', '15', 'So I was right about the energies. Let\'s see what the other tome has to say on the subject.', '12', '33', '100', '0', '0', '0', '2041', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5696', '0', '0', 'Theresa, take this to Father Lankester.', '12', '33', '100', '1', '0', '0', '1995', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5696', '1', '0', 'Why thank you, my pet.', '12', '33', '100', '1', '0', '0', '2011', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5696', '2', '0', 'As good as I told you it would be, yes?', '12', '33', '100', '1', '0', '0', '2003', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5696', '2', '1', 'Didn\'t I tell you my new domination techniques would work?', '12', '33', '100', '1', '0', '0', '2002', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5696', '2', '2', 'So what do you think of my new toy?', '12', '33', '100', '1', '0', '0', '1994', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5696', '2', '3', 'How do you like my pet?', '12', '33', '100', '1', '0', '0', '2000', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5696', '3', '0', 'A little torture, a pinch of magic, and an ample helping of invasive surgery. She was conscious, of course.', '12', '33', '100', '1', '0', '0', '2007', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5696', '3', '1', 'Everyone has a weakness, it\'s just a matter of finding it.', '12', '33', '100', '1', '0', '0', '2008', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5696', '3', '2', 'It was simple once I broke her spirit.', '12', '33', '100', '1', '0', '0', '2004', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5696', '3', '3', 'I managed to discover that certain parts of the brain when removed or stimulated will make a subject much more docile.', '12', '33', '100', '1', '0', '0', '2009', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5696', '3', '4', 'You don\'t expect me to give up all my secrets do you?', '12', '33', '100', '1', '0', '0', '2005', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5696', '3', '5', 'A little ritual torture can go a long way.', '12', '33', '100', '1', '0', '0', '2006', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5697', '0', '0', 'Yes My Lord.', '12', '33', '100', '0', '0', '0', '1998', '0', 'Theresa');
INSERT INTO `creature_text` VALUES ('5697', '1', '0', 'My Lord Abernathy sends this to you.', '12', '33', '100', '0', '0', '0', '1997', '0', 'Theresa');
INSERT INTO `creature_text` VALUES ('5697', '2', '0', 'My Lord. From Father Lankester.', '12', '33', '100', '0', '0', '0', '1999', '0', 'Theresa');
INSERT INTO `creature_text` VALUES ('5698', '0', '0', 'Bravo, Gerard, you have exceeded yourself, yet again.', '12', '33', '100', '1', '0', '0', '2018', '0', 'Joanna Whitehall');
INSERT INTO `creature_text` VALUES ('5698', '0', '1', 'I am truly impressed, Gerard. I thought you were all talk, but I see I have been proven wrong.', '12', '33', '100', '1', '0', '0', '2019', '0', 'Joanna Whitehall');
INSERT INTO `creature_text` VALUES ('5698', '0', '2', 'I don\'t agree with your turning to alchemy to fix some of the problems, but I do agree with the results. Very well done.', '12', '33', '100', '0', '0', '0', '2024', '0', 'Joanna Whitehall');
INSERT INTO `creature_text` VALUES ('5698', '0', '3', 'Did you have to house train her, Gerard?', '12', '33', '100', '1', '0', '0', '2027', '0', 'Joanna Whitehall');
INSERT INTO `creature_text` VALUES ('5698', '0', '4', 'An amazing discovery you have made. I would love an opportunity to look at all of the failed experiments.', '12', '33', '100', '0', '0', '0', '2023', '0', 'Joanna Whitehall');
INSERT INTO `creature_text` VALUES ('5699', '0', '0', 'I\'ve never seen anything like it. No concentration or loss of awareness while the subject is dominated. What did you do to her?', '12', '33', '100', '1', '0', '0', '2017', '0', 'Leona Tharpe');
INSERT INTO `creature_text` VALUES ('5699', '0', '1', 'Amazing! How did you manage a permanent dominate?', '12', '33', '100', '1', '0', '0', '2013', '0', 'Leona Tharpe');
INSERT INTO `creature_text` VALUES ('5699', '0', '2', 'I don\'t approve of your experiments, Gerard, but I must admit the results are impressive. What exactly did you do to her?', '12', '33', '100', '1', '0', '0', '2014', '0', 'Leona Tharpe');
INSERT INTO `creature_text` VALUES ('5699', '0', '3', 'She retains knowledge and speech, and even the self sufficiency to perform tasks for you. Precisely what did you do?', '12', '33', '100', '1', '0', '0', '2016', '0', 'Leona Tharpe');
INSERT INTO `creature_text` VALUES ('5699', '0', '4', 'Truly amazing, Gerard. How did you do it?', '12', '33', '100', '1', '0', '0', '2012', '0', 'Leona Tharpe');
INSERT INTO `creature_text` VALUES ('4607', '0', '0', 'Ugh! What is that scoundrel doing sending his vermin to me? Get out of my sight before I take what is left of your life. And take this with you.', '12', '33', '100', '1', '0', '0', '2028', '0', 'Father Lankester');
INSERT INTO `creature_text` VALUES ('5661', '0', '0', 'Edward. Tyler. Prepare for your first challenge.', '12', '33', '100', '1', '0', '0', '1978', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('5661', '1', '0', 'Lysta, summon in the captives.', '12', '33', '100', '0', '0', '0', '1977', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('5661', '2', '0', 'Not a challenge at all it seems. Let us see how you handle your second test. Lysta, bring forth the minions of the Lich King.', '12', '33', '100', '0', '0', '0', '1980', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('5661', '3', '0', 'Lysta, summon in undead captives.', '12', '33', '100', '0', '0', '0', '8306', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('5661', '4', '0', 'It is time to face your final challenge young warriors! Prepare for your hardest fight yet.', '12', '33', '100', '0', '0', '0', '1984', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('5661', '5', '0', 'Lysta, summon forth... the abomination!', '12', '33', '100', '0', '0', '0', '8307', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('5661', '6', '0', 'Well done Edward and Tyler. You are progressing along in your training quite nicely. We shall test your mettle again soon.', '12', '33', '100', '0', '0', '0', '1985', '0', 'Brother Malach');
INSERT INTO `creature_text` VALUES ('18303', '0', '0', 'My brothers and sisters. Listen to me. We must speak of the survival of our people. We have narrowly survived one calamity, but I fear the next shall destroy us.', '12', '0', '100', '1', '0', '0', '15673', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18303', '1', '0', 'Can you not see what is happening? Open your eyes. It is laid bare for all to see. What is this new alliance? Why have we betrayed our allies of old, only to take up with the enemies of all that live?', '12', '0', '100', '1', '0', '0', '15674', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18668', '0', '0', 'No, you\'re wrong! We were the ones betrayed!', '12', '0', '100', '22', '0', '0', '15675', '0', 'Vaeron Kormar');
INSERT INTO `creature_text` VALUES ('18669', '0', '0', 'When have the dwarves ever been our friends?', '12', '0', '100', '11', '0', '0', '15676', '0', 'Terric Brightwind');
INSERT INTO `creature_text` VALUES ('18724', '0', '0', 'The humans abandoned us in our darkest hour! Their prince himself led the assault on our lands.', '12', '0', '100', '25', '0', '0', '15677', '0', 'Melaya Tassier');
INSERT INTO `creature_text` VALUES ('18671', '0', '0', 'You fool! The prince\'s minions were the very Forsaken we have now allied ourselves with, and now he has betrayed us!', '12', '0', '100', '5', '0', '0', '15678', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18668', '1', '0', 'The Ranger-General of Silvermoon will be a loyal ally!', '12', '0', '100', '1', '0', '0', '15679', '0', 'Vaeron Kormar');
INSERT INTO `creature_text` VALUES ('18303', '2', '0', 'You speak of her as friend? You are a fool. This \"Dark Lady\" is not Sylvanas.', '12', '0', '100', '1', '0', '0', '15680', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18668', '2', '0', 'Blood will be true. We can trust in none but our own!', '12', '0', '100', '25', '0', '0', '15738', '0', 'Vaeron Kormar');
INSERT INTO `creature_text` VALUES ('18303', '3', '0', 'Blood? And these orcs, these fair weather friends? They will aid us?', '12', '0', '100', '11', '0', '0', '15743', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18671', '1', '0', 'A great blight lies across our land! It grows every day. Our need for magic weakens us, and will bring us to destruction, just as it has before!', '12', '0', '100', '25', '0', '0', '15744', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18669', '1', '0', 'How are we to live without magic?', '12', '0', '100', '5', '0', '0', '15745', '0', 'Terric Brightwind');
INSERT INTO `creature_text` VALUES ('18724', '1', '0', 'Look at the wonders of this city! This is magic.', '12', '0', '100', '5', '0', '0', '15747', '0', 'Melaya Tassier');
INSERT INTO `creature_text` VALUES ('18671', '2', '0', 'But at what cost? You\'ve all been blinded. We have all been blinded.', '12', '0', '100', '1', '0', '0', '15748', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18668', '3', '0', 'You call us traitors?', '12', '0', '100', '25', '0', '0', '15749', '0', 'Vaeron Kormar');
INSERT INTO `creature_text` VALUES ('18303', '4', '0', 'The magisters are the traitors! They have sealed our doom.', '12', '0', '100', '25', '0', '0', '15750', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18669', '2', '0', 'Idealistic blather. Go back to your forests!', '12', '0', '100', '3', '0', '0', '15751', '0', 'Terric Brightwind');
INSERT INTO `creature_text` VALUES ('18724', '2', '0', 'You would have us live like the savages!', '12', '0', '100', '5', '0', '0', '15752', '0', 'Melaya Tassier');
INSERT INTO `creature_text` VALUES ('18671', '3', '0', 'No, not savagery! Conscience. The excesses of our people will destroy us, and all that we have struggled to build.', '12', '0', '100', '1', '0', '0', '15753', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18303', '5', '0', 'We have not struggled so hard to survive, only to bring about the true destruction.', '12', '0', '100', '1', '0', '0', '15754', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18671', '4', '0', 'We stand on the brink of destruction. We must reconsider this rash course of action.', '12', '0', '100', '5', '0', '0', '15756', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18303', '6', '0', 'If we speak as one, we cannot be ignored!', '12', '0', '100', '1', '0', '0', '15917', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18671', '5', '0', 'Lend us your voices! Now is the time we must act to save our people.', '12', '0', '100', '1', '0', '0', '15918', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18671', '6', '0', 'Lor\'themar must hear us! We will not be silenced!', '12', '0', '100', '5', '0', '0', '15919', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18337', '0', '0', 'That should take care of that. Summon me if you have further need of my services.', '12', '0', '100', '11', '0', '0', '15922', '0', 'Priest Kath\'mar');
INSERT INTO `creature_text` VALUES ('18303', '7', '0', 'We will repay the betrayal of the Alliance! We shall never forget, and never forgive!', '12', '1', '100', '21', '0', '0', '15930', '0', 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES ('18671', '7', '0', 'Lor\'themar has remade Silvermoon into something we may truly be proud of.', '12', '1', '100', '21', '0', '0', '15924', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('18671', '8', '0', 'The magisters are wise and will plot a prosperous course for our future.', '12', '1', '100', '21', '0', '0', '15926', '0', 'Priest Ennas');
INSERT INTO `creature_text` VALUES ('5700', '0', '16', 'Back to the drawing board.', '12', '33', '100', '1', '0', '0', '2044', '0', 'Samantha Shackleton');
INSERT INTO `creature_text` VALUES ('5696', '2', '4', 'Isn\'t she just the most amazing little creation?', '12', '33', '100', '1', '0', '0', '2001', '0', 'Gerard Abernathy');
INSERT INTO `creature_text` VALUES ('5698', '0', '6', 'Very nice, Gerard. Next you will be breeding them.', '12', '33', '100', '1', '0', '0', '2022', '0', 'Joanna Whitehall');
INSERT INTO `creature_text` VALUES ('5698', '0', '7', 'Your pet is wonderful, Gerard, but she needs a collar with a bell I think.', '12', '33', '100', '1', '0', '0', '2026', '0', 'Joanna Whitehall');
INSERT INTO `creature_text` VALUES ('18896', '0', '0', 'Function: Intelligence Agents, Torturers', '12', '7', '100', '0', '0', '0', '17390', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '1', '0', 'The cruel sayaadi temptresses exemplify the subtler side of the Legion\'s nihilistic Crusade.', '12', '7', '100', '0', '0', '0', '17399', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '2', '0', 'They revel in causing anguish and pain - and their murderous interrogations are the stuff of nightmares.', '12', '7', '100', '0', '0', '0', '17811', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '3', '0', 'Felguard', '12', '7', '100', '0', '0', '0', '16006', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '4', '0', 'Species: Mo\'arg', '12', '7', '100', '0', '0', '0', '17377', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '5', '0', 'Affiliation: Burning Legion', '12', '7', '100', '0', '0', '0', '17368', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '6', '0', 'Function: Standard Soldiers', '12', '7', '100', '0', '0', '0', '17397', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '7', '0', 'Conscripted from the mo\'arg race, these warriors serve as the Legion\'s rank and file soldiers.', '12', '7', '100', '0', '0', '0', '17406', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '8', '0', 'They are fearless and unwaveringly loyal to their master\'s godlike will.', '12', '7', '100', '0', '0', '0', '18308', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '9', '0', 'Doomguard', '12', '7', '100', '0', '0', '0', '15997', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '10', '0', 'Species: Ered\'ruin', '12', '7', '100', '0', '0', '0', '17367', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '11', '0', 'Function: Shock Troops, Enforcers', '12', '7', '100', '0', '0', '0', '17369', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '12', '0', 'Powerful and deliberate, these winged aberrations are renowned across the cosmos for their brutality.', '12', '7', '100', '0', '0', '0', '17370', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '13', '0', 'The Doomguard are utterly devoted to their Pit Lord masters and revel in the destruction of worlds.', '12', '7', '100', '0', '0', '0', '18311', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '14', '0', 'Wrathguard', '12', '7', '100', '0', '0', '0', '15998', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '15', '0', 'Species: Eredar', '12', '7', '100', '0', '0', '0', '17371', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '16', '0', 'Function: Honor Guard', '12', '7', '100', '0', '0', '0', '17389', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '17', '0', 'Cunning and precise, the Wrathguard often protect the Legion\'s eredar masters.', '12', '7', '100', '0', '0', '0', '17398', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '18', '0', 'Their skill in battle should never be underestimated.', '12', '7', '100', '0', '0', '0', '17810', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '19', '0', 'Succubus', '12', '7', '100', '0', '0', '0', '15999', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '20', '0', 'Species: Sayaad', '12', '7', '100', '0', '0', '0', '17372', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '21', '0', 'Shivarra', '12', '7', '100', '0', '0', '0', '16000', '0', 'Exodar Holographic Emitter');
INSERT INTO `creature_text` VALUES ('18896', '22', '0', 'Species: Shivarra', '12', '7', '100', '0', '0', '0', '17373', '0', 'Exodar Holographic Emitter');
