INSERT INTO `creature_text` VALUES ('18896', '24', '0', 'Devout and exceedingly charismatic, these priestesses live only to evangelize the power of Sargeras\' dread vision.', '12', '7', '100', '0', '0', '0', '17400', '0', 'Exodar Holographic Emitter');
