INSERT INTO `creature_text` VALUES ('17240', '1', '0', 'Wow! Really?', '12', '7', '100', '5', '0', '0', '13702', '0', 'Admiral Odesyus');
INSERT INTO `creature_text` VALUES ('17240', '2', '0', 'I hate you so much, Cookie.', '12', '7', '100', '1', '0', '0', '13705', '0', 'Admiral Odesyus');
INSERT INTO `creature_text` VALUES ('17242', '0', '0', 'Of course! Anything for you, darling!', '12', '7', '100', '5', '0', '0', '13712', '0', 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES ('17242', '1', '0', 'Alright, let me take a look at that tablet.', '12', '7', '100', '1', '0', '0', '13713', '0', 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES ('17242', '2', '0', '%s slowly reads the tablet, mouthing the words in their native tongue.', '16', '7', '100', '0', '0', '0', '13714', '0', 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES ('17242', '3', '0', 'Naga warlord! Oh dear...', '12', '7', '100', '5', '0', '0', '13715', '0', 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES ('17242', '4', '0', 'Well, you aren\'t going to like what I have to tell you, young missy. This tablet is a naga communication. Their leader, Warlord Sriss\'tiz, has ordered his naga to take the rest of the island by force.', '12', '7', '100', '1', '0', '0', '13716', '0', 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES ('17242', '5', '0', 'And it looks like he\'s got reinforcements comin\' from Nazjatar!', '12', '7', '100', '5', '0', '0', '13717', '0', 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES ('17242', '6', '0', 'We\'ve got to do something about this!', '12', '0', '100', '5', '0', '0', '13718', '0', 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES ('17241', '0', '0', 'Adamant, could you please take a look at this tablet and try to decipher the runes?', '12', '7', '100', '1', '0', '0', '13711', '0', 'Priestess Kyleen Il\'dinare');
INSERT INTO `creature_text` VALUES ('17241', '1', '0', 'Thank you, Adamant. I think I know just the person for the job.', '12', '7', '100', '2', '0', '0', '13719', '0', 'Priestess Kyleen Il\'dinare');
INSERT INTO `creature_text` VALUES ('17214', '0', '0', 'Now to apply the ointment to her wounds.', '12', '7', '100', '1', '0', '0', '13596', '0', 'Anchorite Fateema');
INSERT INTO `creature_text` VALUES ('17214', '1', '0', 'It appears that the ointment is ineffective. Such a pity...', '12', '7', '100', '1', '0', '0', '13597', '0', 'Anchorite Fateema');
INSERT INTO `creature_text` VALUES ('17214', '2', '0', 'Man\'ari... But how... We are not eredar! She must know! If there are others like her, we must find them and explain...', '12', '7', '100', '1', '0', '0', '13608', '0', 'Anchorite Fateema');
INSERT INTO `creature_text` VALUES ('17215', '0', '0', 'There may still be a solution. Come here, $N. I will explain what I need.', '12', '7', '100', '1', '0', '0', '13598', '0', 'Daedal0');
INSERT INTO `creature_text` VALUES ('17215', '1', '0', 'This should do it...', '12', '7', '100', '0', '0', '0', '13599', '0', 'Daedal');
INSERT INTO `creature_text` VALUES ('17215', '2', '0', 'Wake up little creature. Wake up from your slumber... You are safe now.', '12', '7', '100', '0', '0', '0', '13600', '0', 'Daedal');
INSERT INTO `creature_text` VALUES ('17117', '0', '0', '%s groans.', '16', '7', '100', '0', '0', '0', '13601', '0', 'Injured Night Elf Priestess');
INSERT INTO `creature_text` VALUES ('17117', '1', '0', '%s sits up.', '16', '7', '100', '0', '0', '0', '13602', '0', 'Injured Night Elf Priestess');
INSERT INTO `creature_text` VALUES ('17117', '2', '0', 'Whe... Where am I? What... What is this place?', '12', '7', '100', '6', '0', '0', '13603', '0', 'Injured Night Elf Priestess');
INSERT INTO `creature_text` VALUES ('17117', '3', '0', 'BY THE LIGHT OF ELUNE! Eredar! Cursed demons, what have you done to me? I have not come alone, we will fight you as we have in the past! I...', '12', '7', '100', '5', '0', '0', '13604', '0', 'Injured Night Elf Priestess');
INSERT INTO `creature_text` VALUES ('17117', '4', '0', '%s falls back into a coma.', '16', '0', '100', '0', '0', '0', '13605', '0', 'Injured Night Elf Priestess');
INSERT INTO `creature_text` VALUES ('17375', '0', '3', '[Furbolg] The prophecy is true!', '12', '0', '100', '0', '0', '0', '13980', '0', 'Stillpine Captive');
INSERT INTO `creature_text` VALUES ('17375', '0', '4', '%s growls at its captors.', '16', '0', '100', '0', '0', '0', '13977', '0', 'Stillpine Captive');
INSERT INTO `creature_text` VALUES ('4721', '0', '0', 'The Earth Mother watch over us; under cover of the night as in the day. Let this fire illuminate both our bodies and spirits and remind us of our honored ancestors, who are ever present and guiding our way.', '12', '3', '100', '1', '0', '0', '8677', '0', 'Zangen Stonehoof');
INSERT INTO `creature_text` VALUES ('17702', '0', '0', 'Face the wrath of Bristlelimb!', '14', '0', '100', '0', '0', '0', '14322', '0', 'High Chief Bristlelimb');
INSERT INTO `creature_text` VALUES ('24277', '0', '0', 'Enough of this charade!', '12', '0', '100', '0', '0', '0', '23064', '0', 'Garwal');
INSERT INTO `creature_text` VALUES ('8696', '0', '0', 'Thank again, $N.  Now I\'ll just wait here until it\'s safe to leave.', '12', '0', '100', '0', '0', '0', '4575', '0', 'Henry Stern');
INSERT INTO `creature_text` VALUES ('28355', '2', '0', 'I\'m working on it, I just don\'t want to sell it until I\'m happy with it. It shouldn\'t be long.', '12', '0', '100', '0', '0', '0', '27808', '0', 'Wright Williams');
INSERT INTO `creature_text` VALUES ('28355', '3', '0', 'Sir, our customers are complaining that there\'s too much Deathweed in our Anesthetics.', '12', '0', '100', '0', '0', '0', '27802', '0', 'Wright Williams');
INSERT INTO `creature_text` VALUES ('28347', '0', '0', 'Wright, have you finished that new concoction you\'ve been working on? I\'m tired of these spores filling up my shop!', '12', '0', '100', '0', '0', '0', '27804', '0', 'Miles Sidney');
INSERT INTO `creature_text` VALUES ('28347', '1', '0', 'Then put twice as much in next time!', '12', '0', '100', '0', '0', '0', '27805', '0', 'Miles Sidney');
INSERT INTO `creature_text` VALUES ('28347', '2', '0', 'Remember, kid: You\'re always more wrong than you think you are. Double It!', '12', '0', '100', '0', '0', '0', '27807', '0', 'Miles Sidney');
INSERT INTO `creature_text` VALUES ('28347', '3', '0', 'Only put half as much in next time.', '12', '0', '100', '0', '0', '0', '27806', '0', 'Miles Sidney');
INSERT INTO `creature_text` VALUES ('28347', '4', '0', 'That\'s what you always say. Sometimes I think you\'re never going to finish that thing.', '12', '0', '100', '0', '0', '0', '27810', '0', 'Miles Sidney');
INSERT INTO `creature_text` VALUES ('28347', '5', '0', 'Greetings.', '12', '7', '100', '3', '0', '0', '43337', '0', 'Miles Sidney');
INSERT INTO `creature_text` VALUES ('28347', '5', '1', 'Greetings, $C.', '12', '7', '100', '3', '0', '0', '43330', '0', 'Miles Sidney');
INSERT INTO `creature_text` VALUES ('28347', '5', '2', 'Greetings! Please have a look around.', '12', '7', '100', '3', '0', '0', '43333', '0', 'Miles Sidney');
INSERT INTO `creature_text` VALUES ('28347', '5', '3', 'Let me know if you need help finding anything, $C.', '12', '7', '100', '3', '0', '0', '43335', '0', 'Miles Sidney');
INSERT INTO `creature_text` VALUES ('28347', '5', '4', 'Welcome. May I help you find something?', '12', '7', '100', '3', '0', '0', '43336', '0', 'Miles Sidney');
INSERT INTO `creature_text` VALUES ('16364', '0', '0', '%s releases the last of its energies into the nearby runestone, successfully reactivating it.', '16', '0', '100', '0', '0', '0', '12272', '0', 'Infused Crystal');
INSERT INTO `creature_text` VALUES ('16183', '0', '0', 'Where... where am I?', '12', '0', '100', '0', '0', '0', '12726', '0', 'Courier Dawnstrider');
INSERT INTO `creature_text` VALUES ('15951', '0', '0', 'Good riddance... now none shall be able to repeat my mistakes!', '12', '0', '100', '0', '0', '0', '11713', '0', 'Magister Duskwither');
INSERT INTO `creature_text` VALUES ('16196', '0', '0', 'You\'re not going anywhere just yet, handsome.  Not until you regain your strength.', '12', '0', '100', '0', '0', '0', '12728', '0', 'Apothecary Thedra');
INSERT INTO `creature_text` VALUES ('8421', '6', '0', 'Bravo! Bravo! Good show. What? You thought I was dead?', '12', '0', '100', '0', '0', '0', '4393', '0', 'Dorius');
INSERT INTO `creature_text` VALUES ('8391', '0', '0', 'Your task is complete. Prepare to meet your doom.', '12', '0', '100', '0', '0', '0', '4391', '0', 'Lathoric the Black');
INSERT INTO `creature_text` VALUES ('8391', '1', '0', 'Obsidion, Rise and Serve your Master!', '14', '0', '100', '0', '0', '0', '4392', '0', 'Lathoric the Black');
INSERT INTO `creature_text` VALUES ('27788', '0', '0', 'I was almost ghoul bait! Thanks for the rescue!', '12', '0', '100', '0', '0', '0', '27097', '0', 'Injured 7th Legion Soldier');
INSERT INTO `creature_text` VALUES ('27788', '0', '1', 'It\'s a good thing you came along, ally! We were done for!', '12', '0', '100', '0', '0', '0', '27098', '0', 'Injured 7th Legion Soldier');
INSERT INTO `creature_text` VALUES ('27788', '0', '2', 'Thanks for the cover fire! It\'s a MADHOUSE down there!', '12', '0', '100', '0', '0', '0', '27095', '0', 'Injured 7th Legion Soldier');
INSERT INTO `creature_text` VALUES ('27788', '0', '3', 'Wow, I thought I was a goner! Thanks, friend!', '12', '0', '100', '0', '0', '0', '27096', '0', 'Injured 7th Legion Soldier');
INSERT INTO `creature_text` VALUES ('17058', '0', '2', 'Faster, scum!  The Master won\'t be pleased if we don\'t find the Ata\'mal crystal he\'s after.', '12', '0', '100', '0', '0', '0', '16421', '0', 'Illidari Taskmaster');
INSERT INTO `creature_text` VALUES ('17058', '1', '0', 'You dare interfere with the Master\'s endeavors?  You will pay, foolish mortal!', '12', '0', '100', '0', '0', '0', '16438', '0', 'Illidari Taskmaster');
INSERT INTO `creature_text` VALUES ('17058', '1', '1', 'Slay these intruders, filthy Dreghood!  I promise you neverending pain if you disobey me!', '12', '0', '100', '0', '0', '0', '16439', '0', 'Illidari Taskmaster');
INSERT INTO `creature_text` VALUES ('17058', '1', '2', 'Go, you little wretches! Show these fools that our Master is not to be trifled with!', '12', '0', '100', '0', '0', '0', '16440', '0', 'Illidari Taskmaster');
INSERT INTO `creature_text` VALUES ('17058', '1', '3', 'Do not even think of fleeing, Broken wretches!  I promise you a fate worse than death if you don\'t slay these intruders!', '12', '0', '100', '0', '0', '0', '16441', '0', 'Illidari Taskmaster');
INSERT INTO `creature_text` VALUES ('16794', '0', '0', '...a potent concoction!', '12', '0', '100', '0', '0', '0', '13678', '0', 'Apothecary Azethen');
INSERT INTO `creature_text` VALUES ('16794', '1', '0', 'But where could I ever find a suitable test subject?', '12', '0', '100', '6', '0', '0', '13682', '0', 'Apothecary Azethen');
INSERT INTO `creature_text` VALUES ('16794', '2', '0', 'Drink this, you blue brute!  Disobey me and I promise you a slow, painful death!', '12', '0', '100', '25', '0', '0', '13683', '0', 'Apothecary Azethen');
INSERT INTO `creature_text` VALUES ('16794', '3', '0', 'Hmmm... looks like I\'m going to need a new test subject.', '12', '0', '100', '274', '0', '0', '13684', '0', 'Apothecary Azethen');
INSERT INTO `creature_text` VALUES ('16938', '0', '0', 'Put me out of this misery!', '12', '0', '100', '0', '0', '0', '16471', '0', 'Dreghood Brute');
INSERT INTO `creature_text` VALUES ('16938', '0', '1', 'Forgive me... I have no choice.', '12', '0', '100', '0', '0', '0', '16469', '0', 'Dreghood Brute');
INSERT INTO `creature_text` VALUES ('21257', '0', '0', 'Thank you, $N.  Now, to perform the test...', '12', '0', '100', '1', '0', '0', '19022', '0', 'Apothecary Zelana');
INSERT INTO `creature_text` VALUES ('21257', '1', '0', 'Ah, I see...', '12', '0', '100', '0', '0', '0', '19023', '0', 'Apothecary Zelana');
INSERT INTO `creature_text` VALUES ('21257', '2', '0', 'Yes, this is unfortunate.', '12', '0', '100', '0', '0', '0', '19024', '0', 'Apothecary Zelana');
INSERT INTO `creature_text` VALUES ('21257', '3', '0', '$N, I have confirmed that this blood is from the Bonechewer clan of orcs, tainted with demonic power.  I\'m afraid, however, that my current equipment can delve no deeper into this mystery...', '12', '0', '100', '1', '0', '0', '19025', '0', 'Apothecary Zelana');
INSERT INTO `creature_text` VALUES ('21279', '0', '0', 'Please excuse me while I begin my tests...', '12', '0', '100', '1', '0', '0', '19065', '0', 'Apothecary Albreck');
INSERT INTO `creature_text` VALUES ('21279', '1', '0', 'Interesting...', '12', '0', '100', '0', '0', '0', '19066', '0', 'Apothecary Albreck');
INSERT INTO `creature_text` VALUES ('21279', '2', '0', 'But what is this?', '12', '0', '100', '0', '0', '0', '19067', '0', 'Apothecary Albreck');
INSERT INTO `creature_text` VALUES ('21279', '3', '0', 'Oh, my!', '12', '0', '100', '0', '0', '0', '19068', '0', 'Apothecary Albreck');
INSERT INTO `creature_text` VALUES ('21279', '4', '0', 'If my blood hadn\'t clotted long ago, it would be boiling with rage right now...', '12', '0', '100', '1', '0', '0', '19069', '0', 'Apothecary Albreck');
INSERT INTO `creature_text` VALUES ('16795', '0', '0', 'I pity you, Forsaken.  Your soul is devoid of light.', '12', '35', '100', '0', '0', '0', '13685', '0', 'Draenei Prisoner');
INSERT INTO `creature_text` VALUES ('19354', '0', '0', 'Work these Broken wretches to the bones, Illidari!  If there\'s anything of value in this forsaken place we shall find it!', '14', '0', '100', '0', '0', '0', '16472', '0', 'Arzeth the Merciless');
INSERT INTO `creature_text` VALUES ('19354', '0', '1', 'Do not allow these wretches a moment of rest! If there\'s an Ata\'mal crystal here then we shall find it!', '14', '0', '100', '0', '0', '0', '16476', '0', 'Arzeth the Merciless');
INSERT INTO `creature_text` VALUES ('19354', '0', '2', 'Keep a close eye on this Broken scum! Far too many have escaped from us!', '14', '0', '100', '0', '0', '0', '16474', '0', 'Arzeth the Merciless');
INSERT INTO `creature_text` VALUES ('19354', '0', '3', 'We will find what the Master is looking for! Failure is not an option!', '14', '0', '100', '0', '0', '0', '16475', '0', 'Arzeth the Merciless');
INSERT INTO `creature_text` VALUES ('20680', '0', '0', 'That Broken worm gave you that staff, didn\'t he?  Did he also tell you he\'s the one that sold out his tribe?  No matter, you will both pay for this!', '14', '0', '100', '0', '0', '0', '18349', '0', 'Arzeth the Powerless');
INSERT INTO `creature_text` VALUES ('19191', '0', '0', 'What insolence! What arrogance! To believe you could defeat me on my own doorstep!', '14', '0', '100', '0', '0', '0', '17653', '0', 'Arazzius the Cruel');
INSERT INTO `creature_text` VALUES ('19191', '1', '0', 'Master, I have failed...', '14', '0', '100', '0', '0', '0', '16429', '0', 'Arazzius the Cruel');
INSERT INTO `creature_text` VALUES ('19273', '0', '0', 'Speak with Wing Commander Brack, $N.  He\'ll outfit you with one of our armored wyvern destroyers.  And, good luck!', '12', '0', '100', '25', '0', '0', '17964', '0', 'Forward Commander To\'arch');
INSERT INTO `creature_text` VALUES ('19255', '0', '0', 'Yea, we got problems. I think that\'s crystal clear. The question is, what are we gonna do about it?', '12', '1', '100', '1', '0', '0', '16392', '0', 'General Krakork');
INSERT INTO `creature_text` VALUES ('19255', '0', '1', 'Get used to it, girls. We\'re out here for the rest of our miserable lives as far as I\'m concerned. Welcome to Hellfire Peninsula, your new home.', '12', '1', '100', '1', '0', '0', '16394', '0', 'General Krakork');
INSERT INTO `creature_text` VALUES ('19255', '0', '2', 'How many more of you grunts do I need to send back to mommy and daddy in a body bag before you grow a brain and realize that runnin\' head first into a Legion kill squad is suicide? And don\'t nod your thick skulls at me as if you know what I\'m talkin\' about!', '12', '1', '100', '1', '0', '0', '16393', '0', 'General Krakork');
INSERT INTO `creature_text` VALUES ('19255', '0', '3', 'I\'m not gonna lie to you, soldiers. We\'re getting our butts kicked out there! Now which one of you is orc enough to do something about it?', '12', '1', '100', '1', '0', '0', '16390', '0', 'General Krakork');
INSERT INTO `creature_text` VALUES ('20677', '0', '0', 'You\'ve freed me!  The winds speak to my people once again and grant us their strength.  I thank you, stranger.', '12', '0', '100', '1', '0', '0', '18358', '0', 'Morod the Windstirrer');
INSERT INTO `creature_text` VALUES ('20678', '0', '0', 'I am free!  I will ask the spirit of fire to return to us so that my people may have the courage to fight their masters!  I thank you, $R.', '12', '0', '100', '15', '0', '0', '18356', '0', 'Akoru the Firecaller');
INSERT INTO `creature_text` VALUES ('20679', '0', '0', 'I\'m free!  The spirit of water returns to my people.  It will bring us the wisdom we need to survive in this harsh land.  I am in your debt, $N.', '12', '0', '100', '1', '0', '0', '18357', '0', 'Aylaan the Waterwaker');
INSERT INTO `creature_text` VALUES ('19270', '0', '0', 'If you are ready to laugh, head over to the World\'s End Tavern!', '14', '0', '100', '0', '0', '0', '16380', '0', 'Shattrath Saul');
INSERT INTO `creature_text` VALUES ('18756', '0', '0', 'I, like, so don\'t get it...', '12', '0', '100', '1', '0', '0', '16888', '0', 'Haris Pilton');
INSERT INTO `creature_text` VALUES ('18756', '0', '1', 'Psshh... whatever.', '12', '0', '100', '1', '0', '0', '16882', '0', 'Haris Pilton');
INSERT INTO `creature_text` VALUES ('18756', '0', '2', 'That\'s hot.', '12', '0', '100', '1', '0', '0', '16881', '0', 'Haris Pilton');
INSERT INTO `creature_text` VALUES ('18756', '0', '3', 'That joke\'s hot.', '12', '0', '100', '1', '0', '0', '16887', '0', 'Haris Pilton');
INSERT INTO `creature_text` VALUES ('19228', '0', '0', 'Hey all!  So how long until the jokes spawn?', '12', '0', '100', '6', '0', '0', '16382', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '0', '1', 'I just flew in from Ironforge and, Boy!, are my mount\'s wings tired!', '12', '0', '100', '1', '0', '0', '16649', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '0', '2', 'Hey folks, go easy on me... it\'s my first time.', '12', '0', '100', '24', '0', '0', '16328', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '0', '3', 'Thanks for coming out to see me!  I hope you are ready for a good time, because I am.', '12', '0', '100', '21', '0', '0', '16327', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '1', '0', 'I never met a tauren I didn\'t like...', '12', '0', '100', '1', '0', '0', '16378', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '2', '0', 'To eat!!', '12', '0', '100', '5', '0', '0', '16379', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '3', '0', 'Last week I was in Goldshire. Have you been there?', '12', '0', '100', '6', '0', '0', '16651', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '4', '0', 'That town\'s so small, I asked a kid to draw me a map. He drew it on the back of a Defias wanted poster...', '12', '0', '100', '1', '0', '0', '16652', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '5', '0', 'to scale!!', '12', '0', '100', '5', '0', '0', '16653', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '6', '0', 'Impersonation time!!  I call this, \"The Silly Tauren.\"', '12', '0', '100', '6', '0', '0', '16337', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '7', '0', 'The comedian clears his throat.', '16', '0', '100', '0', '0', '0', '16338', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '8', '0', 'The comedian impersonates a tauren.', '16', '0', '100', '5', '0', '6386', '16580', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '9', '0', 'Here\'s a good one, why do watermelons have water in them? ', '12', '0', '100', '6', '0', '0', '16333', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '10', '0', 'Because they are planted in the spring!', '12', '0', '100', '5', '0', '0', '16336', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '11', '0', 'What\'s the deal with women?  I mean they are always like...', '12', '0', '100', '6', '0', '0', '16339', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '12', '0', 'The comedian impersonates a human female.', '16', '0', '100', '5', '0', '6143', '16583', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '13', '0', 'And then gnomes are even worse!  They are always saying stuff like...', '12', '0', '100', '6', '0', '0', '16340', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '14', '0', 'The comedian impersonates a gnome.', '16', '0', '100', '5', '0', '6133', '16581', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '15', '0', 'What time is it when an elekk sits on your fence?', '12', '0', '100', '6', '0', '0', '16368', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '16', '0', 'Time to get a new fence!!', '12', '0', '100', '5', '0', '0', '16369', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '17', '0', 'If I knew it was going to be like this, I\'d have worn my Greater Boots of Laughter.', '12', '0', '100', '6', '0', '0', '16388', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '17', '1', 'Apparently my reputation with you people is hostile.', '12', '0', '100', '6', '0', '0', '16385', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '17', '2', 'Wow!  The laughs don\'t drop very often here, do they?', '12', '0', '100', '6', '0', '0', '16389', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '18', '0', 'Hey! Hey! Hey! It\'s Raliq the drunk... His momma\'s so fat she didn\'t just make the front cover of Ogre Today...', '12', '0', '100', '25', '0', '0', '16667', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '19', '0', 'She made the back cover too!', '12', '0', '100', '5', '0', '0', '16668', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '20', '0', 'The gnomes used to be a nice respectful people...', '12', '0', '100', '1', '0', '0', '16669', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '21', '0', 'The other day I saw one walking down the street shouting, \"Once you go gnome, you never go home!\"', '12', '0', '100', '1', '0', '4415', '16670', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '22', '0', 'I love blood elf women.  Especially when they say stuff like this...', '12', '0', '100', '6', '0', '0', '16374', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '23', '0', 'The comedian impersonates a blood elf.', '16', '0', '100', '5', '0', '9643', '16582', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '24', '0', 'So the blood elves think they\'re just one step away from ruling the world...', '12', '0', '100', '1', '0', '0', '16682', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '25', '0', 'Twelve steps is more like it!', '12', '0', '100', '5', '0', '0', '16683', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '26', '0', 'One last impersonation.  It\'s tough, but let\'s see if you can guess who it is...', '12', '0', '100', '6', '0', '0', '16351', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '27', '0', 'The comedian impersonates an orc.', '16', '0', '100', '5', '0', '7195', '16584', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '28', '0', 'Now that the show\'s done, can someone run me through Scarlet Monestary?', '12', '0', '100', '6', '0', '0', '16381', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '28', '1', 'Thank you! Don\'t forget to tip your waiters! Good night folks.', '12', '0', '100', '0', '0', '0', '16341', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '28', '2', 'Well, it\'s time to take these comedic muscles elsewhere. Have a good night all!', '12', '0', '100', '23', '0', '0', '16348', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '28', '3', 'Is it over already?  You\'ve been great folks.  Walk safely and have a good night.', '12', '0', '100', '6', '0', '0', '16343', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '29', '0', 'How many kobolds does it take to change a lantern wick?', '12', '0', '100', '6', '0', '0', '16657', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '30', '0', 'You no take candle!', '12', '0', '100', '5', '0', '0', '16658', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '31', '0', 'Goldshire\'s inn advertises a lakeside view...', '12', '0', '100', '1', '0', '0', '16655', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '32', '0', 'I saw a murloc swim by my window.', '12', '0', '100', '1', '0', '0', '16656', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '33', '0', 'Have you ever noticed that all those cute, orc kids look the same?', '12', '0', '100', '6', '0', '0', '16331', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '34', '0', 'It\'s too bad they don\'t stay cute.  There must be some rite of passage in the Barrens called, \"Climb ugly tree and fall out.\"', '12', '0', '100', '0', '0', '0', '16334', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '35', '0', 'I\'ve heard that gnomes can grant wishes...', '12', '0', '100', '6', '0', '0', '16375', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '36', '0', 'Well I wish they\'d go away!', '12', '0', '100', '5', '0', '0', '16376', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '37', '0', 'Look who\'s here, it\'s Raliq the drunk... His momma\'s so fat dragons won\'t eat her...', '12', '0', '100', '25', '0', '0', '16665', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '38', '0', 'They don\'t know where to store the leftovers!', '12', '0', '100', '5', '0', '0', '16666', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '39', '0', 'My wife and I were happy for 23 years...', '12', '0', '100', '6', '0', '0', '16370', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '40', '0', 'And then we met!', '12', '0', '100', '5', '0', '0', '16371', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '41', '0', 'What do you call a broken boomerang?', '12', '0', '100', '6', '0', '0', '16372', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '42', '0', 'A stick!', '12', '0', '100', '5', '0', '0', '16373', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '43', '0', 'So this gnome tells a tauren that he\'s been to Molten Core.', '12', '0', '100', '6', '0', '0', '16332', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '44', '0', 'The tauren says, \"That\'s a load of bull.\"', '12', '0', '100', '5', '0', '0', '16335', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '45', '0', 'How desperate do you have to be for allies that you recruit your livestock?', '12', '0', '100', '6', '0', '0', '16680', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '46', '0', 'I own a dog, but I\'m not giving him a sword.', '12', '0', '100', '1', '0', '0', '16681', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19271', '0', '0', 'Thank you all for coming tonight.  Now put your hands together to welcome Shattrath\'s best, Perry Gatner!', '12', '0', '100', '21', '0', '0', '16383', '0', 'Albert Quarksprocket');
INSERT INTO `creature_text` VALUES ('19271', '1', '0', 'Perry Gatner! What an act!  The cantina is open all night long, so please, stay and enjoy yourselves!', '12', '0', '100', '21', '0', '0', '16384', '0', 'Albert Quarksprocket');
INSERT INTO `creature_text` VALUES ('17831', '0', '0', '$N, let\'s go see if this plan will work.  Come along, Buddy!', '12', '0', '100', '0', '0', '0', '14608', '0', 'Watcher Leesa\'oh');
INSERT INTO `creature_text` VALUES ('17831', '1', '0', 'Ok, let\'s see about using those mushrooms you brought back to grow the bog lords a new food supply.', '12', '0', '100', '0', '0', '0', '14609', '0', 'Watcher Leesa\'oh');
INSERT INTO `creature_text` VALUES ('17831', '2', '0', 'Wait and see what happens!', '12', '0', '100', '0', '0', '0', '14615', '0', 'Watcher Leesa\'oh');
INSERT INTO `creature_text` VALUES ('17831', '3', '0', 'Oh Buddy, you big baby!  Look, it\'s working.  It\'s really working!!  Now all I need to do is grow more of these mushrooms here and the bog lords will likely leave the sporelings alone!', '12', '0', '100', '0', '0', '0', '14610', '0', 'Watcher Leesa\'oh');
INSERT INTO `creature_text` VALUES ('17831', '4', '0', 'Oh, thank you, $N!  You\'ve made it possible to save both the sporelings from the bog lords, and the bog lords from our wrath.', '12', '0', '100', '0', '0', '0', '14611', '0', 'Watcher Leesa\'oh');
INSERT INTO `creature_text` VALUES ('17955', '0', '0', 'The %s takes a piece of the mushroom tree and holds it to its \'nose\'.  Seemingly satisfied, it eats it!', '16', '0', '100', '36', '0', '0', '14612', '0', 'Hungry Bog Lord');
INSERT INTO `creature_text` VALUES ('17955', '1', '0', 'The %s uproots the rest of the mushroom tree and makes off with it.', '16', '0', '100', '36', '0', '0', '14613', '0', 'Hungry Bog Lord');
INSERT INTO `creature_text` VALUES ('19468', '0', '0', 'Look, sweetheart.  No one\'s talking about storming Tempest Keep ourselves, but when we see a target of opportunity we have to take it.', '12', '0', '100', '0', '0', '0', '16872', '0', 'Spymaster Thalodien');
INSERT INTO `creature_text` VALUES ('19468', '1', '0', '...and just look at where that got us!', '12', '0', '100', '0', '0', '0', '16889', '0', 'Spymaster Thalodien');
INSERT INTO `creature_text` VALUES ('19468', '2', '0', 'Finally some talk of action from the big guy!', '12', '0', '100', '1', '0', '0', '16891', '0', 'Spymaster Thalodien');
INSERT INTO `creature_text` VALUES ('19541', '0', '0', 'Good thing that we\'re surrounded by neighbors with excess machine parts.', '12', '0', '20', '1', '0', '0', '17119', '0', 'Netherstorm Agent');
INSERT INTO `creature_text` VALUES ('19541', '0', '1', 'Maybe I should gather up some other agents and head out there after this shift?', '12', '0', '20', '1', '0', '0', '17115', '0', 'Netherstorm Agent');
INSERT INTO `creature_text` VALUES ('19541', '0', '2', 'Hmm, arcane annihilators are pretty tough.  I wonder if Papa Wheeler knows what he\'s asking for?', '12', '0', '20', '1', '0', '0', '17116', '0', 'Netherstorm Agent');
INSERT INTO `creature_text` VALUES ('19541', '0', '3', 'Last time someone went out to try to collect the bounty on Netherock, all we got back was a compressed layer of foolhardy adventurer!', '12', '0', '20', '1', '0', '0', '19660', '0', 'Netherstorm Agent');
INSERT INTO `creature_text` VALUES ('19541', '0', '4', 'Netherock?!  That thing\'ll squish you flat with one step of its massive foot!  No thanks!', '12', '0', '20', '1', '0', '0', '19659', '0', 'Netherstorm Agent');
INSERT INTO `creature_text` VALUES ('19467', '0', '0', 'With all due respect, A\'dal sent us here to investigate Kael\'s activities, not mount a full-scaled attack.', '12', '0', '100', '0', '0', '0', '16870', '0', 'Anchorite Karja');
INSERT INTO `creature_text` VALUES ('19467', '1', '0', 'The magistrix will be happy to know  I\'m  fluent in Thalassian.  Your courtesy is not lost on me.', '12', '0', '100', '0', '0', '0', '16875', '0', 'Anchorite Karja');
INSERT INTO `creature_text` VALUES ('19467', '2', '0', 'Back to the topic...  we should focus our attention on these manaforges.  We need to learn what the enemy is doing with them and why.', '12', '0', '100', '0', '0', '0', '16876', '0', 'Anchorite Karja');
INSERT INTO `creature_text` VALUES ('19469', '0', '0', 'I\'m surrounded by idiots!', '12', '10', '100', '5', '0', '0', '16874', '0', 'Magistrix Larynna');
INSERT INTO `creature_text` VALUES ('19469', '1', '0', 'We\'ve told you all there is to know!  Do you forget that many of us used to be trusted followers of Kael\'thas?', '12', '0', '100', '22', '0', '0', '16883', '0', 'Magistrix Larynna');
INSERT INTO `creature_text` VALUES ('1410', '0', '0', 'You won\'t escape alive!', '12', '0', '100', '0', '0', '0', '16249', '0', 'Firewing Bloodwarder');
INSERT INTO `creature_text` VALUES ('1410', '0', '1', 'I will have your energy and then your life!', '12', '0', '100', '0', '0', '0', '16254', '0', 'Firewing Bloodwarder');
INSERT INTO `creature_text` VALUES ('5355', '0', '2', 'You won\'t escape alive!', '12', '0', '100', '0', '0', '0', '16249', '0', 'Firewing Defender');
INSERT INTO `creature_text` VALUES ('5355', '0', '3', 'You know too much!', '12', '0', '100', '0', '0', '0', '16251', '0', 'Firewing Defender');
INSERT INTO `creature_text` VALUES ('16769', '1', '0', 'For Kael\'thas!', '12', '0', '100', '0', '0', '0', '16252', '0', 'Firewing Warlock');
INSERT INTO `creature_text` VALUES ('16769', '1', '1', 'You won\'t escape alive!', '12', '0', '100', '0', '0', '0', '16249', '0', 'Firewing Warlock');
INSERT INTO `creature_text` VALUES ('16769', '1', '2', 'Kill them before they get away!', '12', '0', '100', '0', '0', '0', '16250', '0', 'Firewing Warlock');
INSERT INTO `creature_text` VALUES ('16769', '1', '3', 'I will have your energy and then your life!', '12', '0', '100', '0', '0', '0', '16254', '0', 'Firewing Warlock');
INSERT INTO `creature_text` VALUES ('21661', '0', '0', '%s becomes enraged!', '16', '0', '100', '0', '0', '0', '10677', '0', 'Cabal Skirmisher');
INSERT INTO `creature_text` VALUES ('21661', '1', '0', 'The end comes for you!', '12', '0', '100', '0', '0', '0', '16800', '0', 'Cabal Skirmisher');
INSERT INTO `creature_text` VALUES ('21661', '1', '1', 'You will not escape us so easily!', '12', '0', '100', '0', '0', '0', '20165', '0', 'Cabal Skirmisher');
INSERT INTO `creature_text` VALUES ('21661', '1', '2', 'I shall be rewarded!', '12', '0', '100', '0', '0', '0', '16803', '0', 'Cabal Skirmisher');
INSERT INTO `creature_text` VALUES ('20418', '5', '0', 'Manaforge shutdown aborted.  Resuming manaforge activity.', '16', '0', '100', '0', '0', '0', '17886', '0', 'manaforge_control fail');
INSERT INTO `creature_text` VALUES ('20440', '5', '0', 'Manaforge shutdown aborted.  Resuming manaforge activity.', '16', '0', '100', '0', '0', '0', '17886', '0', 'manaforge_control fail');
INSERT INTO `creature_text` VALUES ('20218', '0', '0', 'Stop the emergency shutdown! Kael will have our heads if he hears about this!', '12', '0', '100', '0', '0', '0', '17931', '0', 'Sunfury Technician');
INSERT INTO `creature_text` VALUES ('20218', '0', '1', 'Stop this immediately!', '12', '0', '100', '0', '0', '0', '18131', '0', 'Sunfury Technician');
INSERT INTO `creature_text` VALUES ('20218', '0', '2', 'The naaru\'s emergency shutdown will disable the manaforge permanently! It must be aborted!', '12', '0', '100', '0', '0', '0', '17932', '0', 'Sunfury Technician');
INSERT INTO `creature_text` VALUES ('24126', '0', '0', 'Excellent.  What\'s the lowdown?  Do we have a bodycount?', '12', '0', '100', '11', '0', '0', '22797', '0', 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES ('24126', '1', '0', 'Well, spit it out!  What\'s the bad news?', '12', '0', '100', '1', '0', '0', '22798', '0', 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES ('24126', '2', '0', 'Severe stomach pain?', '12', '0', '100', '5', '0', '0', '22800', '0', 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES ('24126', '3', '0', 'SEVERE STOMACH PAIN?', '14', '0', '100', '15', '0', '0', '22801', '0', 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES ('24126', '4', '0', 'We are the Royal Apothecary Society!  Inducing belly aches is not part of our job description!', '12', '0', '100', '0', '0', '0', '22802', '0', 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES ('24126', '5', '0', 'Am I surrounded by amateurs?  I want a full analysis of the new strain by tomorrow morning!  Get on it, Ravien!', '12', '0', '100', '25', '0', '0', '22803', '0', 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES ('24126', '6', '0', 'We were making progress with our plague in Undercity.  All of a sudden our strains keep failing!', '12', '0', '100', '5', '0', '0', '22406', '0', 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES ('24126', '6', '1', 'The last ten strains from Undercity... all of them worthless!', '12', '0', '100', '5', '0', '0', '22403', '0', 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES ('24126', '6', '2', 'I cannot work under these conditions!  We need a viable strain!', '12', '0', '100', '5', '0', '0', '22404', '0', 'Apothecary Lysander');
INSERT INTO `creature_text` VALUES ('23782', '0', '0', 'Reports from the test on the enemy fleet have come in, sir!', '12', '0', '100', '1', '0', '0', '22793', '0', 'Apothecary Ravien');
INSERT INTO `creature_text` VALUES ('23782', '1', '0', 'Well... the good news is we forced the reinforcements to turn back.', '12', '0', '100', '6', '0', '0', '22794', '0', 'Apothecary Ravien');
INSERT INTO `creature_text` VALUES ('23782', '2', '0', 'The plague was not quite... fatal.  However, reports of harmful effects include nausea, loss of equilibrium and severe stomach pain.', '12', '0', '100', '274', '0', '0', '22795', '0', 'Apothecary Ravien');
INSERT INTO `creature_text` VALUES ('23782', '3', '0', 'Yes, sir!', '12', '0', '100', '273', '0', '0', '22796', '0', 'Apothecary Ravien');
INSERT INTO `creature_text` VALUES ('27349', '0', '0', 'The %s moans but seems otherwise unaffected by the blight.', '16', '0', '100', '0', '0', '0', '26389', '0', 'Scarlet Onslaught Prisoner');
INSERT INTO `creature_text` VALUES ('27250', '0', '0', 'Huh?', '12', '0', '100', '6', '0', '0', '26274', '0', 'Junior Apothecary Schlemiel');
INSERT INTO `creature_text` VALUES ('27250', '1', '0', 'But... I followed your instructions precisely! Sorry?', '12', '0', '100', '6', '0', '0', '26277', '0', 'Junior Apothecary Schlemiel');
INSERT INTO `creature_text` VALUES ('27250', '2', '0', 'Eep!', '12', '0', '100', '34', '0', '1368', '26281', '0', 'Junior Apothecary Schlemiel');
INSERT INTO `creature_text` VALUES ('27248', '0', '0', 'Schlemiel!', '12', '0', '100', '5', '0', '0', '26273', '0', 'Apothecary Vicky Levine');
INSERT INTO `creature_text` VALUES ('27248', '1', '0', 'Apparently you flubbed this batch of blight. What do you have to say for yourself?', '12', '0', '100', '5', '0', '0', '26275', '0', 'Apothecary Vicky Levine');
INSERT INTO `creature_text` VALUES ('27248', '2', '0', 'Sorry? SORRY?! Here\'s sorry for you!', '12', '0', '100', '5', '0', '0', '26279', '0', 'Apothecary Vicky Levine');
INSERT INTO `creature_text` VALUES ('27248', '3', '0', 'Oops. I guess it IS working! That\'s odd.', '12', '0', '100', '6', '0', '0', '26280', '0', 'Apothecary Vicky Levine');
INSERT INTO `creature_text` VALUES ('27248', '4', '0', 'I\'m gonna need a cleanup and a new assistant over here!', '12', '0', '100', '5', '0', '0', '26289', '0', 'Apothecary Vicky Levine');
INSERT INTO `creature_text` VALUES ('24703', '0', '0', 'The Scourge has us surrounded. We can hold the village for awhile, but if we can\'t defeat them, they will overrun us.', '12', '0', '100', '5', '0', '0', '24791', '0', 'Chieftain Wintergale');
INSERT INTO `creature_text` VALUES ('24703', '1', '0', 'The time has come to act. We must decide how to deal with the Scourge before it consumes us.', '12', '0', '100', '1', '0', '0', '24807', '0', 'Chieftain Wintergale');
INSERT INTO `creature_text` VALUES ('24706', '0', '0', 'The choice is clear. We must attack. I know the odds are against us, but Taunka do not go quietly to the slaughter.', '12', '0', '100', '25', '0', '0', '24792', '0', 'Durm Icehide');
INSERT INTO `creature_text` VALUES ('24706', '1', '0', 'I\'ll die with my axes in my hands and drenched in the blood of our foes!', '12', '0', '100', '53', '0', '0', '24793', '0', 'Durm Icehide');
INSERT INTO `creature_text` VALUES ('24703', '2', '0', 'I won\'t sacrifice my warriors for romantic notions of glorious death on the battlefield. What use is fighting for survival if no one survives?', '12', '0', '100', '1', '0', '0', '24798', '0', 'Chieftain Wintergale');
INSERT INTO `creature_text` VALUES ('24703', '3', '0', 'There will be a time for battle, Durm. But first, we must find a way to even the odds. What would you have me do, Aeire?', '12', '0', '100', '6', '0', '0', '24799', '0', 'Chieftain Wintergale');
INSERT INTO `creature_text` VALUES ('24709', '0', '0', 'The ancestors knew a way to confound an enemy\'s senses. We could use this knowledge to discover the Scourge\'s weakness or strike at their commanders. Without their leaders, the Scourge would fall before our warriors.', '12', '0', '100', '1', '0', '0', '24800', '0', 'Sage Aeire');
INSERT INTO `creature_text` VALUES ('24706', '2', '0', 'A dishonorable scheme.', '12', '0', '100', '274', '0', '0', '24801', '0', 'Durm Icehide');
INSERT INTO `creature_text` VALUES ('24703', '4', '0', 'What is your honor worth, Durm? Your life? Mine? The life of blind old Greatfather Mahan, who raised you from childhood?', '12', '0', '100', '1', '0', '0', '24802', '0', 'Chieftain Wintergale');
INSERT INTO `creature_text` VALUES ('24703', '5', '0', 'Honor is meaningless to a dead man. A warrior may throw himself into battle for honor\'s sake, but a chieftain is responsible for far more. We will follow Sage Aeire\'s plan and continue to defend our village.', '12', '0', '100', '1', '0', '0', '24803', '0', 'Chieftain Wintergale');
INSERT INTO `creature_text` VALUES ('24709', '1', '0', 'I\'ll take those. We can\'t have the mighty chieftain cowering in fear of a few hides.', '12', '0', '100', '11', '0', '0', '24811', '0', 'Sage Aeire');
INSERT INTO `creature_text` VALUES ('24703', '6', '0', 'Aeire, $N has brought your hides. Please, take them before they make me ill.', '12', '0', '100', '25', '0', '0', '24810', '0', 'Chieftain Wintergale');
INSERT INTO `creature_text` VALUES ('25256', '0', '0', 'We are surrounded... Our enemies press in from all sides, young Hellscream.', '12', '1', '100', '1', '0', '0', '24486', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '1', '0', 'The Scourge descends like locust from the north.', '12', '1', '100', '25', '0', '0', '24487', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '2', '0', 'The Alliance holds the only secure shipping lane in this region, and even that is at risk of being lost to those dreaded mists.', '12', '1', '100', '25', '0', '0', '24488', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '3', '0', 'Our only viable port for resupply is held by the Forsaken on the other side of this blasted continent!', '12', '1', '100', '1', '0', '0', '24489', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '4', '0', 'Anything our zeppelins cannot haul must be brought in by ship and travel the length of Northrend to reach us.', '12', '1', '100', '1', '0', '0', '24490', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25237', '0', '0', '%s grunts.', '16', '1', '100', '0', '0', '0', '24491', '0', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('25237', '1', '0', 'Shipping lanes... supplies... You bore me to death! We need nothing more than the warrior spirit of the Horde, Saurfang! Now that we are firmly entrenched in this frozen wasteland, nothing shall stop us!', '12', '1', '100', '1', '0', '0', '24492', '0', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('25256', '5', '0', 'Siege engines, ammunition, heavy armor... How do you propose to shatter the walls of Icecrown without those?', '12', '1', '100', '1', '0', '0', '24493', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25237', '2', '0', 'Propose? I will show you what I propose!', '12', '1', '100', '6', '0', '0', '24494', '0', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('25237', '3', '0', 'There... Now we now have a shipping lane.', '12', '1', '100', '1', '0', '0', '24495', '0', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('25237', '4', '0', 'And just for good measure...', '12', '1', '100', '5', '0', '0', '24496', '0', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('25256', '6', '0', 'So the prodigal son has spoken!', '12', '1', '100', '5', '0', '0', '24497', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '7', '0', 'Your father\'s blood runs strong in you, Hellscream. Impatient as always... Impatient and reckless.', '12', '1', '100', '1', '0', '0', '24498', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '8', '0', 'You rush headlong into all-out war without a thought of the consequences.', '12', '1', '100', '1', '0', '0', '24500', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25237', '5', '0', 'Do not speak to me of consequences, old one. ', '12', '1', '100', '25', '0', '0', '24501', '0', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('25256', '9', '0', 'I drank of the same blood your father did, Garrosh. Mannoroth\'s cursed venom pumped through my veins as well.', '12', '1', '100', '1', '0', '0', '24502', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '10', '0', 'I drove my weapons into the bodies and minds of my enemies.', '12', '1', '100', '1', '0', '0', '24503', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '11', '0', 'And while Grom died a glorious death - freeing us all from the blood curse - he could not wipe away the terrible memory of our past.', '12', '1', '100', '1', '0', '0', '24504', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '12', '0', 'His act could not erase the horrors we committed. ', '12', '1', '100', '274', '0', '0', '24505', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '13', '0', '%s pauses.', '16', '1', '100', '0', '0', '0', '24506', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '14', '0', 'The winter after the curse was lifted, hundreds of veteran orcs like me were lost to despair.', '12', '1', '100', '1', '0', '0', '24507', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '15', '0', 'Our minds were finally free, yes... Free to relive all of the unthinkable acts that we had performed under the Legions\' influence.', '12', '1', '100', '273', '0', '0', '24508', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '16', '0', '%s nods.', '16', '1', '100', '273', '0', '0', '24509', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '17', '0', 'I think it was the sounds of the draenei children that unnerved most of them... You never forget...', '12', '1', '100', '1', '0', '0', '24510', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '18', '0', 'Have you ever been to Jaggedswine Farm? When the swine are of age for the slaughter... It\'s that sound. The sound of the swine being killed... It resonates the loudest. Those are hard times for us older veterans.', '12', '1', '100', '6', '0', '0', '24511', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25237', '6', '0', 'But surely you cannot think that those children were born into innocence? They would have grown up and taken arms against us!', '12', '1', '100', '5', '0', '0', '24512', '0', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('25256', '19', '0', '%s shakes his head.', '16', '1', '100', '274', '0', '0', '24513', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '20', '0', 'I am not speaking solely of the children of our enemies...', '12', '1', '100', '1', '0', '0', '24515', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '21', '0', '%s pauses.', '16', '1', '100', '1', '0', '0', '24506', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '22', '0', 'I won\'t let you take us down that dark path again, young Hellscream. I\'ll kill you myself before that day comes...', '12', '1', '100', '274', '0', '0', '24516', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25237', '7', '0', 'How have you managed to survive for so long, Saurfang? Not fallen victim to your own memories?', '12', '1', '100', '6', '0', '0', '24518', '0', 'Garrosh Hellscream');
INSERT INTO `creature_text` VALUES ('25256', '23', '0', 'I don\'t eat pork...', '12', '1', '100', '274', '0', '0', '24519', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('25256', '24', '0', '%s spits.', '16', '1', '100', '0', '0', '0', '24520', '0', 'High Overlord Saurfang');
INSERT INTO `creature_text` VALUES ('24014', '0', '0', 'You too will serve the Lich King.', '12', '0', '100', '0', '0', '0', '23663', '0', 'Necrolord');
INSERT INTO `creature_text` VALUES ('24014', '0', '1', 'There\'s always room for one more.', '12', '0', '100', '0', '0', '13542', '23669', '0', 'Necrolord');
INSERT INTO `creature_text` VALUES ('24014', '0', '2', 'Your life for the Lich King!', '12', '0', '100', '0', '0', '0', '23667', '0', 'Necrolord');
INSERT INTO `creature_text` VALUES ('24014', '0', '3', 'So eager to die are we?', '12', '0', '100', '0', '0', '0', '23662', '0', 'Necrolord');
INSERT INTO `creature_text` VALUES ('24014', '0', '4', 'Hello, little one. Come to play?', '12', '0', '100', '0', '0', '0', '23666', '0', 'Necrolord');
INSERT INTO `creature_text` VALUES ('24014', '0', '5', 'I have so many wonders to show you.', '12', '0', '100', '0', '0', '0', '23668', '0', 'Necrolord');
INSERT INTO `creature_text` VALUES ('24014', '0', '6', 'Excellent, another sacrificial lamb.', '12', '0', '100', '0', '0', '0', '23661', '0', 'Necrolord');
INSERT INTO `creature_text` VALUES ('24014', '0', '7', 'There\'s always room for one more.', '12', '0', '100', '0', '0', '0', '23669', '0', 'Necrolord');
INSERT INTO `creature_text` VALUES ('24014', '0', '8', 'Plucked ripe off the vine.', '12', '0', '100', '0', '0', '0', '23678', '0', 'Necrolord');
