INSERT INTO `creature_text` VALUES ('24362', '0', '0', 'They keep their tools in boxes that look like this. Look for them near the pavilions.', '12', '0', '100', '25', '0', '0', '23552', '0', 'Longrunner Pembe');
INSERT INTO `creature_text` VALUES ('24362', '1', '0', 'I\'m only going to carve the runes I think will give you command over a giant.', '12', '0', '100', '1', '0', '0', '23246', '0', 'Longrunner Pembe');
INSERT INTO `creature_text` VALUES ('24362', '2', '0', 'I\'m omitting the runes for \"north\" and \"west\" because I\'m not sure how they impact the giant. I\'m also including a rune to limit how long you can control it.', '12', '0', '100', '0', '0', '0', '23247', '0', 'Longrunner Pembe');
INSERT INTO `creature_text` VALUES ('24362', '3', '0', 'There we go, the Rune of Command... hopefully. Not nearly as vicious as its Iron Dwarf cousin, but functional.', '12', '0', '100', '1', '0', '0', '23248', '0', 'Longrunner Pembe');
INSERT INTO `creature_text` VALUES ('27683', '0', '0', 'I\'ve lived here and kept this shrine with the blessing of the Alexstrasza for longer than I can remember.  In its darkest hour, I\'ve failed... I fell at the feet of that damned death knight...  Bloodbane.', '12', '0', '100', '0', '0', '0', '26939', '0', 'Remnant of Dahlia Suntouch');
INSERT INTO `creature_text` VALUES ('27683', '1', '0', 'Dahlia spits on the ground at the thought of the name.', '16', '0', '100', '0', '0', '0', '26943', '0', 'Remnant of Dahlia Suntouch');
INSERT INTO `creature_text` VALUES ('27683', '2', '0', 'The Lich King is stretching his hand far now... fearlessly treading on the most sacred lands of all of the flights.  He intends to force us to action.', '12', '0', '100', '0', '0', '0', '26940', '0', 'Remnant of Dahlia Suntouch');
INSERT INTO `creature_text` VALUES ('27683', '3', '0', 'My time is done... I offered myself to Alexstrasza in life, but I have nothing left to offer in death.  She will need a new keeper for her shrine.', '12', '0', '100', '0', '0', '0', '26941', '0', 'Remnant of Dahlia Suntouch');
INSERT INTO `creature_text` VALUES ('27683', '4', '0', 'And thank you... thank you for freeing me from the shackles of the damned.  You are a hero... I know you will go on to great deeds.', '12', '0', '100', '0', '0', '0', '26942', '0', 'Remnant of Dahlia Suntouch');
INSERT INTO `creature_text` VALUES ('27683', '5', '0', 'The Remnant of Dahlia Suntouch fades away...', '16', '0', '100', '0', '0', '0', '26944', '0', 'Remnant of Dahlia Suntouch');
INSERT INTO `creature_text` VALUES ('27210', '0', '0', 'Now I\'ll show you the REAL power of the Onslaught!', '12', '0', '100', '0', '0', '14192', '27593', '0', 'High General Abbendis');
INSERT INTO `creature_text` VALUES ('27951', '0', '0', 'Kill them Abbendis! Do not fail me!', '14', '0', '100', '25', '0', '14198', '27354', '0', 'Admiral Barean Westwind');
INSERT INTO `creature_text` VALUES ('27951', '1', '0', 'The grand admiral studies you a moment through narrowed eyes.', '16', '0', '100', '0', '0', '0', '27355', '0', 'Admiral Barean Westwind');
INSERT INTO `creature_text` VALUES ('27951', '2', '0', 'You served your purpose.', '12', '0', '100', '0', '0', '14199', '27357', '0', 'Admiral Barean Westwind');
INSERT INTO `creature_text` VALUES ('27951', '3', '0', 'I will see you in Icecrown, weakling... if you live that long!', '12', '0', '100', '0', '0', '14200', '27358', '0', 'Admiral Barean Westwind');
INSERT INTO `creature_text` VALUES ('27203', '0', '0', 'Be purified by steel!', '12', '0', '100', '0', '0', '0', '26404', '0', 'Onslaught Footman');
