INSERT INTO `creature_text` VALUES ('27202', '0', '0', 'Abbendis will see you purged!', '12', '0', '100', '0', '0', '0', '26399', '0', 'Onslaught Raven Priest');
INSERT INTO `creature_text` VALUES ('27202', '0', '1', 'You are impure!', '12', '0', '100', '0', '0', '0', '26393', '0', 'Onslaught Raven Priest');
INSERT INTO `creature_text` VALUES ('27202', '0', '2', 'Gah! What are you doing?', '12', '0', '100', '5', '0', '7256', '26637', '0', 'Onslaught Raven Priest');
INSERT INTO `creature_text` VALUES ('27202', '1', '0', 'I sense the taint of this land in you, footman.  Report to the Bishop before the start of your next shift.', '12', '0', '100', '0', '0', '0', '26256', '0', 'Onslaught Raven Priest');
INSERT INTO `creature_text` VALUES ('27202', '1', '1', 'Your protection is strong, child.  Continue your work.', '12', '0', '100', '0', '0', '0', '26390', '0', 'Onslaught Raven Priest');
INSERT INTO `creature_text` VALUES ('27202', '1', '2', 'The Grand Admiral himself has come to see to our dedication.  If I see you slacking on your post again, I will gut you myself.', '12', '0', '100', '0', '0', '0', '26655', '0', 'Onslaught Raven Priest');
INSERT INTO `creature_text` VALUES ('27202', '1', '3', 'You require the Bishop\'s blessing.  Go to him soon.', '12', '0', '100', '0', '0', '0', '26391', '0', 'Onslaught Raven Priest');
INSERT INTO `creature_text` VALUES ('27202', '2', '0', 'HELP! HELP! THIS $g MAN : WOMAN; HAS STOLEN MY IMAGE!', '12', '0', '100', '0', '0', '0', '26543', '0', 'Onslaught Raven Priest');
INSERT INTO `creature_text` VALUES ('27246', '0', '0', 'It is good that you have come my child.  The Light\'s protection has worn thin on you.', '12', '0', '100', '0', '0', '0', '26271', '0', 'Bishop Street');
INSERT INTO `creature_text` VALUES ('27246', '1', '0', 'It is done.  Serve the Light well.', '12', '0', '100', '0', '0', '0', '26272', '0', 'Bishop Street');
