INSERT INTO `creature_text` VALUES ('34044', '0', '0', 'Just in time. Let\'s see this thing off, then.', '12', '0', '100', '0', '0', '15826', '34084', '0', 'Brann Bronzebeard');
INSERT INTO `creature_text` VALUES ('16128', '0', '0', 'We received Brann\'s message, and we have begun preparations.', '12', '0', '100', '1', '0', '15649', '33982', '0', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '1', '0', 'Citizens of Dalaran! Raise your eyes to the skies and observe!', '14', '0', '100', '0', '0', '15650', '33983', '2', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '2', '0', 'Today our world\'s destruction has been averted in defiance of our very makers!', '14', '0', '100', '0', '0', '15651', '33984', '2', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '3', '0', 'Algalon the Observer, herald of the titans, has been defeated by our brave comrades in the depths of the titan city of Ulduar.', '14', '0', '100', '0', '0', '15652', '33985', '2', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '4', '0', 'Algalon was sent here to judge the fate of our world.', '14', '0', '100', '0', '0', '15653', '33986', '2', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '5', '0', 'He found a planet whose races had deviated from the titans\' blueprints. A planet where not everything had gone according to plan.', '14', '0', '100', '0', '0', '15654', '33987', '2', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '6', '0', 'Cold logic deemed our world not worth saving. Cold logic, however, does not account for the power of free will. It\'s up to each of us to prove this is a world worth saving.', '14', '0', '100', '0', '0', '15655', '33988', '2', 'Rhonin');
INSERT INTO `creature_text` VALUES ('16128', '7', '0', 'That our lives... our lives are worth living.', '14', '0', '100', '0', '0', '15656', '33989', '2', 'Rhonin');
INSERT INTO `creature_text` VALUES ('28217', '4', '0', 'Oh no! Some puppy-men followed!', '12', '0', '100', '0', '0', '0', '27799', '0', 'Injured Rainspeaker Oracle');
INSERT INTO `creature_text` VALUES ('28325', '0', '0', 'You hear a low guttural growl from nearby.', '16', '0', '100', '0', '0', '0', '27831', '0', 'Ravenous Mangal Crocolisk');
INSERT INTO `creature_text` VALUES ('28327', '0', '0', 'Shaman Vekjik comes out of the underbrush nearby.', '16', '0', '100', '0', '0', '0', '27738', '0', 'Shaman Vekjik');
INSERT INTO `creature_text` VALUES ('28327', '1', '0', 'You help Rainspeaker! I saw you help Rainspeaker! Traitor thing!', '12', '0', '100', '0', '0', '0', '27739', '0', 'Shaman Vekjik');
INSERT INTO `creature_text` VALUES ('28327', '2', '0', 'If you want to be friends with big-tongues, you not friends with Frenzyheart! I tell village! We no trust you anymore!', '12', '0', '100', '0', '0', '0', '27740', '0', 'Shaman Vekjik');
INSERT INTO `creature_text` VALUES ('28327', '3', '0', 'Shaman Vekjik scurries off towards Frenzyheart Hill.', '16', '0', '100', '0', '0', '0', '27741', '0', 'Shaman Vekjik');
INSERT INTO `creature_text` VALUES ('28077', '0', '0', 'Dumb big-tongue lover! You not friend of Frenzyheart no more. Frenzyheart will get you good.', '14', '0', '100', '0', '0', '0', '27798', '0', 'Frenzyheart Tracker');
INSERT INTO `creature_text` VALUES ('28213', '0', '0', 'The %s screams with rage and rushes to the aid of her babies.', '16', '0', '100', '0', '0', '0', '27607', '0', 'Hardknuckle Matriarch');
INSERT INTO `creature_text` VALUES ('28113', '3', '0', 'The mosswalker victim groans in pain.', '16', '0', '100', '0', '0', '0', '28638', '0', 'Mosswalker Victim');
INSERT INTO `creature_text` VALUES ('28092', '0', '0', 'Omega Rune deployed.  Activation successful.', '12', '0', '100', '51', '0', '0', '33004', '0', 'The Etymidian');
INSERT INTO `creature_text` VALUES ('28033', '0', '0', 'Finally, we have the parts to repair the Spirit of Gnomeregan.', '12', '0', '100', '1', '0', '0', '29878', '0', 'Weslex Quickwrench');
INSERT INTO `creature_text` VALUES ('28033', '1', '0', 'Let\'s see, just a tweak here and an adjustment there...', '12', '0', '100', '432', '0', '0', '29879', '0', 'Weslex Quickwrench');
INSERT INTO `creature_text` VALUES ('28033', '2', '0', 'And there she is, the Spirit of Gnomeregan!', '12', '0', '100', '25', '0', '0', '29880', '0', 'Weslex Quickwrench');
INSERT INTO `creature_text` VALUES ('28033', '3', '0', 'You can use her, but I don\'t want to hear any complaints about saw blades in your seat. I had to fix \'er with what we could find.', '12', '0', '100', '1', '0', '0', '29881', '0', 'Weslex Quickwrench');
INSERT INTO `creature_text` VALUES ('4484', '0', '0', 'It looks like we\'re in trouble. Look lively, here they come!', '12', '7', '100', '0', '0', '0', '1372', '0', 'Feero');
INSERT INTO `creature_text` VALUES ('4484', '1', '0', 'Assassins from that cult you found... Let\'s get moving before someone else finds us out here.', '12', '7', '100', '0', '0', '0', '1294', '0', 'Feero');
INSERT INTO `creature_text` VALUES ('4484', '2', '0', 'Hold! I sense an evil presence... Undead!', '12', '7', '100', '0', '0', '0', '1373', '0', 'Feero');
INSERT INTO `creature_text` VALUES ('4484', '3', '0', 'They\'re coming out of the woodwork today. Let\'s keep moving or we may find more things that want me dead.', '12', '7', '100', '0', '0', '0', '1310', '0', 'Feero');
INSERT INTO `creature_text` VALUES ('4484', '4', '0', 'These three again?', '12', '7', '100', '0', '0', '0', '1374', '0', 'Feero');
INSERT INTO `creature_text` VALUES ('4484', '5', '0', 'I\'ll finish you off for good this time!', '12', '0', '100', '0', '0', '0', '1499', '0', 'Feero');
INSERT INTO `creature_text` VALUES ('4484', '6', '0', 'Well done! I should be fine on my own from here. Remember to talk to Delgren when you return to Maestra\'s Post in Ashenvale.', '12', '7', '100', '0', '0', '0', '1315', '0', 'Feero');
INSERT INTO `creature_text` VALUES ('4484', '7', '0', 'We must move quickly. Auberdine isn\'t too far away, but many enemies lurk in the forest.', '12', '7', '100', '0', '0', '0', '1292', '0', 'Feero');
INSERT INTO `creature_text` VALUES ('3899', '0', '0', 'Not quite so sure of yourself without the Purifier, hm?', '12', '0', '100', '0', '0', '0', '1313', '0', 'Balizar the Umbrage');
INSERT INTO `creature_text` VALUES ('28998', '0', '0', 'Behold, mon! We be creatin\' da greatest Scourge army ever seen!', '12', '0', '100', '396', '0', '14023', '29441', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '1', '0', 'These be da first of many! We shall sweep across Zul\'Drak like da wind....', '12', '0', '100', '396', '0', '14024', '29442', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '2', '0', 'What treachery is this?! You be payin\' for this deceit with your life, mon!', '12', '0', '100', '0', '0', '14025', '29885', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '3', '0', 'The Scepter of Domination glows brightly! Use it now!', '41', '0', '100', '0', '0', '0', '29886', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '4', '0', 'How could dis be? Da Lich King said he be havin\' great plans for you, mon...', '14', '0', '100', '0', '0', '14027', '29917', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '4', '1', 'Ya done turned your back on destiny, mon. Now you gunna die!', '14', '0', '100', '0', '0', '14029', '29919', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '4', '2', 'Fool! Ya coulda been havin\' great power, mon!', '14', '0', '100', '0', '0', '14026', '29916', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '4', '3', 'Your betrayal not gunna be slowin\' me down none, mon.', '14', '0', '100', '0', '0', '14030', '29920', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '4', '4', 'I shoulda known not to be trustin\' nobody!', '14', '0', '100', '0', '0', '14028', '29918', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '4', '5', 'I never needed ya, mon. I can crush Zul\'Drak without ya!', '14', '0', '100', '0', '0', '14031', '29921', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '5', '0', 'ENOUGH!', '14', '0', '100', '22', '0', '14020', '29922', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '6', '0', 'Dis foolish treachery has cost ya your destiny!', '14', '0', '100', '396', '0', '14021', '29923', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '7', '0', 'Master, dis mortal scum be double-crossin\' us. Dey must be made to suffer!', '12', '0', '100', '396', '0', '14022', '29969', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28998', '8', '0', 'Disguise Failing! Avoid Scourge Contact!', '41', '0', '100', '0', '0', '0', '28891', '0', 'Overlord Drakuru');
INSERT INTO `creature_text` VALUES ('28498', '7', '0', 'You have failed me, Drakuru!', '12', '0', '100', '396', '0', '14762', '29986', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('28498', '8', '0', 'It is you who should suffer. Be content that your death is a quick one....', '12', '0', '100', '397', '0', '14763', '29982', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('28498', '9', '0', 'As for you...', '12', '0', '100', '397', '0', '14764', '29983', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('28498', '10', '0', 'I spare your insignificant life as a reward for this amusing betrayal. There may yet be a shred of potential in you.', '12', '0', '100', '396', '0', '14765', '29984', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('28498', '11', '0', 'Be warned...', '12', '0', '100', '396', '0', '14766', '29989', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('28498', '12', '0', 'When next we meet I shall require much more to justify your life.', '12', '0', '100', '396', '0', '14767', '29985', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('28479', '0', '0', 'Gods of old, we beseech you. What must we do? How can we save the gods of this land?', '15', '0', '100', '6', '0', '0', '28431', '0', 'Witch Doctor Khufu');
INSERT INTO `creature_text` VALUES ('28514', '0', '0', 'But try they must or all will be lost. The Drakkari will be the death of both themselves and their gods!', '12', '0', '100', '0', '0', '473', '28435', '0', 'Nalorakk');
INSERT INTO `creature_text` VALUES ('28515', '0', '0', 'The spotted one must be sought. Their fates are entwined.', '12', '0', '100', '0', '0', '9919', '28432', '0', 'Jan\'alai');
INSERT INTO `creature_text` VALUES ('28516', '0', '0', 'But she is chained. A way must be found to free her.', '12', '0', '100', '0', '0', '12196', '28433', '0', 'Akil\'zon');
INSERT INTO `creature_text` VALUES ('28517', '0', '0', 'To her altar this mortal must travel. What they must do will not be easy. In the end it may prove impossible.', '12', '0', '100', '0', '0', '643', '28434', '0', 'Halazzi');
INSERT INTO `creature_text` VALUES ('28176', '0', '0', 'Let us see if it is truly impervious.', '12', '0', '100', '1', '0', '0', '27783', '0', 'Rimblat Earthshatter');
INSERT INTO `creature_text` VALUES ('28176', '1', '0', 'Nothing.', '12', '0', '100', '1', '0', '0', '27766', '0', 'Rimblat Earthshatter');
INSERT INTO `creature_text` VALUES ('28175', '0', '0', 'Saronite you say?', '12', '0', '100', '0', '0', '0', '27779', '0', 'Crusade Commander Korfax');
INSERT INTO `creature_text` VALUES ('28175', '1', '0', 'Never heard of it. Looks like pure evil.', '12', '0', '100', '6', '0', '0', '27748', '0', 'Crusade Commander Korfax');
INSERT INTO `creature_text` VALUES ('28175', '2', '0', 'Let\'s see how it holds up to the might of my axe!', '12', '0', '100', '0', '0', '0', '27749', '0', 'Crusade Commander Korfax');
INSERT INTO `creature_text` VALUES ('28175', '3', '0', 'What the? My reaper didn\'t even scratch it! That\'s the toughest ore I\'ve ever seen!', '12', '0', '100', '0', '0', '0', '27750', '0', 'Crusade Commander Korfax');
INSERT INTO `creature_text` VALUES ('28175', '4', '0', 'Oof!', '12', '0', '100', '0', '0', '9410', '27788', '0', 'Crusade Commander Korfax');
INSERT INTO `creature_text` VALUES ('28177', '0', '0', 'By the Goddess. It\'s as if Nature can\'t touch it!', '12', '0', '100', '5', '0', '0', '27763', '0', 'Rayne');
INSERT INTO `creature_text` VALUES ('28177', '1', '0', 'Gah!', '12', '0', '100', '0', '0', '9410', '27786', '0', 'Rayne');
INSERT INTO `creature_text` VALUES ('28178', '0', '0', '%s rolls his eyes.', '16', '0', '100', '0', '0', '0', '27784', '0', 'Avenger Metz');
INSERT INTO `creature_text` VALUES ('28178', '1', '0', 'No surprise there.', '12', '0', '100', '0', '0', '0', '27769', '0', 'Avenger Metz');
INSERT INTO `creature_text` VALUES ('28178', '2', '0', 'What?', '12', '0', '100', '0', '0', '0', '27770', '0', 'Avenger Metz');
INSERT INTO `creature_text` VALUES ('28178', '3', '0', 'Fine. Just remember though, the power of the Holy Light isn\'t something to be invoked casually, brother.', '12', '0', '100', '1', '0', '0', '27771', '0', 'Avenger Metz');
INSERT INTO `creature_text` VALUES ('28178', '4', '0', 'What\'s it doing?!', '12', '0', '100', '5', '0', '0', '27772', '0', 'Avenger Metz');
INSERT INTO `creature_text` VALUES ('28178', '5', '0', 'Whoa!', '12', '0', '100', '0', '0', '9410', '27787', '0', 'Avenger Metz');
INSERT INTO `creature_text` VALUES ('28178', '6', '0', '%s grunts.', '16', '0', '100', '0', '0', '0', '27776', '0', 'Avenger Metz');
INSERT INTO `creature_text` VALUES ('28244', '0', '0', 'Now we will see why this ore is so important to the Scourge.', '12', '0', '100', '16', '0', '0', '27746', '0', 'Eitrigg');
INSERT INTO `creature_text` VALUES ('28244', '1', '0', 'Korfax, what can you tell from this saronite?', '12', '0', '100', '6', '0', '0', '27747', '0', 'Eitrigg');
INSERT INTO `creature_text` VALUES ('28244', '2', '0', 'Rayne, Rimblat, what about the power of nature and the elements?', '12', '0', '100', '6', '0', '0', '27759', '0', 'Eitrigg');
INSERT INTO `creature_text` VALUES ('28244', '3', '0', 'Metz?', '12', '0', '100', '6', '0', '0', '27767', '0', 'Eitrigg');
INSERT INTO `creature_text` VALUES ('28244', '4', '0', 'You know what.', '12', '0', '100', '25', '0', '0', '27768', '0', 'Eitrigg');
INSERT INTO `creature_text` VALUES ('28244', '5', '0', '%s dusts himself off.', '16', '0', '100', '0', '0', '9410', '27789', '0', 'Eitrigg');
INSERT INTO `creature_text` VALUES ('28244', '6', '0', 'Clearly this is getting us nowhere. We need to get this saronite to Highlord Fordring for further study.', '12', '0', '100', '1', '0', '0', '27774', '0', 'Eitrigg');
INSERT INTO `creature_text` VALUES ('28244', '7', '0', 'Thank you for trying, my friends. We\'ll unlock its secrets. Hopefully before it\'s too late.', '12', '0', '100', '1', '0', '0', '27775', '0', 'Eitrigg');
INSERT INTO `creature_text` VALUES ('28284', '0', '0', 'Oh, these are sticky! I wonder how they shoot the webbing?', '12', '0', '100', '0', '0', '0', '27734', '0', 'Specialist Cogwheel');
INSERT INTO `creature_text` VALUES ('28284', '1', '0', 'ACK! Oh no, I webbed myself! It\'s sticky. MY HAIR!', '12', '0', '100', '15', '0', '6906', '27735', '0', 'Specialist Cogwheel');
INSERT INTO `creature_text` VALUES ('28284', '2', '0', 'Okay! Let me just see if I can fashion these disgusting parts into something useful.', '12', '0', '100', '0', '0', '0', '27736', '0', 'Specialist Cogwheel');
INSERT INTO `creature_text` VALUES ('28284', '3', '0', 'Eureka!', '12', '0', '100', '4', '0', '0', '27737', '0', 'Specialist Cogwheel');
INSERT INTO `creature_text` VALUES ('28780', '0', '0', 'Explosive Charges will detonate in 5 seconds...', '16', '0', '100', '0', '0', '0', '28816', '0', 'Explosive Charges Bunny');
INSERT INTO `creature_text` VALUES ('28780', '1', '0', 'Explosive Charges will detonate in 4 seconds...', '16', '0', '100', '0', '0', '0', '28817', '0', 'Explosive Charges Bunny');
INSERT INTO `creature_text` VALUES ('28780', '2', '0', 'Explosive Charges will detonate in 3 seconds...', '16', '0', '100', '0', '0', '0', '28818', '0', 'Explosive Charges Bunny');
INSERT INTO `creature_text` VALUES ('28780', '3', '0', 'Explosive Charges will detonate in 2 seconds...', '16', '0', '100', '0', '0', '0', '28819', '0', 'Explosive Charges Bunny');
INSERT INTO `creature_text` VALUES ('28780', '4', '0', 'Explosive Charges will detonate in 1 seconds...', '16', '0', '100', '0', '0', '0', '28820', '0', 'Explosive Charges Bunny');
INSERT INTO `creature_text` VALUES ('29775', '0', '0', 'Use of the Inventor\'s Disk detected. Emergency protocol gamma activated.', '12', '0', '100', '0', '0', '0', '30402', '0', 'Archivist Mechaton');
INSERT INTO `creature_text` VALUES ('29775', '1', '0', 'Verifying status of Norgannon\'s shell.', '12', '0', '100', '0', '0', '0', '30403', '0', 'Archivist Mechaton');
INSERT INTO `creature_text` VALUES ('29775', '2', '0', 'Norgannon\'s shell accounted for and secure. It will be available for transfer once user\'s identity has been verified.', '12', '0', '100', '1', '0', '0', '30404', '0', 'Archivist Mechaton');
INSERT INTO `creature_text` VALUES ('29775', '3', '0', 'Standby to verify identity as Keeper Mimir.', '12', '0', '100', '1', '0', '0', '30405', '0', 'Archivist Mechaton');
INSERT INTO `creature_text` VALUES ('29775', '4', '0', 'Identity verification failed. User is not Keeper Mimir.', '12', '0', '100', '25', '0', '0', '30406', '0', 'Archivist Mechaton');
INSERT INTO `creature_text` VALUES ('29775', '5', '0', 'The Inventor\'s Disk has fallen into the hands of an unauthorized user.  Activating defense protocol.', '12', '0', '100', '0', '0', '0', '30407', '0', 'Archivist Mechaton');
INSERT INTO `creature_text` VALUES ('29775', '6', '0', 'Impostor must be dealt with. The Inventor\'s Disk must be recovered.', '12', '0', '100', '5', '0', '0', '30408', '0', 'Archivist Mechaton');
INSERT INTO `creature_text` VALUES ('30381', '2', '0', 'But the North Wind did not silently accept defeat. Instead, it gathered power and bided its time, deciding the best revenge was to destroy your memory, and with it, your village.', '12', '0', '100', '1', '0', '0', '32751', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '3', '0', 'The wind used its minions to distort the history of this place, changing the stories your people told, destroying the spirits of the honored dead.', '12', '0', '100', '1', '0', '0', '32752', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '4', '0', 'It would\'ve succeeded if $N and I hadn\'t tracked the North Wind\'s historical corruptions to their source: your ancient battle.', '12', '0', '100', '1', '0', '0', '32754', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '5', '0', 'With my help, $N travelled back in time to your battle, and ensured that history was set right. The North Wind\'s fate was sealed, and your story restored.', '12', '0', '100', '1', '0', '0', '32755', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '6', '0', 'Rest well, hero.', '12', '0', '100', '273', '0', '0', '32762', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '7', '0', 'This isn\'t right at all, $N. In the tale Wind Tamer Barah told me, Stormhoof was a hero, not a villain.', '12', '0', '100', '6', '0', '0', '31041', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '8', '0', 'The North Wind is the ancient enemy of the taunka, a ruler among the elements, and the very force which bleeds the life from these ancient people.', '12', '0', '100', '1', '0', '0', '31044', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '9', '0', 'They would rather die than bow before the North Wind. Something is amiss here.', '12', '0', '100', '274', '0', '0', '31046', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '10', '0', 'The Lorehammer...', '12', '0', '100', '25', '0', '0', '31049', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '11', '0', 'It confirms our suspicions. The threads of time have been severed and rewoven in the wrong pattern. But, how?', '12', '0', '100', '1', '0', '0', '31050', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '12', '0', 'There\'s no doubt that something has distorted the true history of Stormhoof and the warriors of Camp Tunka\'lo. But, what?', '12', '0', '100', '1', '0', '0', '31259', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '13', '0', 'No! The true voice of history will triumph. We will restore all you have destroyed!', '12', '0', '100', '25', '0', '0', '31263', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30381', '14', '0', '$N, we must hurry and discover when the North Wind has started distorting the timeline. Only then can we defeat him and correct it.', '12', '0', '100', '1', '0', '0', '31264', '0', 'Xarantaur');
INSERT INTO `creature_text` VALUES ('30388', '0', '0', 'The horn! Use the horn on it while it\'s weak!', '14', '0', '100', '0', '0', '0', '31183', '0', 'Stormhoof');
INSERT INTO `creature_text` VALUES ('30388', '1', '0', 'Go to the center of the temple and use the Horn of Elemental Fury!', '41', '0', '100', '0', '0', '0', '36207', '0', 'Stormhoof');
INSERT INTO `creature_text` VALUES ('30550', '0', '0', 'Do not trouble yourself, little tauren. Neither Stormhoof, nor you and your ally will stand in my way.', '12', '0', '100', '1', '0', '0', '31260', '0', 'Image of the North Wind');
INSERT INTO `creature_text` VALUES ('30550', '1', '0', 'Even now, my power grows. I have long waited for the day when my revenge would be complete.', '12', '0', '100', '1', '0', '0', '31261', '0', 'Image of the North Wind');
INSERT INTO `creature_text` VALUES ('30550', '2', '0', 'Stormhoof and his kin will be erased from history and these lands will be restored to their rightful owner!', '12', '0', '100', '1', '0', '0', '31262', '0', 'Image of the North Wind');
INSERT INTO `creature_text` VALUES ('30474', '0', '0', 'Fool! You will never defeat me!', '14', '0', '100', '0', '0', '0', '31180', '0', 'North Wind');
INSERT INTO `creature_text` VALUES ('30474', '1', '0', 'I will never allow you to use the Horn of Elemental Fury against us! Die, weakling!', '14', '0', '100', '0', '0', '0', '31181', '0', 'North Wind');
INSERT INTO `creature_text` VALUES ('30479', '0', '0', 'This is Camp Tunka\'lo, the village ruled by my brother, but I... I don\'t remember you.', '12', '0', '100', '6', '0', '0', '32747', '0', 'Stormhoof\'s Spirit');
INSERT INTO `creature_text` VALUES ('30479', '1', '0', 'The North Wind? It still troubles my people?', '12', '0', '100', '6', '0', '0', '32749', '0', 'Stormhoof\'s Spirit');
INSERT INTO `creature_text` VALUES ('30479', '2', '0', 'You both have my thanks and that of the village, though many will never know of your deeds.', '12', '0', '100', '2', '0', '0', '32756', '0', 'Stormhoof\'s Spirit');
INSERT INTO `creature_text` VALUES ('30479', '3', '0', 'I cannot remain here. I must return to my cairn where I belong, but when I see the ancestors again, it is I who will tell your story.', '12', '0', '100', '1', '0', '0', '32759', '0', 'Stormhoof\'s Spirit');
INSERT INTO `creature_text` VALUES ('29525', '0', '0', 'Thank you, thank you!  Time to get away from these crazy blue women!', '12', '0', '100', '0', '0', '0', '30803', '0', 'Zeev Fizzlespark');
INSERT INTO `creature_text` VALUES ('28511', '0', '0', 'The Eye of Acherus launches towards its destination.', '42', '0', '100', '0', '0', '0', '28507', '0', 'Eye of Acherus');
INSERT INTO `creature_text` VALUES ('28511', '1', '0', 'The Eye of Acherus is in your control.', '42', '0', '100', '0', '0', '0', '28465', '0', 'Eye of Acherus');
